css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("greasyfork.org") {

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

/* Background color */
html, body, body:not(.Settings), .Preview, #Content .HomepageTitle
{ background-color: var(--bg) }

#main-header, #script-info, code, .text-content, .script-list, .list-option-group ul, .list-option-groups .carbon-ad, body:not(.Settings) #Head, #Panel .MeBox, #Content, #Panel .FilterMenu, #Panel .PanelCategories, .FlyoutMenu, .MenuItems, .SiteMenu nav, .alert, nav nav, div.Warning, form.external-login-form
{ background-color: var(--bg-2) }

#additional-info > div, .Item.Read, div.Popup .Body, form.new_user
{ background-color: var(--bg-odd) }

.jslghtbx-contentwrapper > img
{ background-color: var(--border-color) }

.CodeRay .string
{ background-color: transparent }

/* Foreground colors */
html, body, body:not(.Settings), .install-link:link, .install-help-link:link, #main-header, .FlyoutMenu, .script-list dd, blockquote.Quote, .Preview
{ color: var(--fg) }
.MenuItems a:link, .MenuItems a
{ color: var(--fg) !important }

#main-header #site-name-text a, #Head #site-name-text a, #main-header h1, .DiscussionForm label
{ color: var(--fg-2) }

.ItemDiscussion .Meta, .DataList .Meta, #script-meta dl dt, article > dl dt, dl.About dt
{ color: var(--fg-muted) }

a, a:link, #main-header a, #Head a, body:not(.Settings) a:not(.Button):not(.ChangePicture):not(.install-link):not(.install-help-link), a .TextColor
{ color: var(--fg-link) }
a:hover, #site-nav > nav a:hover, #Head a:hover, body:not(.Settings) a:not(.Button):not(.ChangePicture):hover, .Attachment:hover .FileHover, a .TextColor:hover, a:hover .TextColor
{ color: var(--fg-link-hover) }
a:visited, #Head a:visited, body:not(.Settings) a:not(.Button):not(.ChangePicture):visited, body:not(.Settings) :not(li) > a:not(.Button):not(.ChangePicture):not(.install-link):not(.install-help-link):visited
{ color: var(--fg-link-visited) }

.jslghtbx-close
{ color: var(--border-color) }


/* Inputs */
select, input, button, textarea, .script-list + .pagination > *, input.InputBox, .Button, .RecentDiscussionFilter a, .Pager > *, .NavButton, form.new_user input[type="text"], form.new_user input[type="email"], form.new_user input[type="password"]
{ background-color: var(--bg-input) }
select, input, button, textarea, .script-list + .pagination > *, input.InputBox, .Button, .RecentDiscussionFilter a, .Pager > *, .NavButton, form.new_user input[type="text"], form.new_user input[type="email"], form.new_user input[type="password"]
{ color: var(--fg-input) }

select:focus, input[type="submit"]:hover, input[type="text"]:focus, button:hover, textarea:focus, .script-list + .pagination > *:hover, input.InputBox:focus, .Button:hover, .Button:focus, .RecentDiscussionFilter a:focus, .RecentDiscussionFilter a:hover, .Pager > a:focus, .Pager > a:hover, .Attachment:hover .FileHover, .NavButton:hover, table.PreferenceGroup tbody tr:hover td
{ background-color: var(--bg-input-hover) }
select:focus, input[type="submit"]:hover, input[type="text"]:focus, button:hover, textarea:focus, .script-list + .pagination > *:hover, input.InputBox:focus, .Button:hover, .Button:focus, .RecentDiscussionFilter a:focus, .RecentDiscussionFilter a:hover, .Pager > a:focus, .Pager > a:hover, .Attachment:hover .FileHover, .NavButton:hover, table.PreferenceGroup tbody tr:hover td
{ color: var(--fg-input-hover) }


/* Colorful Boxes */
.notice
{ background-color: var(--bg-info); color: var(--fg-info) }
.WarningMessage
{ background-color: var(--bg-warning); color: var(--fg-warning) }
.CodeRay .error, :target, .field_with_errors textarea
{ background-color: var(--bg-error); color: var(--fg-error) }
.MenuItems a:hover
{ background-color: var(--fg-link) !important; color: var(--bg) !important }

/* Line Numbers */
.CodeRay .line-numbers {
  background-color: var(--syntax-bg) !important;
  border-right: solid 0.5px var(--border-color) }
ol.linenums {
  border-left:  solid 0.5px var(--border-color) }
.CodeRay .line-numbers a, ol.linenums
{ color: var(--fg-muted) !important }
.Message pre, .CodeRay .line-numbers {
  box-shadow:  4px 0px 8px -1px var(--bg-2) inset }

/* Syntax Highlighting */
#code-container, .Message pre, .Message pre ol, .diff ul { background-color: var(--syntax-bg) }
.CodeRay .regexp, .diff li.diff-block-info { background-color: var(--syntax-bg-2) }

.CodeRay, #code-container, .pln { color: var(--syntax-fg) }
.com, .CodeRay .comment { color: var(--syntax-comment) }
.atv, .str, .CodeRay .regexp .content, .CodeRay .regexp .delimiter, .CodeRay .regexp .modifier, .CodeRay .string .content, .CodeRay .string .delimiter { color: var(--syntax-string) }
.CodeRay .tag, .pun, .opn, .clo, .CodeRay .predefined-constant, .CodeRay .keyword { color: var(--syntax-builtin) }
.atn, .kwd, .CodeRay .key, .CodeRay .local-variable { color: var(--syntax-keyword) }
.CodeRay .attribute-name, .CodeRay .predefined, .fun, .typ, .tag, .CodeRay .function { color: var(--syntax-function) }
.dec, .var, .lit, .CodeRay .integer { color: var(--syntax-number) }

.diff li:hover {
  background-color: #fff3 !important }
.diff li.del, .diff del strong {
  background-color: #f003; color: #faa }
.diff li.ins, .diff ins strong {
  background-color: #0f03; color: #afa }

/* Borders */
#script-info, #additional-info > div, code, .text-content, .script-list, .list-option-group ul, .list-option-groups .carbon-ad, #Panel .MeBox, #Content, #Panel .FilterMenu, #Panel .PanelCategories, .FlyoutMenu, .MenuItems, .WarningMessage, .change-script-set section
{ border: none}
.Message pre, .DataList .Item, .NavButton, div.Warning, textarea:focus, form.new_user, form.new_user input[type="text"], form.new_user input[type="email"], form.new_user input[type="password"], #home-script-nav
{ border-color: var(--border-color) }
select, input, button, #code-container, textarea, figure, .script-list li, input.InputBox, .Button, .MenuItems hr, form.external-login-form
{ border: solid 0.5px var(--border-color) }
.jslghtbx-prev.jslghtbx-no-img
{ border-right-color: var(--border-color) }
.jslghtbx-next.jslghtbx-no-img
{ border-left-color: var(--border-color) }
.tabs .current { border-color: var(--fg-link) }
#Panel .PanelCategories .Active a, #Panel .FilterMenu .Active a, #Content .HomepageTitle, .list-option-group .list-current
{ border-color: var(--fg-link) }

blockquote.UserQuote {
  border-color: var(--border-color);
  background-color: var(--bg-odd);
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.5) }
#main-header .subtitle, .subtitle
{ text-shadow: 0 0 3px var(--fg-2) }

#script-info, .text-content, .script-list, .list-option-group ul, .list-option-groups .carbon-ad, .list-option-group a:hover, .list-option-group a:focus, #Panel .MeBox, #Content, #Panel .FilterMenu, #Panel .FilterMenu a:hover, #Panel .FilterMenu a:focus, #Panel .PanelCategories, #Panel .PanelCategories a:hover, #Panel .PanelCategories a:focus, .FlyoutMenu, .WarningMessage, #Content, .SiteMenu nav, .SiteMenu nav, .alert, nav nav
{ box-shadow: 0 0 3px rgba(0,0,0,0.5) }

#main-header, .list-option-group .list-current, .list-option-group a:hover, .list-option-group a:focus, body:not(.Settings) #Head, #Panel .FilterMenu .Active a, #Panel .FilterMenu a:hover, #Panel .FilterMenu a:focus, #Panel .PanelCategories a:hover, #Panel .PanelCategories a:focus, #Panel .PanelCategories .Active a
{ background-image: linear-gradient(var(--bg-2), var(--bg)) }

.ReactSprite, .Sprite16, canvas, .OptionsTitle, .SpEditProfile { filter: invert(100%) brightness(200%) }
.SiteSearch .Button, a.Bookmark { filter: saturate(400%) brightness(400%) }
.SiteSearch .Button:hover, .SiteSearch .Button:focus {
  background-color: transparent; filter: none }
.width-constraint { max-width: 90% }

#Form_Go { filter: invert(100%) brightness(70%) }

/* Forum: Table Header */
.Message th:nth-child(2n+1), .Message th
{ background-color: transparent; color: var(--fg-2) }

/* Forum: Table odd row */
.Message tr:nth-child(2n+1) td:nth-child(2n), .Message tr:nth-child(2n+1) td:nth-child(2n+1)
{ background-color: rgba(255, 255, 255, 0.1) }

/* Forum: Table even row */
.Message tr:nth-child(2n) td, .Message tr:nth-child(2n) td:nth-child(2n)
{ background-color: rgba(255, 255, 255, 0.05) }

a.self-link { opacity: 0.5; transition: opacity 0.2s ease-in-out }
a.self-link:hover { opacity: 1}

:target {
  padding-left: 15px; padding-right: 15px }

.ad.carbon-ad {
  display: none }

textarea:focus {
  border-color: rgba(255, 255, 255, 0.1) !important; box-shadow: none !important }

}
