css:
@-moz-document domain("nnm-club.me"), domain("nnmclub.to"), domain("nnm-club.name"), domain("nnm-club.tv") {
.b-magic-forest, .b-page_newyear, .request, html>body>div>table>tbody>tr>td>div>table>tbody, html>body>div.wrap>table.tg>tbody>tr>td>table.header>tbody>tr>td.row2>table, body > div.wrap > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr > td.leftnav > center:nth-child(14){
 display:none!important;   
}
*{
    border-color:#1976d2!important;
 
}
html, table, body{
    background-color:	#212121!important;
    color:white!important;
}
body noindex:first-child {
    display: none !important;
}
body{
    padding-top:0px!important;
}
th, .top{
 background-image:none!important;   
    background-color: #616161!important;
    color:white!important;
    
}
.spoiler-body, .forumline, .menu ul li a{
    background-color:	#424242!important;
}
.menu ul{
 background:#424242!important;   
}
.sp-fold, .seed, .seedmed, .seedsmall,.dlComplete{
    color:#64dd17!important;
}
.leech, .leechmed, .leechsmall,.dlDown{
    color:#ff5252!important;
}
a{
	color:#80d8ff!important;
}
span.nav > b, .postdetails{
    color:#0288d1!important;
}
input.post, textarea.post, input, textarea, option{
    background-color: #616161!important;
    color:white!important;
}
::placeholder, .signature{
   color:#e0e0e0!important;
}
select{
    background-color: #616161!important;
    color:white!important;
}
.quote, optgroup{
	color:#bdbdbd!important;
    background-color:	#424242!important;
}
.postbody, a.gen, .gen, .genmed, .gensmall, .nav, b a[rel='nofollow']{
	color:white!important;
}
.spoiler-wrap{
    border-color:#f5f5f5!important;
    background:#424242!important;
}
.spoiler-head{
    background-color:#424242!important;
    color:white!important;
}
.rank_progress_bar span, .seed_bonus, .bonusmed, .bonussmall{
    color:#448aff;
}
legend{
    color:#40c4ff;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #000;
    width: 14px;
    height: 14px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c;
}

td.catLeft{
        background-color:	#222!important;
}
td.row1, td.row3, td.row3Right, .row2{
        background-color:	#222!important;
}
td{
    background-color:	#222!important;
}
.mod{
 color:white;
}
}

@-moz-document url-prefix("http://nnm-club.name/forum/index.php"), url-prefix("http://nnm-club.me/forum/index.php"), url-prefix("http://nnmclub.to/forum/index.php"), url-prefix("http://nnm-club.tv/forum/index.php"), url-prefix("http://nnm-club.name/forum/viewforum.php"), url-prefix("http://nnm-club.me/forum/viewforum.php"), url-prefix("http://nnmclub.to/forum/viewforum.php"), url-prefix("http://nnm-club.tv/forum/viewforum.php") {
font{
 color:#009933!important;
}
}
