css:
@-moz-document domain(avenoel.org) {
html {
    font-family: monospace;
    background-image: url(https://orig00.deviantart.net/0a4f/f/2017/227/3/f/retrowave_neon_80_s_background___4k_by_rafael_de_jongh-dbk7ro6.jpg);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
section {
    border: 1px solid #98cbe8;
    box-shadow: 2px 2px 28px #ffc13c;
    /*fe3030
     box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 28px rgba(152, 203, 232, 0.6);
    */
}
.form-control {
    background-color: #484b51;
    border: 1px solid #282826;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    color: #f6f6f6;
}
article {
    /*Used on focus textarea*/
    
    border-color: #98cbe8;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 18px rgba(152, 203, 232, 0.6);
}

body {
    font-family: monospace;
    background: linear-gradient(180deg, rgba(0, 0, 0, 0.5), rgba(255, 255, 255, 0));
    background-size: 100% 10px;
    margin-bottom: 0;
    animation: scanlines 1s linear infinite;
    -webkit-animation: scanlines 2s linear infinite;
    -moz-animation: scanlines 2s linear infinite;
    -o-animation: scanlines 2s linear infinite;
}
.bottombar {
    display: none;
}
main.container {
    margin-bottom: 100px;
}
.footer {
    position: relative;
    border-top: 4px groove #9D00B4;
    background: linear-gradient(180deg, #32004d, #cf1899, #fe1111);
}
nav.navbar {
    background: linear-gradient(90deg, #ff02ff, #ef0079, #a622fe, #37002e);
    background-size: 300% 300%;
    animation: backgroundVape 13s ease infinite;
}
.navbar {
    border-bottom: 4px groove #9D00B4 !important;
}


/************************************

			BUTTONS

***********************************/



.grey-btn.btn {
    color: #057a05;
    background-color: #ffffffe6;
    border: 2px solid #FFF;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(152, 203, 232, 0.6);
}
.grey-btn.btn:hover {
    box-shadow: 0px 0px 7px 3px #f7fb5f;
}
.grey-btn.btn:focus {
    color: #057a05;
    background-color: #7DFC00;
    border: 2px inset #000;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 28px rgba(152, 203, 232, 0.6);
}
.btn-primary {
    color: #fff;
    background-color: #1b7ab3e6;
    border: 2px solid #FFF;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(152, 203, 232, 0.6);
}
.btn-primary:hover {
    color: #fff;
    background-color: #1b7ab3e6;
    border: 2px solid #FFF;
    box-shadow: 0px 0px 7px 3px #f7fb5f;
}

.btn-primary:focus {
    color: #fff;
    background-color: #59aef9;
    border: 2px inset black;
    outline: 0;
    box-shadow: 0 0px 16px rgb(230, 231, 217);
}
.btn-primary:active:focus {
    color: #fff;
    background-color: #1b7ab3e6;
    border-color: 0px inset black;
}
.btn-danger {
    color: #fff;
    background-color: #ff3737e6;
    border: 2px inset #FFF;
    box-shadow: none;
}
.btn-danger:hover {
    color: #fff;
    background-color: #ff3737e6;
    border: 2px solid #FFF;
    box-shadow: 0px 0px 7px 3px #f7fb5f;
}
.btn-danger:focus {
    color: #fff;
    background-color: #ff3737e6;
    border: 2px inset black;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 28px rgba(152, 203, 232, 0.6);
}
.btn-danger:active:focus {
    border: 0px inset black;
    color: #fff;
    background-color: #ff6547;
    outline: 0;
    box-shadow: 0 0px 16px rgb(230, 231, 217);
}
.topics-author a:hover {
    text-decoration: none;
    text-shadow: 0 0 3px #000;
}
section td a {
    text-shadow: 0 0 3px #9187FE;
}
section td a:hover {
    text-shadow: 0 0 3px #00FF00;
    color: #c7d9ff;
}
main.container .container-content section {
    background: #2f3136;
}
.topic-message.odd {
    background: #454b5a;
    border: 1px solid #65c5f6;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 18px rgba(152, 203, 232, 0.6);
}
.topic-message {
    background: #4b4b5e;
    border: 1px solid #8888FF;
    /*border-color: #98cbe8;*/
    
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 18px rgba(152, 203, 232, 0.6);
}
.topic-message iframe {
    max-width: 80%;
    border: 3px inset darkblue;
}
.topic-message.odd iframe {
    max-width: 80%;
    border: 3px inset darkblue;
}
.topic-title {
    color: darkseagreen;
}
.message-username a {
    text-shadow: 0 0 3px #9187FE;
}
.message-username a:hover {
    text-shadow: 0 0 3px #00FF00;
    color: #c7d9ff;
}
.message-actions img {
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 18px rgba(152, 203, 232, 0.6);
}
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
    background-color: #b1b1b1;
    opacity: 1;
}


/********************************

			ANIMATIONS

*******************************/

@keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-webkit-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-moz-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-o-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-webkit-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-moz-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-o-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
}
