css:
@-moz-document url-prefix("https://desteria.com/bans/") {
/* This is thrown together */

/* latin-ext */
@font-face {
  font-family: 'Dosis';
  font-style: normal;
  font-weight: 400;
  src: local('Dosis Regular'), local('Dosis-Regular'), url(https://fonts.gstatic.com/s/dosis/v7/RlBXAIuiO5GvH9-0-JbBlw.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Dosis';
  font-style: normal;
  font-weight: 400;
  src: local('Dosis Regular'), local('Dosis-Regular'), url(https://fonts.gstatic.com/s/dosis/v7/4hYyXH_8WmbBLamf6WjLwg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}

/* Get rid of margins and give a more full-width look. */

body {
    margin-left: 0%;
    margin-right: 0%;
    margin-top: 0%;
    color: #000;
    font-family: 'Dosis', sans-serif;
    font-weight: bold;
}

.navbar-inverse {
    background-color: #2d1f11;
    border: none;
    width: 100% !important;
}

.navbar-inverse .navbar-nav>.active>a,
.navbar-inverse .navbar-nav>li>a:hover,
.navbar-inverse .navbar-nav>.active>a,
.navbar-inverse .navbar-nav>.active>a:hover {
    background-color: #24180d;
}

.navbar-inverse .navbar-nav>li>a {
    color: #fff;
}

.navbar-inverse .navbar-brand {
    color: #fff;
}

.jumbotron {
    border-width: 29px;
    -moz-border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    -webkit-border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    border-style: solid;
    border-image-outset: 10px;
    background-color: rgba(45, 31, 17, 0.8);
    color: #faefdc;
}

.modal-header {
    color: #fff;
    border: none;
}

.table-bordered {
    border: none;
}

th {
    background-color: #423528;
    padding: 15px !important;
    font-size: 14px;
    border: none !important;
}

.table>tbody>tr>td {
    vertical-align: middle;
    font-size: 15px;
}

.form-inline {
    text-align: right;
}

.col-lg-12 {
    border-width: 29px;
    -moz-border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    -webkit-border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    border-image: url("https://desteria.com/images/container_border.png") 29 stretch;
    border-style: solid;
    border-image-outset: 10px;
    background-color: rgba(45, 31, 17, 0.8);
}

.litebans-pager-active {
    color: #faefdc;
}


/* Thought there was going to be a bit more tbh but what works works */
}
