css:
@-moz-document domain(youtube.com) {
    
#yt-masthead-container {
    background: #ffe7fbe6;
    border-bottom: 1px solid #ff97e8;
    border-top: 1px solid #ff97e8;
    border-left: 1px solid #ff97e8;
    border-right: 1px solid #ff97e8;
    border-radius: 10px;
}
    
#masthead-search-terms {
    background-color: #fff7fd;
}
    
.masthead-search-terms-border {
    border: 1px solid #e7a7ff;
    border-radius: 5px;
}
    
.gfse_a {
    border: 1px solid #FF9FD9;
}
    
.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    border-color: #ff89e5;
        border-left-color: rgb(255, 137, 229);
    background: #fff7fc;
    color: #ff99e1;
    border-radius: 5px;
        border-top-left-radius: 5px;
        border-bottom-left-radius: 5px;
}
    
#masthead-appbar {
    background-color: #fceaff;
    border-bottom: 1px solid #e283ffcc;
    border-radius: 5px;
}
    
.exp-invert-logo a.yt-uix-button-epic-nav-item:hover, .exp-invert-logo a.yt-uix-button-epic-nav-item.selected, .exp-invert-logo a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .exp-invert-logo a.yt-uix-button-epic-nav-item.partially-selected, .exp-invert-logo a.yt-uix-button-epic-nav-item.partially-selected:hover, .exp-invert-logo button.yt-uix-button-epic-nav-item:hover, .exp-invert-logo button.yt-uix-button-epic-nav-item.selected, .exp-invert-logo button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .exp-invert-logo .epic-nav-item:hover, .exp-invert-logo .epic-nav-item.selected, .exp-invert-logo .epic-nav-item.yt-uix-button-toggled, .exp-invert-logo .epic-nav-item-heading, .exp-invert-logo .yt-gb-shelf-item-thumbtab.yt-gb-selected-shelf-tab::before {
    border-color: #e9a3ff;
}
    
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
    color: #eb87ff;
    font-weight: 500;
}
    
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
    line-height: 40px;
    font-weight: normal;
    color: #ff97e4;
}
    
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 1px solid #ffacea;
    border-radius: 5px;
}
    
#appbar-guide-menu, .guide-flyout {
    background: #ffe9f9e6;
}
    
.exp-invert-logo .resume-playback-progress-bar, .exp-invert-logo .yt-uix-button-subscribe-branded, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled], .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:hover, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:active, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:focus, .exp-invert-logo .sb-notif-on .yt-uix-button-content, .exp-invert-logo .guide-item.guide-item-selected, .exp-invert-logo .guide-item.guide-item-selected:hover, .exp-invert-logo .guide-item.guide-item-selected .yt-deemphasized-text, .exp-invert-logo .guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background-color: #e9b7ff;
    border: 1px solid #e08bff;
    border-radius: 5px;
}
    
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
    color: #fef9ff;
}
    
#masthead-search-term::-moz-placeholder {
    color: #ff87d3;
}
    
.guide-item {
    color: #ff7be6;
}
    
.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: #ffeaf7e6;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #ff9fd9;
    border-radius: 5px;
}
    
.search-header .num-results, .search-header .num-results strong, .search-header .yt-uix-button-content {
    color: #ff91df;
}
    
.yt-ui-ellipsis {
    background-color: #fae7ff;
    border: 1px solid #e09bff;
    border-radius: 5px;
}
    
.shelf-description, .shelf-annotation {
    color: #e479ff;
}
    
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
    color: #e479ff;
}
    
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    color: #ea97ff;
}
    
.yt-lockup {
    color: #ff7ecb;
}
    
.branded-page-module-title a:visited, .branded-page-module-title a {
    color: #ffa1dd;
}
    
a {
    color: #e294ff;
    cursor: pointer;
}
    
#watch7-sidebar .watch-sidebar-head {
    color: #ff94d8;
}
    
.autoplay-bar .checkbox-on-off {
    color: #ff94d8;
}
    
.video-list .video-list-item .title {
    color: #ff94d8;
}
    
.video-list .video-list-item .description, .video-list .video-list-item .stat {
    color: #ec89ff;
}
    
.yt-uix-checkbox-on-off label {
    border: 1px solid #dc7eff;
    background: #f4deff;
}
    
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
    border: 1px solid #ff91ea;
    border-radius: 0 5px 5px 0;
}
    
#watch-description {
    color: #ff87d8;
}
    
#watch-header .yt-user-info a {
    color: #ff84db;
}
    
.watch-view-count {
    color: #ec8eff;
}
    
.comment-section-header-renderer {
    color: #dd9bff;
}
    
.comment-simplebox-frame {
    border: 1px solid #e283ff;
    border-radius: 5px;
    box-shadow: inset 0 0 1px rgba(0, 0, 0, 0);
}
    
.comment-simplebox {
    background-color: #fff0;
}
    
.comment-simplebox-text:empty::after {
    color: #e08cff;
}

.comment-simplebox-text:empty::after {
    color: #db89ff;
}
    
.comment-simplebox-renderer {
    border-bottom: 1px solid #ff9be1;
}
    
.comment-renderer-author-comment-badge.creator {
    background-color: #ffa4e7;
}
    
.comment-renderer-header .comment-renderer-time a {
    color: #ff86db;
}
    
.comment-author-text {
    color: #e18fff;
}
    
body #footer-container {
    margin-top: -145px;
    background-color: #FCEAFF;
    border-top: 1px solid #E9A3FF;
    border-radius: 5px;
}
    
#footer-links-primary a {
    color: #ff83da;
}
    
#footer-links-secondary a, #footer-links-secondary .vat-notice, #footer-links-secondary .copyright {
    color: #dd81ff;
    font-size: 11px;
}
    
body {
     background: url(/*[[background]]*/) repeat center fixed! important;
}
    
/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/smilies/smi-2/smi164.cur), progress !important;} /* End http://www.cursors-4u.com */
    
}
