css:
@-moz-document domain("4chan.org") {
    @font-face {
        font-family: Roboto;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/Fcx7Wwv8OzT71A3E1XOAjvesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF
    }
    @font-face {
        font-family: Roboto;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000
    }
    :root {
        --grey-800: #424242;
        --grey-900: #212121;
        --purple-200: #B39DDB;
        --purple-300: #9575CD;
        --red-a700: #D50000
    }
    body,
    html {
        background: var(--grey-900)!important
    }
    body {
        color: #FFF!important;
        font-family: Roboto, Arial, sans-serif
    }
    a {
        color: var(--purple-300)!important
    }
    a:hover {
        color: var(--purple-200)!important
    }
    hr {
        border-top: 1px solid var(--grey-800)
    }
    input[type=text],
    textarea {
        background: var(--grey-800);
        color: #FFF;
        border: 0!important
    }
    #errmsg {
        color: var(--red-a700)!important
    }
    .box-outer {
        background: var(--grey-800)!important
    }
    .boxcontent {
        background: var(--grey-800);
        color: #FFF
    }
    #ft li,
    .preview,
    .reply,
    .reply.highlight {
        background: var(--grey-800)!important
    }
    #ft ul {
        border: 0
    }
    #ft .fill {
        visibility: hidden
    }
    #ft li {
        padding: .5rem .75rem!important;
        border: 1px solid var(--grey-900)!important
    }
    .boardTitle,
    .globalMessage h3 {
        color: var(--red-a700)
    }
    .rules {
        color: rgba(255, 255, 255, .5)
    }
    #filters-ctrl,
    #qf-ctrl,
    #tobottom span,
    #totop span {
        color: var(--purple-300)!important
    }
    #filters-ctrl:hover,
    #qf-ctrl:hover,
    #tobottom span:hover,
    #totop span:hover {
        color: var(--purple-200)!important
    }
    #threads {
        margin-top: -13px;
        padding: 0
    }
    .thread {
        padding: 1rem 0 0
    }
    .subject {
        color: #42A5F5!important
    }
    .preview,
    .reply {
        padding: .5rem!important;
        border: 0!important;
        border-radius: 2px;
        box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)
    }
    .reply.highlight,
    .reply:target {
        border: 0!important
    }
    .preview {
        box-shadow: 0 3px 6px rgba(0, 0, 0, .16), 0 3px 6px rgba(0, 0, 0, .23)
    }
    .reply:target, 
    .reply.highlight {
        background: rgba(255, 255, 255, .25)!important
    }
    .fileThumb img {
        border-radius: 2px
    }
    .nameBlock .name {
        color: #388E3C!important
    }
   .quoteLink, 
   .quotelink, 
    .deadlink, .pageJump a
	.burichan_new .backlink a,
	.yotsuba_b_new .backlink a,
    a.replylink:not(:hover),
    div#absbot a:not(:hover) {
        color: var(--purple-200)!important
    }
    .dd-menu ul {
        background: var(--grey-900);
        border: 1px solid var(--grey-800);
        border-right-width: auto
    }
    .dd-menu li {
        padding: .5rem .75rem;
        border-bottom: 1px solid var(--grey-800)
    }
    .dd-menu li:hover {
        background: rgba(255, 255, 255, .17)
    }
    .absBotDisclaimer {
        color: var(--grey-800)
    }
  	.pagelist {
		background: var(--grey-900) !important;
		border: 0 !important;
	}
}
