css:
@-moz-document domain("nixos.org") {
@font-face {
  font-family: 'Quattrocento';
  font-style: normal;
  font-weight: 400;
  src: local('Quattrocento'), url(http://themes.googleusercontent.com/static/fonts/quattrocento/v5/WZDISdyil4HsmirlOdBRFKRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
body {
  font-family: 'Quattrocento';
  font-size: 18px;
  line-height: 1.45em;
  background-color: #002b36 !important;
  color: #93a1a1 !important;
}
h1 {
  color: #93a1a1 !important;
}
a:link {
  color: #268bd2 !important;
}
a:visited {
  color: #2aa198 !important;
}
a:hover {
  color: #d33682 !important;
}
div.book {
  background-color: #073642 !important;
}
div.docbook code {
  font-size: 0.8em;
}
pre {
  box-shadow: none !important;
  border: none !important;
}
code,
pre {
  background-color: #073642 !important;
  color: #93a1a1 !important;
}
.alert-info code,
.alert-info pre,
.alert-info {
  background-color: #fdf6e3 !important;
  color: #586e75 !important;
  border: none !important;
  line-height: 1.4em !important;
}
.alert-info code,
.alert-info pre {
  background-color: #eee8d5 !important;
}
table#search-results tr.odd > td {
  background-color: #073642;
}
.table th,
.table td {
  border: none;
}
table#search-results tr.details > td:hover {
  background-color: #002b36;
}
.table-hover tbody tr:hover>td,
.table-hover tbody tr:hover>th {
  background-color: #002b36;
}
.navbar-inner {
  background-image: none;
  background-color: #073642;
  border: none;
}
.navbar .brand,
.navbar .nav>li>a {
  text-shadow: none;
}
.navbar-search .search-query {
  background-color: #002b36;
  border: none;
}
ul.nixos-blogs > li:nth-child(odd),
ul.nixos-commits > li:nth-child(odd) {
  background-color: #073642;
}
ul.nixos-blogs > li,
ul.nixos-commits > li {
  border-top: 1px solid #073642;
}
hr {
  border-top: 1px solid #073642;
  border-bottom: 1px solid #073642;
}
tr.news-header {
  border-bottom: 1px solid #073642;
}
.news-short {
  color: #b58900;
}
