css:
@-moz-document domain("star.longzhu.com")
{
  /*去左栏*/
/*页面布局*/

#stage-center {
    padding-left: 0px;
    padding-right: 0px;
    width: auto;
    height: auto;
    padding-top: 40px;
}
.stage-inner
{margin-left:0px;
}
.stage {
    padding-left: 0px;
}
.stage {
    height: 120px;
    width: 1036px;
    padding-left: 0px;
    padding-top: 40px;
}
.ptc-footer {
    margin-top: 0px;
    border-top-width: 0px;
}
.header {
    height: 80px;
    padding-left: 70px;
    position: relative;
}
.header-info {
    padding-top: 0px;
    padding-left: 0px;
}
.header-info-line {
    margin-bottom: 3px;
}
.gift-bar-content-prev {
    background: rgba(255, 255, 255, 0);
}
.gift-bar-content-next {
    background: rgba(255, 255, 255, 0);
}
.top-banner-pic {
    display: none !important
}
.live-footer-tab {
    border-right-width: 0px;
}
.chatroom .chatroom-interactive {
    top: 0px;
}
.chatroom-inner {
    top: 0px;
}
.chat-panel>.chat-more-button {
    background: rgba(51, 51, 51, 0.57);
}
/*头像大小*/

#host-bg {
    display: none !important
}
.header>.report-rbi-click {
    top: 10px;
    left: 10px;
    width: 60px;
    height: 60px;
}
/*搜索下拉*/

.topbar-hotsearch-layer {
    display: none !important
}
/*去神龙*/

.rank-first-update {
    display: none !important
}
.rank-first-style {
    display: none !important
}
.effect-panel-outer {
    display: none !important
}
.summon-effect-panel-outer {
    display: none !important
}
.pt-chat-plugins-cellr {
    display: none !important
}
.rank-first-updater {
    display: none !important
}
.rank-first-styler {
    display: none !important
}
.pt-chat-plugins-gift-cell {
    display: none !important
}
.guard-enter-msg {
    display: none !important
}
.guard-style {
    display: none !important
}
.live-holder {
    display: none !important
}
/*去提醒*/

.fresh-guide {
    display: none !important
}
.gift-guide {
    display: none !important
}
/*去左栏*/

#left-nav {
    display: none !important
}
#quiz-intrduction {
    display: none !important
}
/*去右栏*/

#right-nav {
    display: none !important
}
/*去下方推广*/

.stage-container-widget {
    display: block !important
}
.official-activities {
    display: none !important
}
/*去小游戏*/

.live-footer-games {
    display: none !important
}
/*去榜单*/

.stage-container-head .stage-container-right {
    display: none !important
}
/*去VIP进入*/

.vip-enter-msg {
    display: none !important
}
/*去聊天栏顶部礼物通知*/

.chatroom-top-gift {
    display: none !important
}
/*去观众列表*/

.chatroom-tabs {
    display: none !important
}
.chatroom-onlinelist {
    display: none !important
}
/*去礼物列表*/

#gift-history-list {
    display: none !important
}
/*去APP下载*/

.header-info-split {
    display: none !important
}
.header-info-app {
    display: none !important
}
/*去一战成名*/

#live-player .goog-inline-block {
    display: none !important
}
#live-player .gift-bar-item-ticket {
    display: none !important
}
#live-player .gift-bar-item-ticket-1 {
    display: none !important
}
/*关闭进场动画*/

.cars-enter-gif {
    display: none !important
}
.vip_enter_msg .vip_enter_msg_1 .vip-style .user_enter_msg {
    display: none !important
}
/*关闭聊天窗口上广告*/

.chatroom-ap {
    display: none !important
}
.medals-wrap {
    display: none !important
}
/*去除不在直播时视频页面的九个推荐*/

.live-ending-page {
    display: none !important
}
/*去除主播挑战任务*/

.task-level {
    display: none !important
}

/*去除苏宁广告*/
.tp-link .report-rbi-static
{
     display: none !important
}
/*去除不能复制弹幕功能*/
.chatroom-con, .gift-history-list{
    user-select:text
}
}
