css:
@-moz-document domain("sliver.tv") {
.chat-container,
.grand-raffle-container,
.grand-raffle-container .buy-tickets-container,
.mini-game-container,
.donation-notification,
.donation-notifications,
.buffs-tray,
.wallet.buff-multiplier-large,
.store-page,
.store-page .store-page-wrapper .right-gutter,
.store-page .store-page-wrapper .store-categories-tab-bar-wrapper,
body {
    color: #a6a6a6;
    background-color: #000;
    background-image: url(data:image/png;base64,) !important;
}
.streamer-bar,
.slvr-secondary-navbar,
.slvr-navbar__content,
.slvr-navbar__game-links li a,
.modal-overlay.diamond-store-modal,
.home-page-v2,
.channel-schedule-section,
.featured-videos-section,
.popular-crates-section,
.upcoming-events-section,
.win-page-2-col .main-panel .main-panel-content-wrapper .content-panel .get-notified-footer,
.unclaimed-inventory-item,
.bet-cards {
    color: #a6a6a6;
    background-color: #000;
    background-image: url(data:image/png;base64,);
}
.slvr-navbar__content,
.chat-container .header,
.inventory-page .buffs-panel .buffs-list .buff-item-row,
.inventory-page .buffs-panel .total-row,
.inventory-page .inventory-panel .inventory-content .claimed-inventory-content .claimed-inventory-list .claimed-inventory-item,
.channel-program-list,
.donation-notification:last-child,
.grand-raffle-action {
    border-top: 0px solid #dee1e6;
    border-bottom: 0px solid #dee1e6;
    border-right: 0px solid #dee1e6;
}
.slvr-navbar__currencies li a:hover,
.slvr-navbar__links li.slvr-navbar__link a:hover,
.tabs-wrapper .tabs .tab a:hover {
    color: #00bfff !important;
}
.slvr-navbar__currencies li a,
.slvr-navbar__links li a {
    color: white !important;
}
.chat-container .footer,
.inventory-page .buffs-panel,
input {
    color: #a6a6a6;
    background-color: #000;
    background-image: url(data:image/png;base64,);
}
.chat-container .header,
.store-item-card {
    background: #2f2f2f;
}
.inventory-page .inventory-panel .inventory-content .unclaimed-inventory-content .unclaimed-inventory-list .unclaimed-inventory-item .name,
.inventory-page .inventory-panel .inventory-content .claimed-inventory-content .claimed-inventory-list .claimed-inventory-item .left-container .metadata-container .name,
.channel-schedule-section__title,
.featured-videos-section__title,
.popular-crates-section__title,
.upcoming-events-section__title,
.video-card {
    color: white;
}
.inventory-page .inventory-panel .inventory-content .unclaimed-inventory-content .unclaimed-inventory-list .unclaimed-inventory-item .source-wrapper .source,
.buffs-tray .coin-cap-buff-tray .details .details-title,
.buffs-tray .title {
    color: #c6c988;
}
.inventory-page .inventory-panel .inventory-content .steam-trade-url-cta .steam-trade-url-cta-content .action-button {
    color: #2a3036;
    background: #fff;
}
.win-page-2-col .main-panel .main-panel-content-wrapper .content-panel .get-notified-footer .get-notified-title {
    color: #67d8a8;
}
.grand-raffle-container .buy-tickets-container,
.grand-raffle-container,
.bet-card,
.trivia-card {
    background: #2f2f2f;
}
.bet-card .bet-options .bet-option .bet-option-title,
.trivia-card .bet-options .bet-option .bet-option-title,
.bet-card .bet-title,
.trivia-card .bet-title,
.bet-card .bet-explanation .bet span,
.bet-card .bet-explanation .earn span,
.trivia-card .bet-explanation .bet span,
.trivia-card .bet-explanation .earn span,
.bet-card .betting-closed-explanation,
.trivia-card .betting-closed-explanation {
    color: #f2f1ef;
}
.daily-reward-countdown--fluid-width {
    background: #2F2F2F;
}
.chat-container .chat-messages-container .system-chat-message-container {
    background: #24A740;
}
.bet-payout-info__stake {
    color: yellow;
}
.bet-payout-info__payout {
    color: green;
}
.system-chat-message-container,
.donation-notifications,
.donation-notification__note,
.inventory-page .inventory-panel .inventory-content .urgent-message {
    color: white
}
.donation-notifications {
    background-color: #04bd8d;
}
.donation-notification {
    background: #04bd8d;
    border-bottom: 1px solid #04bd8d;
}
}
