css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

  .profile .right_column_container .profile_sidebar_ads, 
  .UIStandardFrame_SidebarAds, .UICompatibilityFrame_SidebarAds, 
  .UIWashFrame_SidebarAds, #home_sponsor, #highlights_ad, #home_sponsor_nile {
    display:none !important;
  }
  
  /* SEARCH RESULTS */
  .ff3 #nonfooter .UIStandardFrame_Content {width: 940px !important;}

  /* HOME PAGE */
  body.home div.UIFilterList div.UIFilterList_Item {min-height: 26px !important; height: auto !important;}
  body.home div.UIFilterList a.UIFilterList_ItemLink {min-height: 22px !important; height: auto !important;;}
  body.home div.UIFilterList span.UIFilterList_Title {white-space: normal !important;}
  body.home div.UIFilterList div.UIObject_SelectedItem {height: 26px !important;}
  body.home div.UIFilterList div.UIObject_SelectedItem span.UIFilterList_Title {white-space: nowrap !important;}
  body.home div#home_stream .ufi_section {width: 450px !important;}
  body.home div#home_stream .ufi_section .comment_text {width: 400px !important;}
  body.home div.comments_add_box_image {display: block !important;}
  body.home div.add_comment_box_wrapper textarea {width: 400px !important;}

  /* INBOX PAGE */
  body.inbox .message_rows {width: 938px !important;}
  body.inbox .message_rows tr .name_and_date {width: 100px !important;}

  body.inbox .notifications .main_column {width: 730px !important;}
  body.inbox .notifications .main_column #notifications .story .body {width: 660px !important;}
  body.inbox .notifications .side_column {float: right !important;}

  body.inbox .updates_all .main_column {width: 730px !important;}
  body.inbox .updates_all .main_column .updates_messages .s_message_header {width: 585px !important;} 
  body.inbox .read_updates .main_column {width: 720px !important;}
  body.inbox .updates_all .side_column, 
  body.inbox .read_updates .side_column {float: right !important;}

  body.inbox .UIStandardFrame_Content {width: 940px !important;}

  /* NOTES PAGE */
  body.notes .UIWashFrame_Content {width: 938px !important;}
  body.notes .UITwoColumnLayout_Content {width: 720px !important;}
  body.notes .note_body, body.notes .note_content {width: 720px !important;}
  body.notes .ufi_section {width: 720px !important;}
  body.notes .ufi_section .comment_actions {width: 660px !important;}
  body.notes .ufi_section .comment_text {width: 660px !important;}
  body.notes .ufi_section .comments_add_box textarea {width: 650px !important;}

  /* PHOTOS PAGE */ 
  body.album .summary_bar {width: 930px !important;}
  body.album #album_container {width: 938px !important;}
  body.album #album_container table {width: 100% !important;}
  body.album .footer {width: 925px !important;}

  /* PROFILE PAGE */
  .profile #profile_top_bar {padding: 0 180px 0 220px !important;}
  .profile #profile_top_bar #top_bar_pic {display: none !important;}
  .profile .left_column {display: block !important;}
  .profile .profile_bottom_wash .right_column {width: 720px !important;}
  .profile .profile_bottom_wash #tab_canvas {width: 720px !important;}
  .profile .profile_bottom_wash .ufi_section {width: 640px !important;}
  .profile .profile_bottom_wash div.comments_add_box_image {display: block !important;}
  .profile .profile_bottom_wash div.add_comment_box_wrapper textarea {width: 590px !important;}
  .profile .profile_bottom_wash .comment_actions {width: 598px !important;}
  .profile .profile_bottom_wash .comment_text {width: 590px !important}
  .profile .profile_bottom_wash .profile_info dd {width: 550px !important;}
  .profile .profile_bottom_wash .photos_tab #photos_of_wrapper, 
  .profile .profile_bottom_wash .photos_tab #photos_by_wrapper {width: 720px !important;}
  .profile .profile_bottom_wash .photos_tab #photos_by_wrapper #main_album_content,
  .profile .profile_bottom_wash .photos_tab #photos_by_wrapper #main_album_content .content_wrapper {width: auto !important;}
  .profile .profile_bottom_wash .photos_tab #photos_by_wrapper #main_album_content .album_cell {padding: 8px 3px 0px 4px !important;}
  .profile .profile_bottom_wash .box_tab .app_box_left_column {width: 480px !important}
  .profile .profile_bottom_wash .box_tab .app_box_left_column .app_custom_content .inside {width: 445px !important}

  /* HIDE CONVERSATIONS - FOR USE WITH DIGSBY */
  .tab_handle, .tab_button_div, .tab_hit_area, #presence_error_section, #buddy_list_tab { display: none !important; }
  
  /* MAKE THE FOOTER THE SAME WIDTH AS THE HEADER */
  div#presence_ui {width: 964px !important; margin-left: auto !important; margin-right: auto !important;}
}
