css:
@-moz-document domain(www.tumblr.com) {
    
/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/symbols/sym-2/sym172.ani), url(http://cur.cursors-4u.net/symbols/sym-2/sym172.png), progress !important;} /* End http://www.cursors-4u.com */
    
body {
background: url(/*[[background]]*/) repeat center fixed!important;
}
a, body {
    color: #fba3d7;
}
.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsl(326.3, 100%, 87.5%);
    background-color: #ffd6ee80;
}
.tab_bar .tab.active, .tab_bar .tab.active .tab_anchor, .tab_bar .tab.selected, .tab_bar .tab.selected .tab_anchor {
    color: #f2c9ff;
}
.selected .tab_notice {
    background: #e6beff;
}
    
.tab-bar-container .tab_notice {
    color: #cc7bff;
}
.tab-notice--outlined {
    border: 2px solid #c960ff;
}
.tab_bar .tab_anchor {
    color: #ffa3d3;
}
.compose-button {
    fill: #FFC2E3;
}
.popover_header {
    background: #fff1fb;
    border-bottom: 1px solid #ffd4f3;
    color: #f7c1ff;
}
.l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content {
    background-color: #ffd6ee80;
    border: 1px solid #FFBFE3;
    width: 1000px;
    border-radius: 10px;
}
.l-container.l-container--two-column-dashboard .left_column {
    background-color: transparent;
    margin-top: 10px;
}
.post_avatar {
    background-color: transparent;
}
.post_avatar .post_avatar_link {
    background: #FFD6EE 50% 50% no-repeat;
    border: 3px solid #FFBFE3;
    border-radius: 20px;
}
.post.new_post {
    background-color: #ffd6ee;
    border: 1px solid #FFBFE3;
    border-radius: 10px;
    width: 542px;                                        
}
.peepr-body .indash_blog .post {
    background: #FBEAFF;
}
.new_post_label i.icon_post_text, 
.new_post_label i.icon_post_photo, 
.new_post_label i.icon_post_quote, 
.new_post_label i.icon_post_link, 
.new_post_label i.icon_post_chat,
.new_post_label i.icon_post_audio,
.new_post_label i.icon_post_video {
    color: #FFC2E3;
}
.post.new_post .new_post_label {
    color: #FFC2E3;
}
.post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item {
    background-color: #f9dcff;
    border: 1px solid #eda7ff;
    border-radius: 10px;
}
.post_full.is_note .nipple, .post-composer_note-post .nipple {
    border-left: 8px solid #F9DCFF;
}
.notification .notification_sentence .username, .notification .notification_sentence .banner_text {
    color: rgba(236, 174, 255, 0.91);
}
.notification .notification_sentence a {
    color: rgba(240, 182, 255, 0.9);
}
.notification {
    color: rgba(234, 179, 255, 0.9);
}
.l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column {
    background-color: #FFD6EE;
    width: 320px;
    border: 1px solid #FFBFE3;
    border-radius: 10px;
    margin-top: 10px;
    margin-left: 10px;
}
.identity .controls_section li.account_header, .identity .controls_section li.contributors_header, .identity .controls_section li.editors_header, .identity .controls_section li.section_header {
    color: hsla(283.7, 100%, 84.1%, 0.9);
}
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title {
    color: hsla(283.7, 100%, 84.1%, 0.9);
}
.identity .controls_section li.section_header {
    border-bottom: 2px solid #FFC4E4CC;
}
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name {
    color: hsla(288.3, 100%, 82%, 0.8);
}
.identity .controls_section.user_list li .follow_list_item_blog::before {
    background-image: linear-gradient(90deg,rgba(54,70,93,0),#FFD6EE);
    border-right: 5px solid #FFD6EE;
}
.identity .controls_section.user_list li:active .follow_list_item_blog::before, .identity .controls_section.user_list li:hover .follow_list_item_blog::before {
    background-image: linear-gradient(90deg,rgba(68,83,104,0),#FFD6EE);
    border-right: 5px solid #FFD6EE;
}
.identity .right_column::after {
    background: linear-gradient(180deg,transparent,rgba(54,70,93,0));
}
.radar .radar_footer .radar_avatar::before {
    background-image: linear-gradient(to right, rgba(54,70,93,0), #FFD6EE);
}
.radar .radar_controls {
    background-color: #FFC2E3;
    border-radius: 5px;
    border: 1px solid #FFBFE3;
}
.radar .radar_tumblelog_label .name {
    color: hsla(283.7, 100%, 84.1%, 0.9);
}
.radar .radar_tumblelog_label .subheading {
    color: hsla(288.3, 100%, 82%, 0.8);
}
.plus-follow-button {
    cursor: pointer;
    color: #FFC7E5;
}
.no_posts_found {
    background: hsla(327.7, 100%, 97.5%, 0.4);
    color: #FFBFE3;
    border: 1px solid #FFBFE3;
}
.post.post_full {
    background: #FFD6EE;
    word-wrap: break-word;
    border: 1px solid #FFBFE3;
    border-radius: 10px;
}
.post_full .post_tags .post_tag {
    color: #EAA7FF;
}
.post_controls .post_control::before {
    color: #EBA7FC;
}
.post_full .post_footer {
    color: #EBA7FC;
}
.post .post-source-footer {
    border: 1px solid #ECA7FC;
    border-radius: 10px;
    background: #fcf2ff;
    color: #ECA7FC;
    margin-left: 5px;
}
.post .post-source-footer a.post-source-link {
    color: #eda4ff;
}
.radar .radar_controls .note_count > a {
    color: #FF97D6CC;
}
.radar .radar_button {
    color: #FF97D6CC;
}
.post_full .post_header .post_info .post_info_link:first-child {
    color: #ffa4e1cc;
}
.post .post_info .reblog_icon {
    color: #e8b3ff;
}
.post_full .post_info .post_info_fence .reblog_source .post_info_link {
    color: #e4b1ff;
}
.reblog_follow_button .follow-text, .worded-follow-button .follow-text {
    color: #fface4e6;
}
.post_full .post_header, .post_full .post_header .post_info .post_info_link {
    color: #e4b1ff;
}
.discover-controls-wrapper .types-tabs > .content-control {
    color: hsla(283.6, 100%, 85.7%, 0.9);
}
.l-container.l-container--flex .l-content {
    background-color: #ffd6ee80;
    border: 1px solid #FFBFE3;
    border-radius: 10px;
}
.search_sub_header .search_term_heading {
    color: #ffc1ed
}
.search_sub_header .search_control.related_searches {
    color: hsla(321.6, 100%, 88%, 0.9);
}
.search_sub_header .search_control.related_searches .search_control_link {
    color: hsla(288.4, 100%, 86.9%, 0.6);
}
.post_brick {
    background: #fdedff;
    border: 1px solid #f2c9ff;
    border-radius: 10px;
}
.post_brick .post_tags .post_tag {
    color: #ffb7ea;
}
.post_brick .post_footer {
    color: #ffa4db;
}
.dashboard_options_form, .tag_psa {
    border: 1px solid #FFBFE3;
    color: #FFBFE3;
}
.identity .controls_section a .count {
    color: hsl(330.5, 100%, 88%);
}
.identity .controls_section .open_blog .open_blog_link {
    color: hsla(283.6, 100%, 85.7%, 0.9);
    font-size: 13px;
}
.identity .controls_section .open_blog .currently_selected_blog {
    color: hsla(287.5, 100%, 89.6%, 0.9);
}
#dashboard_useraction_following .l-content {
    border-radius: 15px;
    background: #fff1fb80 !important;
}
.follower.invite_someone {
    border-top: 1px solid rgba(255, 175, 229, 0.93);
    background: #ffe6f8;
}
.follower {
    border-bottom: 1px solid rgba(255, 166, 229, 0.91);
    background: #ffeaf8;
}
.chrome.blue {
    border-color: #e19eff;
    border-bottom: #e19eff;
    background: #ebc4ff;
    color: #e19eff;
    margin: 5px;
}
.elevator {
    transition: opacity .15s ease-out,transform .15s ease-out;
    color: hsla(324.5, 100%, 85.1%, 0.9);
}
}
