body {
    margin: 0;
    padding: 0;
    font-family: 'Roboto', sans-serif;
    font-size: 16px;
    display: flex;
    justify-content: space-between;
}

#header,
#sections {
    padding-top: 2rem;
    padding-bottom: 2rem;
}

#header {
    background-color: transparent;
    box-shadow: none;
    width: 100%;
    position: relative;
    flex: 1;
    border-right: none;
    padding-left: 2rem;
    padding-right: 2rem;
}

#header h1,
#header section h2 {
    margin-bottom: 1rem;
}

#header section {
    margin-bottom: 2rem;
}

h1,
h2 {
    font-weight: 400;
}

label {
    font-weight: 300;
}

input {
    border-radius: .3rem;
    border: 1px solid #cccccc;
    padding: .5rem .7rem;
}

input,
select {
    font-size: 1.2em;
}

#options [type="number"] {
    max-width: 5rem;
}

button {
    background-color: #ffffff;
    border: .1em solid #cccccc;
    border-radius: .2rem;
    font-size: 1.1em;
    font-weight: 300;
    padding: .5em .7em;
    margin: 0 .2em;
    transition: .2s ease;
}

button:hover {
    background-color: #cccccc;
    color: #ffffff;
}

.remove-applies-to,
.add-applies-to,
.add-section,
.remove-section,
#cancel-button,
#save-button,
.style-edit-link button,
.disable,
.delete,
#add-style-label {
    color: #ffffff;
}

.remove-applies-to,
.remove-section,
#cancel-button,
.delete {
    background-color: #e24641;
    border-color: #d43f3a;
}

.remove-applies-to:hover,
.remove-section:hover,
#cancel-button:hover,
.delete:hover {
    background-color: #ffffff;
    color: #e24641;
}

#save-button,
.style-edit-link button,
#add-style-label {
    background-color: #337ab7;
    border-color: #2e6da4;
}

#save-button:hover,
.style-edit-link button:hover,
#add-style-label:hover {
    background-color: #ffffff;
    color: #337ab7;
}

.disable {
    background-color: #f0ad4e;
    border-color: #eea236;
}

.disable:hover {
    background-color: #ffffff;
    color: #f0ad4e;
}

.add-applies-to,
.add-section {
    background-color: #5cb85c;
    border-color: #4cae4c;
}

.add-applies-to:hover,
.add-section:hover {
    background-color: #ffffff;
    color: #5cb85c;
}

#basic-info-enabled {
    display: flex;
    margin-top: 1rem;
}

#basic-info-enabled input[type="checkbox"] {
    margin-right: .5rem;
}

#options [type="number"] {
    max-width: 5rem;
}

.aligned {
    display: flex;
    align-items: center;
    padding-bottom: 2rem;
}

.aligned input,
.aligned select {
    margin-left: 1rem;
}

.CodeMirror {
    font-family: 'Fira Mono', monospace;
}

img {
    margin-left: .5rem;
}

#sections {
    flex: 2.5;
    min-width: 40rem;
    padding-left: 3rem;
    box-shadow: 0 0 15px 5px rgba(0, 0, 0, .2);
}

#sections > div:not(:first-of-type) {
    border: none;
}

#actions div:first-child {
	display: flex;
     align-items: center;
}

#help-popup {
    border-radius: .5rem;
    box-shadow: 3px 3px 15px rgba(0, 0, 0, .5);
}

#help-popup .close-icon {
    margin: .5rem;
    border-radius: 100%;
    background-color: tomato;
}

#help-popup .title,
#help-popup .contents {
    padding: 1rem 2rem;
}

.applies-to {
    margin-bottom: 1rem;
}

#installed {
    flex: 2.5;
    margin: 0;
    box-shadow: 0 0 15px 5px rgba(0, 0, 0, .2);
}

#header {
    padding-right: 5rem;
}

[style-id] {
    border: none;
    padding-left: 5rem;
    margin-bottom: 2em;
}

fieldset div,
#options div {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
}

fieldset div label,
fieldset div input {
    margin: 0;
}

input[type="checkbox"],
input[type="radio"] {
    margin: 0;
    margin-right: .5rem;
}

fieldset div input[type="checkbox"] {
    margin-right: .5rem;
}
}
