css:
@-moz-document domain("www.watch2gether.com") {
:root {
    --acc: #00FFFF;
    --accd: rgba(225, 255, 255, .5);
}

a.bottom-sticker.feedback-sticker,
a.bottom-sticker.chromeext-sticker.chrome-extension-install,
#client-pusher > div.topbar > div > div > div > div.six.wide.computer.tablet.only.column.logo-column > div,
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > div:nth-child(2),
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > a,
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > div:nth-child(5),
#w2g-greatrow,
#player-settings-section > a,
#time-slider > div > div.noUi-origin > div:before,
#time-slider > div > div.noUi-origin > div:after,
#main-volume-slider > div > div.noUi-origin > div:before,
#main-volume-slider > div > div.noUi-origin > div:after,
#client-pusher > div.footer {
    display: none;
}

.topbar {
    background: #212121;
    border: none;
    box-shadow: 0px 0px 15px -1px rgba(0, 0, 0, .5);
    z-index: 999;
    position: relative;
}

.ui.toggle.checkbox input:checked ~ .box:before,
.ui.toggle.checkbox input:checked ~ label:before,
.noUi-connect {
    background-color: var(--acc) !important;
}

.w2g-yellow-bg,
.card.smartcard.available > div.content,
.card.smartcard.available > div.content>a,
.usermsg .ui.pointing.label,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
label:before,
.autocomplete-suggestion {
    background-color: var(--acc) !important;
    color: rgba(255, 255, 255, 0.9) !important;
}

#chat-input-field,
#chat-input-form > div > div,
.ui.primary.button,
.ui.primary.buttons .button,
#search-bar-input,
#search-bar-form > div > div > i,
#search-bar-form > div > div > button,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown,
#playlist-container > div.ui.fluid.action.input > input[type="text"],
#playlist-container > div.ui.fluid.action.input > button,
#playlist-container > div.ui.fluid.action.input > div.change-playlist-button.ui.icon.button {
    background-color: #212121 !important;
    color: #fff;
}

#chat-messages-table > div > div > div > div.chat-message-time {
    color: var(--acc) !important;
}

.card.smartcard.available,
#player-overflow,
.usermsg .ui.pointing.label,
#playlist-history > div.ui.bottom.attached.segment,
.main-content .ui.menu .active.item,
#chat-input-form > div,
#search-container,
#search-results-grid > div,
#userbar,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
#player-chat.fixed,
#search-bar-form,
#playlist-container > div.ui.fluid.action.input {
    box-shadow: 0px 0px 15px -1px rgba(0, 0, 0, .5) !important;
    /*     position: inherit;
    z-index: 5; */
}

#userbar {
    border-radius: 5px;
}

#client-container > div.ui.stackable.grid > div:nth-child(3),
#search-results-grid > div,
#player-settings-section > div.volume-dropdown.ui.icon.bottom.right.pointing.dropdown.item.active.visible > div {
    background-color: rgba(33, 33, 33, 0.7);
}

#playlist-shuffle > label,
#chat-messages-table > div.they.usermsg > div.chat-avatar > div,
#search-results-grid > div *,
.ui.selection.visible.dropdown>.text:not(.default) {
    color: #fff !important;
}

.main-content .ui.menu .active.item {
    box-shadow: 0 2px 0 var(--acc) inset;
}

#player-room-actions > span,
#player-video-info > b > a {
    color: var(--acc);
}

#player-room-actions > span {
    color: var(--accd);
}

#player-video-info,
#player-menu > div.ui.attached.inverted.small.menu,
#search-results-grid > div > div.extra.content > div {
    border-radius: 0;
    border: none;
    background-color: #212121 !important;
}

#playlist-history > div.ui.bottom.attached.segment,
#search-container,
.main-content .ui.menu .active.item,
#playlist-history > div.ui.top.attached.tabular.small.menu {
    background-color: rgba(33, 33, 33, .2) !important;
    border-radius: 0;
    border: none;
}

#chat-messages,
#client-container,
#chat-input,
#chat-input-field,
#search-results-menu,
#search-bar-input,
#search-results-grid > div,
#search-results-grid > div > div.image.mod-disable-clear,
#search-results-grid > div > div.content.search-video-content,
#search-results-grid > div > div.price,
#userbar,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active.visible > div.menu.transition.visible,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active > div.menu.transition.animating.slide.down.in,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.visible > div.menu.transition.visible.animating.slide.down.out,
.autocomplete-suggestions {
    border: none !important;
}

.autocomplete-suggestions::-webkit-scrollbar-track {
    background-color: #212121;
    box-shadow: none;
    border-radius: 0;
}

.autocomplete-suggestions::-webkit-scrollbar {
    width: 5px;
    background-color: #FFF;
}

.autocomplete-suggestions::-webkit-scrollbar-thumb {
    background-color: #FFF;
    box-shadow: none;
}

#chat-input {
    padding: 0;
}

#search-bar-input {
    padding: .67857143em 1em;
}

#chat-input-field,
#chat-input-form > div > div,
#search-bar-input,
#search-bar-form > div > div > div,
#search-results-grid > div,
#search-results-grid > div > div.image.mod-disable-clear > img,
#userbar,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active.visible > div.menu.transition.visible,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active > div.menu.transition.animating.slide.down.in,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.visible > div.menu.transition.visible.animating.slide.down.out,
#playlist-container > div.ui.fluid.action.input > input[type="text"],
#playlist-container > div.ui.fluid.action.input > div.change-playlist-button.ui.icon.button {
    border-radius: 0;
}

#search-bar-form > div > div > div {
    margin-left: -7.5px;
}

#playlist-container > div.ui.basic.segment {
    background: transparent !important;
}

#gfycat > i {
    filter: invert(1);
}

.main-content .ui.menu .item,
#player-chat {
    transition: .5s ease-in-out;
}

.main-content .ui.menu .active.item {
    padding: 0 25px !important;
}

body > div.autocomplete-suggestions {
    max-height: 150px !important;
}

#player-chat {
    border: none !important;
    box-shadow: none !important;
}

#time-slider > div > div.noUi-origin > div,
#main-volume-slider > div > div.noUi-origin > div {
    border-radius: 100000px;
}

.ui.menu .ui.dropdown .menu>.item {
    padding: 1em 2em !important;
}

.noUi-vertical .noUi-handle {
    width: 28px;
    height: 28px;
}

.w2g-app-icon {
    display: none !important;
}

.ui.grid>.row>[class*="center aligned"]
.column.column, 
.ui.grid>[class*="center aligned"]
.column.column, 
.ui.grid>[class*="center aligned"]
.row>.column, 
.ui[class*="center aligned"]
.grid>.column, 
.ui[class*="center aligned"]
.grid>.row>.column {
     display: none;
}

/*Define avatares, transparencia y redondez*/
.smartcard img {
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    border-radius: 100%;
}

.ui.cards>.card {
    font-size: 1em;
    background: transparent !important;
    border: none !important;
    box-shadow: none;
}

/*Logo y cabecera*/
.topbar 
.logo {
    background-image: url(https://3.bp.blogspot.com/-uHV5peO2T1c/WlH6BrtrrFI/AAAAAAAAAkM/8Rk0fduqeQI1buMKbqjATTpu3Pu3r4KJwCLcBGAs/s320/ezgif.com-resize.gif);
}

.topbar {
    background: #000000;
    border: none;
    box-shadow: 0px 0px 15px -1px rgba(0, 0, 0, .5);
    z-index: 999;
    position: relative;
}

/*Invierte el chat*/
#userbar-chat {
    -webkit-flex-direction: column;
    -ms-flex-direction: column;
    flex-direction: column-reverse;
    margin-left: 860px;
    float: right;
    margin-top: 140px;
    position: fixed;
    z-index: 900;
    height: 460px !important;
    width: 486px !important; 
}

/*tamaÃ±o de video*/
.ui.grid>[class*="ten wide"].column {
    width: 85% !important;
    position: absolute;
    z-index: 100 !important;
    margin-left: -155px;
    float: left;
}

#chat-messages {
 margin-top: -150px;   
}

/*iconito de usuarios en sala*/
#userbar .label {
    position: absolute;
    top: 0.4em;
    margin: -15em 0 0 -12em !important;
}

/*margen general barra de listas y resultados*/
#client-container > div.ui.stackable.grid > div:nth-child(3) {
margin-top: 140px !important;
}

#search-container {
    background-color: rgba(33, 33, 33, .2) !important;
    border-radius: 0;
    border: none;
    margin-top: 440px !important;
}

#player-video-info {
    border-radius: 0;
    border: none;
    background-color: #21212100 !important;
}

/*Define la informacion del video*/
#player-video-info {
    width: 100% !important;
    max-width: 100% !important;
    margin: 0 !important;
    padding: 0.5em;
    font-size: 0.9em;
    z-index: 900;
    text-align: center;
}

.ui.grid>.row>[class*="five wide"].column {
    width: 31.25% !important;
    margin-top: 470px !important;
}

#client-container > div.ui.stackable.grid > div:nth-child(3) {
 background-color: rgba(33, 33, 33, 0);   
}

/*avatar barra derecha*/
.ui.fluid.image, 
.ui.fluid.image img, 
.ui.fluid.image svg, 
.ui.fluid.images, 
.ui.fluid.images img, 
.ui.fluid.images svg {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 100%;
    /* align-content: center; */
}

.ui.card .meta *, .ui.cards>.card .meta * {
    margin-right: .3em;
    display: none !important;
}

.card.smartcard.available > div.content>a {
 color: #a221ba !important;   
}

.usermsg .ui.pointing.label {
    color: rgba(29, 3, 245, 0.9) !important;
}
  
.smartcard .card-menu {
    display: none;
    position: absolute;
    left: 0;
    z-index: 120;
    width: 100%;
    background-color: #4c4a4700 !important;
    color: #fff !important;
    padding: 0.1em;
    text-align: right;
    font-size: 1.2em;
}

.card.smartcard.unavailable {
  -webkit-filter: blur(5px);
  -moz-filter: blur(5px);
  -o-filter: blur(5px);
  -ms-filter: blur(5px);
  filter: blur(5px);
}

.card.smartcard.unavailable:hover  {
    
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0px);
  filter: blur(0px);
}

.card.smartcard.unavailable.windowed.ui-draggable.ui-resizable {
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0px);
  filter: blur(0px);
}
  
  /*posicion del background*/
.main-content {
background-attachment: fixed;
}

.ui.items>.item>.image>img {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 20px;
    border: none;
}

#search-results-grid > div * {
    color: #00fff3 !important;
}

div.menu {
background: #0000009c !important;
}

div.item:hover {
 background: #000000cf !important;   
}

.ui.menu .item>i.icon {
 color: var(--acc)!important   
}

/*Sala Principal*/
.main-content {
    background-color: black;
}

#application-container>.segment {
    background-color: black;
}

div.ui.segment {
    background-color: black;
}

.user-room-list .extra>span {
    color: deepskyblue;
}

.ui.items>.item>.content>.description {
    color: #6435c9;
}

.ui.items>.item img.avatar {
    width: 40px;
    height: 40px;
}
  
  i.icon, i.icons {
    font-size: 10px;
    color: #15141470;
}
  
  i.green.icon {
    color: #3404f3 !important;
}
  
  div.ui.segment {
  background-color: black;
  }
  
  footer {
    background: linear-gradient(to bottom, #000000 1%, #04fbf0 100%);
    color: rgba(255,255,255,0.5) !important;
}

.noUi-handle {
    border-radius: 100%; 
}
}
