css:
@-moz-document domain("basecamp.com"){

html,
body{
  font-family: 'Proxima Nova', 'Helvetica Neue', Helvetica, Roboto, Arial, sans-serif;
}

body {
  background-color: #fff;
}

h1, h2, h3, h4, h5 {
  font-weight: 600;
}

b, strong {
  font-weight: 600 !important;
}

.topnav {
  border: none;
  background-color: #f2f6f6;
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 6;
}

.left-tab--support {
  display: none !important;
}

.card--project-logo {
  position: fixed;
  top: .5rem;
  left: .5rem;
  z-index: 999;
  margin: 0 !important;
  width: 15rem;
  height: 15rem;
  display: none !important;
}
.card--project-logo .card__content {
  padding: 0;
}

.recordable {
  position: relative;
  background: #fff;
  box-shadow: 0 0 65px 0 rgba(0,0,0,0.1);
  border-radius: 0;
  padding: 2rem;
}

.document-style-editor.recordable .sticky-trix-toolbar {
  margin-left: 0;
  margin-right: 0;
  padding-left: 0;
  padding-right: 0;
}

/* Button */

.action_button, .emoji-button {
  padding-left: 2.5rem;
  padding-right: 2.5rem;
  border: none;
  text-decoration: none;
  text-align: center;
  font-weight: 400;
  color: #283c46;
  display: inline-block;
  line-height: 1.5;
  white-space: normal;
  vertical-align: middle;
}

.action_button.primary {
  background-color: #00d060;
  border-color: #00d060;
  border: none;
  color: #fff;
}
.action_button.primary:hover {
  background-color: #02B454;
}

.recordable__footer {
  margin: 2.5rem 0 0 !important;
}

.recordable__history {
  background: none;
  padding-left: 0;
}


.metadata {
  font-size: 0.8em;
  color: #a4a5b2;
}
.metadata img {
  opacity: .5;
}

.txt--uncolor {
  color: #a4a5b2;
}

.card-grid--projects {
  overflow: hidden !important;
}



.panel.filterable-projects {
  max-width: 65em !important;
}

.card-grid--projects .card {
  float: left;
  margin: 0 1% 2% !important;
}

.card {
  border-radius: 0 !important;
  box-shadow: none;
  border: 2px solid #e9e9f1;
}

.card:hover {
  border-color: #fe9200;
}

.card--add-project {
  background-color: #f2f6f6;
}

.card--project .card__title {
  font-size: 2.5rem;
}

.card__description {
  color: #a4a5b2;
}

.project-index__section header.centered {
  text-align: left;
}
.break--on-background>span {
  background-color: #f1f6f6;
  padding: .5rem 3rem !important;
  border-radius: 999px;
}

.project-index__header {
  color: inherit;
  margin: 3rem 0 2rem !important;
}

@media screen and (min-width: 768px) {

  .card--project {
    width: 31%;
  }
}

.recordable--todo .todo .checkbox__button {
	height: 2rem;
  width: 2rem;
  border-color: gray;
  border-width: 1px;
  border-radius: 0;
  box-shadow: none;
}

.recordable--todo .todo .checkbox__text {
  display: none;
}

}
