css:
@-moz-document url-prefix("https://ffmpeg.org/") {
body {
	/* background-color:	#EEF1E8; */
	background-color:	#DEDED8 !important;
	color: 				#000000 !important;
	font-family: 		"Times New Roman" !important;
	font-size: 			17px !important;
	line-height: 			22px !important;
	text-align: 			left !important;
}
dd {
	line-height: 			22px !important;
	padding-top: 		0px !important;
}
samp {
	font-family: 		"Arial" !important;
	font-size: 			15px !important;
	line-height: 			18px !important;
}
p samp{
	font-size: 			14px !important;
	color: 				#9A0001 !important;
}
code {
	background-color: 	transparent !important;
	font-family: 		"Arial" !important;
	font-size: 			14px !important;
	line-height: 			15px !important;
	color: 				#862121 !important;
	display: 			inline !important;
	/* margin-top: 		0px; */
	/* margin-bottom:		0px; */
	margin-left: 		1px !important;
	margin-right: 		1px !important;
}
.example pre{
	border-left: 		2px solid #968780 !important;
	border-top: 		none !important;
	border-right: 		none !important;
	border-bottom: 	none !important;
	font-family: 		"Consolas" !important;
	color: 				#000000 !important;
	background-color: 	#E6E6DD !important;
	border-radius:		0px !important;
}
.verbatim{
	font-family: 		"Courier New" !important;
	font-weight: 		bold !important;
	line-height: 			13px !important;
}
h2 {
	font-weight: 		bold !important;
}
h3 {
	font-family: 		"Arial" !important;
	font-size: 			20px !important;
	padding-left: 		0px !important;
	padding-top: 		18px !important;
	border-bottom:		2px #222222 solid !important;
	padding-bottom: 	10px !important;
}
h4 {
	/* color: 				#000000; */
	font-family: 		"Arial" !important;
	text-align: 			center !important;
	font-weight: 		normal !important;
	border-bottom: 	none !important;
	padding-bottom:	0px !important;
}
h3 strong {
	/* color: 				#C34FCE; */
	color: 				#C34FCE  !important;
}
h1,h2,h3,h4,h5,h6 {
	font-family: 		"Arial" !important;
	color: 				#000000 !important;
}

 a	{
	text-decoration:	none !important;
	color: #49863A  !important;

}
a:hover	{
	text-decoration: 	underline !important;
	/* color: 				inherit !important; */
}
p a ,
li a{
	font-weight: 		bold !important;
	color: 				#862121 !important;
}
p a:hover 
li a:hover{
	color: 				#B84B4B !important;
}
p a:before,
li a:before{
	/* content:		":: ";  */
}
.col-md-6 {
	position: 			relative !important;
	min-height:			1px !important;
	padding-right:		15px !important;
	padding-left:		15px !important;
	width:				100% !important;
	float:				left !important;
}
.well {
	background-color: 	transparent !important;
	color: 				#000000 !important;
}
.content-header {
	height: 				auto !important;
	background-color: 	#B1B5A8 !important;
}
.content-header h1 {
	color: 				#000000 !important;
	display: 			block !important;
	margin: 			0 !important;
	margin-bottom: 	20px !important;
	line-height: 			normal !important;
	border-bottom: 	none !important;
}

/*  */
/*  sidebar  */
/*  */

#sidebar-wrapper {
	/* width:			200px; */
	background:	#E6E6DD !important;
	/* transition: 		none; */
	/* position: 		absolute; */
}

#page-content-wrapper {
	top: 			0 !important;
	width: 			700px !important;
	/* border: 		3px solid #968780 !important; */
	border: 		1px solid #8F9088 !important;
	margin-left: 	80px !important;
	position: 		absolute !important;
}

}
