@-moz-document url-prefix("https://www.keyshot.com/forum/") {
/*Dark Grey (background)*/
body, #header, #header div.frame {background: #111;}

/*Grey (layer1)*/

#content_section, #content_section div.frame, .windowbg span.topslice, .windowbg span.topslice span, .windowbg span.botslice span, .windowbg span.botslice, .description, .description_board, .plainbox, #footer_section, #footer_section div.frame, .roundframe, div.title_barIC, div.title_barIC h4.titlebg, span.upperframe span, span.upperframe, span.lowerframe span, span.lowerframe {background: #1c1c1c}

.windowbg, #preview_body, .windowbg2 span.topslice span, .windowbg2 span.topslice, .windowbg2 span.botslice span, .windowbg2 span.botslice {background-color: #1c1c1c}

.windowbg2 {background-color: #252525}

[class="windowbg"] {border-bottom: 1px solid #1c1c1c; border-top: 0px solid green}
[class="windowbg2"] {border-bottom: 0px solid #1c1c1c; border-top: 0px solid blue}
.windowbg2 span.botslice  {background: #1c1c1c}
.windowbg2 span.botslice  span{background: #1c1c1c}

.windowbg span.botslice {background: #1c1c1c}
.windowbg span.botslice span {background: #1c1c1c}

.windowbg2 span.botslice {border-top: 5px solid #252525}

[class="subject windowbg2"] {background: #1c1c1c}
[class="lastpost windowbg2"] {background: #1c1c1c}
[id*="board"] {background: #1c1c1c}

.windowbg2 .inner {border-top: 2px solid #252525}
.windowbg .inner {border-top: 2px solid #1c1c1c}

.windowbg2 .attachments {border-top: 2px solid #252525}
.windowbg .attachments {border-top: 2px solid #1c1c1c}

table.table_grid td {border-bottom: 1px solid #1c1c1c; border-right: 1px solid #1c1c1c}

.navigate_section ul {border-top: 1px solid #1c1c1c}

input, button, select, textarea {color: white; background: #1c1c1c}

input:focus, textarea:focus, select:focus {border: 1px solid #1c1c1c;}

.avatar img {border: 2px solid #1c1c1c;}

/*Light Grey Text (almost white)*/

.dropmenu a, .windowbg, #preview_body, .windowbg, #preview_body  {color: #e1e1e1}

body, td, th, tr, .windowbg2  {color: #e1e1e1}

/*Grey Text*/

body, td, th, tr, .windowbg2, .dropmenu a, .windowbg, #preview_body, .windowbg, #preview_body, .dropmenu a, .windowbg, #preview_body, .windowbg, #preview_body, .table_list tbody.content td.children, h4 {color: #a2a2a2}

/*Light Blue Text*/

a:link, a:visited, .poster h4, .poster h4 a {color:  #84b2ff}

/*Lightest Blue Text (whiteish)*/
.dropmenu li a.active, .table_list tbody.content td.info a.subject {color: #c8ddff;}

/*Locked Posts COLOR (dark grey)*/
.lockedbg2, .lockedbg {background: #111}

/*Sticky Posts COLOR*/
.stickybg2, .stickybg {background: #21212d}

/*Quote Background Color*/
blockquote.bbc_standard_quote {background-color: #292929}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {color: #7d7d7d; border-top: 2px solid #292929; border-bottom: 2px solid #292929;}

/* Logo changed from black text to white KeyShot */
.forumtitle > a > img {
    background: url(http://i.imgur.com/sdNWyrw.png)no-repeat !important;
    height: 0 !important;
    width: 0 !important;
    padding-left: 150px !important;
    padding-top: 40px !important;
					}

/* NEW tag icon changed */
[id*="newicon"] {
    background: url(http://i.imgur.com/KajYEAp.png)no-repeat!important;
    height: 11px !important;
    width: 26px !important;
	margin-top: 8px;
	margin-left: 6px;
    position: absolute;
    z-index: 2;}

/* NEW tag icon removed */
a>img[alt="New"]   {
    height: 0 !important;
    width: 0 !important;
    position: absolute;
	z-index: -1;}

/* quote in quote color */
blockquote.bbc_alternate_quote {background-color: #3a3a3a;}

[href="https://www.keyshot.com/forum/index.php?action=reporttm;"] {color: red}

element.style {
    color: white;
}

.bbc_link:link, .bbc_link:visited {
    border-bottom: 1px solid #848484;
}


}

