css:
@-moz-document domain('console.firebase.google.com')
{
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        color: #ddd;
        background-color: #222 !important;
    }

    ng-transclude {
        color: #ddd !important;
        background-color: #222 !important;
    }
    card-action-bar {
        color: #ddd !important;
        background-color: #222 !important;
    }
    mat-list {
        color: #ddd !important;
        background-color: #222 !important;
    }

    f7e-data-panel {
        color: #ddd !important;
        background-color: #222 !important;
    }
   layout-fill {
        font-weight: 300 !important;
        line-height: 1.9 !important;
        color: #fff !important;
    }
   md-single-grid {
        color: #222 !important;
        background-color: #222 !important;
    }
   md-card {
        color: #222 !important;
        background-color: #222 !important;
    }


    a {
        color: #608dff !important;
        text-decoration: none;
    }

    a:hover {
        color: #637ab5 !important;
        text-decoration: underline;
    }

    h1 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        font-size: 36px !important;
        color: #eee !important;
    }

    h2 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 26px !important;
        color: #eee !important;
    }

    h3 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 16px !important;
        color: #ddd !important;
    }

    h4 {
        color: #ccc !important;
    }

    .reflist,
    .refbegin {
        background-color: #282828 !important;
    }

    .thumbcaption {
        color: #888 !important;
    }

    #content h3 {
        font-size: 20px !important;
    }
    database-leaf-value {
        font-weight: 300 !important;
        line-height: 1.9 !important;
        color: #fff !important;
    }

    p {
        font-weight: 300 !important;
        line-height: 1.9 !important;
        color: #fff !important;
    }

}
