css:
@-moz-document domain("ivoox.com") { 
/*Cabecera*/
header > .navbar > .container {
    padding-right: 0;
    background: #333 !important;
}

#dropdown-language {
    padding-top: 12px;
    padding-bottom: 11px;
    padding-right: 7px;
    padding-left: 11px;
    text-align: center;
    border: none;
        border-top-width: medium;
        border-right-width: medium;
        border-top-style: none;
        border-right-style: none;
        border-top-color: inherit;
        border-right-color: inherit;
    border-top: 1px solid #333 !important;
    border-right: 1px solid #333 !important;
    color: #999 !important;
    background: #333 !important;
}

header .navbar-nav > li > a {
    color: #999 !important;
    padding-top: 12px;
    padding-bottom: 11px;
    border-top: 1px solid #FFF !important;
        border-top-color: #333 !important;
}

.navbar > .container {
    border-bottom: solid 1px #333 !important;
}

/*Segunda Cabecera*/
.header-ficha, .header-ficha2 {
    background: #333 !important;
    border-bottom: 2px solid #333 !important;
    margin-bottom: 18px;
}
/*Texto Segunda Cabecera*/
.header-ficha .navbar-nav.main-navbar > li > a {
    font-size: 12px;
    font-weight: 500;
    padding: 5px 5px 13px 5px;
        padding-right: 5px;
        padding-left: 5px;
    color: #d2d2d2;
    position: relative;
    top: 2px;
    border-bottom: 2px solid transparent;
}
.header-ficha .ficha-bottom-menu.menu-border-top {
    border-top: 1px solid #333 !important;
    padding-top: 6px;
}

/*Main*/
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    color: #d2d2d2 !important;
    line-height: 1.42857143;
    background: #333 !important;
}
/*Módulos*/

.modulo-view {
    background: #0c060699 !important;
    border-bottom: 2px solid #0c060699 !important;
}

.modulo-view .header-modulo .wrapper span {
    font-size: 13px;
    font-weight: 600;
    color: #ffffffb3 !important;
}

.modulo-view.modulo-type-programa .title-wrapper a, .modulo-view.modulo-type-canal .title-wrapper a, .modulo-view.modulo-type-episodio .title-wrapper a, .modulo-view.modulo-type-radio .title-wrapper a, .modulo-view.modulo-type-lista .title-wrapper a, .list-title a {
    font-size: 22px !important;
    color: #ffffffb3 !important;
}

.modulo-view .content p {
    font-size: 15px;
    color: #ffffffb3 !important;
}

.modulo-view .content ul.action, .suscripcion-header ul.action {
    background: #0c060699 !important;
}
.modulo-view .content ul.action, .suscripcion-header ul.action {
    padding: 6px 0 0 0;
    list-style: none;
    overflow: hidden;
    text-align: center;
    font-size: 11px;
    font-weight: bold;
    color: #777;
    font-family: Arial;
    margin: 0;
}
.modulo-view .content ul.action {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    border-top: 1px solid #FF8000 !important;
    height: 38px;
}

.modulo-view .content ul.action li {
    width: 33.33%;
    float: left;
    position: relative;
    color: #ffffffb3 !important;
}
.modulo-view .content ul.action li a, .suscripcion-header ul.action li a {
    color: #ffffffb3 !important;
}
/*Ultimas Escuchas*/

.border-box {
    background: #0c060699 !important;
    border-bottom: 2px solid #0c060699 !important;
    padding: 10px 10px 0 10px;
    margin-bottom: 20px;
}

.border-box h3 {
    color: #ffffffb3 !important;
    font-size: 22px;
    margin: 0 -10px 0 -8px;
    border-bottom: 1px solid #EEE;
    padding: 0 10px 15px 8px;
}
/*Mis suscripciones*/
.filtros .menu-wrapper {
    overflow: hidden;
    background: #1B1818 !important;
    border-bottom: #1B1818 !important;
}

.filtros {
    background: #1B1818 !important;
    border-bottom: #1B1818 !important;
    margin-bottom: 20px;
    margin-top: 20px;
}

.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #1B1818 !important;
}
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
    border-color: #1B1818 !important;
}

.table-list a.title {
    color: #d2d2d2 !important;
}

.table-list .date, .table-list .stair {
    font-size: 12px;
    font-weight: bold;
    color: #d2d2d2 !important;
    font-family: Arial;
}
.table.table-list {
    border-bottom: 2px solid #1B1818 !important;
    background-color: #1B1818 !important;
    font-size: 12px;
}

.filtros .full-links li a {
    font-size: 12px;
    font-weight: bold;
    padding: 9px 10px;
    display: block;
    color:#d2d2d2 !important;
}

.filtros > .menu-wrapper > ul.full-links > li > p {
    font-size: 12px;
    font-weight: bold;
    padding: 8px 8px 0;
    display: block;
    color: #d2d2d2 !important;
}

.filtros > .menu-wrapper > ul.full-links > li.active > a {
    color: #FF8000 !important;
    font-weight: 600;
}

/*Texto Mis Listas*/

.modulo-view .content h4 a {
    color: #d2d2d2 !important;
}

/*Texto Radios Favoritas*/
.modulo-view.modulo-type-radio .header-modulo .wrapper strong {
    color: #d2d2d2 !important;
}

/*Reproducción*/
.header-ficha h1 {
    font-size: 25px;
    font-weight: 600;
    color: #d2d2d2 !important;
}

.player-timebar {
    background: #1B1818 url(https://static-1.ivooxcdn.com/img/player-timebar.png) repeat-x 2px 8px  !important;
    height: 45px;
    margin-top: 13px;
    position: relative;
}

.buffer-timebar {
    background: #1B1818 url('https://static-1.ivooxcdn.com/img/player-timebar.png') repeat-x 2px 8px !important;
    height: 100%;
    width: 0;
}

.player-timebar .time, .player-timebar .total-time {
    display: block;
    position: absolute;
    top: 47px;
    font-size: 11px;
    color: #d2d2d2 !important;
    padding-left: 2px;
}
.header-ficha2 {
    padding-top: 25px;
    border-top: 1px solid #333 !important;
}

.episode-list .content p > a {
    font-weight: normal;
    font-size: 12px;
    color: #d2d2d2 !important;
}

/*1a Correción Líneas sueltas*/

.comment-row {
    padding-bottom: 9px;
    border-bottom: 1px solid #1B1818 !important;
}

.border-box.banner-box.big-banner hr {
    border-top-color: #1B1818 !important;
    margin: 0 -10px 10px -10px;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #ffffffb3 !important;
}

/*2a Correción de Etiquetas*/

.rounded-label, .bootstrap-tagsinput .tag {
    border: 1px solid #DDD;
    border-radius: 10px;
    padding: 3px 10px;
    text-align: center;
    color: #333;
    font-size: 12px;
    background: #4E474799 !important;
    font-weight: normal !important;
    white-space: nowrap;
    display: inline-block;
    margin-bottom: 2px;
}

.rounded-label:hover {
    background: #4E474799 !important;
    color: #333 !important;
    text-decoration: none;
}
.form-control:hover, .form-control:focus {
    background-color: #333 !important;
}

.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #333 !important;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: #333 !important;
    border: 1px solid #ddd;
}

/*3a Correción Menús flotantes*/

.modulo-view-default {
    background: #1B1818 !important;
    font-size: 16px;
    border-bottom: 1px solid #1B1818 !important;
}
.modulo-add-options .content ul li:hover {
    background: #FF8000 !important;
}

.modulo-add-options .content ul li {
    border-bottom: 0px solid #EEE !important;
    padding: 12px 20px;
}

.modulo-add-options .content ul li a, .popover-lista .popover-content .buttons > li > a {
    color: #606060;
    font-size: 16px;
    font-weight: normal;
}

.suggestionsBox {
    background-color: #333 !important;
    border-radius: 0px;
    box-shadow: none;
    color: #808080 !important;
    z-index: 1000;
    text-transform: capitalize;
    margin-top: 6px;
    display: none;
    width: 100%;
    position: absolute;
}

.popover-lista .popover-content .buttons {
    padding: 15px 20px;
    background-color: #1B1818 !important;
}

.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    margin-left: -1px;
    line-height: 1.42857143;
    color: #337ab7;
    text-decoration: none;
    background-color: #1B1818 !important;
    border: 1px solid #1B1818 !important;
        border-top-color: rgb(27, 24, 24);
        border-right-color: rgb(27, 24, 24);
        border-bottom-color: rgb(27, 24, 24);
        border-left-color: rgb(27, 24, 24);
}

.filtros .main-links li a.open {
    background: #1B1818 !important;
    color: #FF7F00;
}

.filtros .main-links li a {
    font-weight: 600;
    color: #777 !important;
    display: inline-block;
    padding: 9px 10px 5px 10px;
    background-color: #1B1818 !important;
}

.filtros .active-filters {
    border-top: 1px solid #1B1818 !important;
    overflow: hidden;
    background: #1B1818 !important;
    padding-top: 6px;
    padding-bottom: 10px;
    display: none;
}

.filtros .main-links li, .filtros .list-types li {
    float: left;
    text-align: left;
    background-color: #1B1818 !important;
}

.popover-title {
    border: none;
    font-size: 12px;
    color: #d2d2d2 !important;
    padding: 13px 10px 10px 10px;
    line-height: 12px;
    font-weight: bold;
    background-color: #1B1818 !important;
}

.popover-content {
    font-size: 12px;
    padding: 0 10px 10px 10px;
    color: #d2d2d2 !important;
    background-color: #1B1818 !important;
}

.panel-default > .panel-heading {
    color: #ffffffb3 !important;
    background-color: #1B1818 !important;
    border-color: #1B1818 !important;
}

.panel-body {
    padding: 15px;
    background-color: #1B1818 !important;
}

.dropdown-menu.dropdown-user > li > a {
    padding: 7px 8px 7px 17px;
    background-color: #1B1818 !important;
}

#dropdown-language-ul {
    text-transform: none;
    border-radius: 0px;
    background: #f2f2f2;
    border: 1px solid #fff;
    width: 575px;
    margin-top: 1px;
    background-color: #1B1818 !important;
}

.comment, .contenedor-escribir > form {
    background-color: #333 !important;
    padding: 10px;
    border-radius: 8px;
    margin-top: 5px;
    width: 100%;
}


/*------DIC-18--------*/

textarea#comment-control {
    color: #333 !important;
}
button#dropdown-language,
header > .navbar > .container,
div#main-navbar {
    background-color: #777 !important;
}
a.icon-buscar>span,
ul#main>li>a,
header .navbar-default .navbar-nav>li>a.audio-upload{
color:#000 !important;
}
img.main{
	    border-radius: 50%;
	    box-shadow: 0 0 10px #ff8000;
}
.player-timebar,
.jp-seek-bar.buffer-timebar,
.current-timebar.jp-play-bar {
    background-color: #333 !important;
}
.player-timebar .time, .player-timebar .total-time{
	color:#ff8000 !important
}
a.rounded-label{
border-radius:0 !important;
}
a.rounded-label:hover{
	 background-color:#f9f9f961 !important
}
.header-ficha2 .player-bar .button{
    box-shadow: 0 2px #D96D00;
}
button.close.big.circle {
    z-index: 9999999;
    position: absolute;
    right: 0;
    background: red;
    opacity: 0.7;
    margin-right: -23px;
    margin-top: -22px;
}
.share-audio-player {
    background-color: rgba(51, 51, 51, 0.9);
    border-top: solid rgba(51, 51, 51, 0.9) 4px;
    margin-top: -7px;
}

div.modal-header{
    background-color: rgba(51, 51, 51, 0.9);
    }

div.modal-body{
    background-color: rgba(51, 51, 51, 0.9);
    }

.submenu .nav-tabs li a:focus {
    color: #000 !important;
    background: #777 !important;
    border: none;
}

a.activador{
    color: #000 !important;
    font-weight: bold;
    text-decoration: none;
    background-color: #777 !important;

}
.submenu .nav-tabs li.active a, .submenu .nav-tabs li a:hover, .submenu .nav-tabs li a:focus{

    color: #d2d2d2  !important;
    background: #0000004d !important;
    border-bottom: 0px solid #777!important;

}

.dropdown.mobile-dropdown li a {

    padding: 10px;
    border-top: 1px solid #777 !important;
    border-bottom: 1px solid #777 !important;
    background: #777 !important;
}
.submenu {
    border-bottom: none;
}
.submenu .pills-container .nav-pills {

    margin-top: 0px;
    background: #777 !important;
    border-bottom: 1px solid #000 !important;
}

.submenu .container {
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
    background: #777 !important;
}

.submenu .child-menu-container {

    background: #EEE !important;
    padding-bottom: 0px;
    padding-top: 0px;
}

.submenu .pills-container .nav-pills > li.active > a, .submenu .pills-container .nav-pills > li > a:hover, .submenu .pills-container .nav-pills > li > a:focus {
    background-color: #0000004d !important;
    border-radius: 0;
}

.nav-tabs {
    border-bottom: 1px solid #000!important;
}

.header-ficha h2 a.info, .modulo-view .content h4 .info, .btn.info-popover {
    margin-left: 10px;
}

.header-ficha .background img {
    top: 45px;
}

}
