css:
@-moz-document url-prefix("https://bugzilla.mozilla.org") {
    :root {
        --black: #24292e;
        --blue: #0366d6;
        --green: #2cbe4e;
        --red: #d62935;
        --light-gray: #f6f8fa;
        --defaut-font-size: 14px;
    }

    body {
        background-color: white;
        background-image: none;
    }

    body,
    td,
    th,
    input,
    select,
    option,
    optgroup,
    button,
    .text_input {
        font-family: "Fira Sans", -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, "Twemoji Mozilla", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;
    }


    select[multiple],
    textarea,
    input[type="text"],
    input[type="password"],
    input[type="email"],
    input[type="url"],
    input[type="number"],
    input:not([type]),
    .text_input,
    .yui-ac-input {
        vertical-align: baseline;
    }

    a,
    a:hover {
        color: var(--blue);
    }

    #wrapper {
        min-height: 500px;
    }

    #header {
        background-color: #24292e;
    }

    #header button[title="Mozilla"] img {
        filter: invert(100%);
    }

    #header .icon {
        color: #c8c9ca;
    }

    #header a,
    #header a:visited {
        color: rgba(255, 255, 255, 0.75);
        font-weight: 600;
    }

    #header a:hover {
        color: #fff;
    }

    #header #quicksearch_top {
        color: rgba(255, 255, 255, 0.75);
        background-color: #404448;
    }

    #header .dropdown a {
        font-weight: normal;
        display: block;
        padding: 4px 10px 4px 15px;
        color: inherit
    }

    #header .dropdown li:hover,
    #header .dropdown li:hover * {
        background-color: var(--blue);
        color: #fff;
    }

    .module-header,
    .module-content,
    .change-set {
        box-shadow: none;
    }
    .change-set .comment,
    .change-set .change {
        padding-bottom: 0;
    }
    .change-set .activity .change {
        min-height: auto;
        background: inherit;
    }
    .change-set .comment,
    .change-set .change {
        padding-bottom: 0;
    }

    .change-head {
        background-color: var(--light-gray);
    }

    #field-status_summary {
        color: #666;
    }
    #field-status_summary b {
        color: white;
        background-color: var(--green);
        display: inline-block;
        padding: 2px 1em;
        border-radius: 2px;
    }
    #field-status_summary b.bz_status_RESOLVED,
    #field-status_summary b.bz_status_VERIFIED {
        background-color: var(--red);
    }
    #field-status_summary b.bz_status_ASSIGNED {
        background-color: var(--blue);
    }
    #field-status_summary b.bz_status_UNCONFIRMED {
        background-color: #5319e7;
    }
    .change-gravatar {
        display: inline-block;
        margin-left: -64px;
        padding-left: 0 !important;
        position: absolute;
    }
    .gravatar {
        width: 44px;
        height: 44px;
        border-radius: 3px;
    }
    .change-head {
        padding-left: 10px;
    }
    .change-set {
        position: relative;
        margin-top: 1.5em;
        padding: 1px 0;
        border: 1px solid #d1d5da;
        border-radius: 3px;
        color: #6a737d;
        background-color: var(--light-gray);
        min-height: 40px;
    }
    .change-set::before {
        position: absolute;
        top: 11px;
        right: 100%;
        left: -16px;
        display: block;
        width: 0;
        height: 0;
        pointer-events: none;
        content: " ";
        border-color: transparent;
        border-style: solid solid outset;
        border-width: 8px;
        border-right-color: #e6ebf1;
    }
    .comment-tag {
        border: 1px solid #d1d5da;
        padding: 2px 6px 2px 4px;
        margin-right: 2px;
        border-radius: 4px;
        background-color: #F1F8FF;
        color: #6a737d;
        display: inline-block;
    }
    pre.comment-text {
        font-size: var(--defaut-font-size);
        font-family: "Fira Sans", "Open Sans", "Helvetica Neue", Arial, Helvetica, "Twemoji Mozilla", sans-serif;
        padding: 1em;
        padding-right: 20%;
        line-height: 1.5em;
        color: #24292e;
        margin: 0;
    }
    pre.comment-text span.quote,
    pre.comment-text span.quote_wrapped {
        background-color: white !important;
        color: #6a737d !important;
        position: relative;
        margin-top: 0.5em;
        margin-bottom: 0.5em;
        margin-left: 1em;
        width: calc(100% - 2em) !important;
        padding-left: 0.7em !important;
    }
    pre.comment-text span.quote::before {
        content: "";
        width: 1em;
        background-color: white;
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        height: 100%;
        border-right: 0.25em solid #dfe2e5;
    }
    .discussion-timeline::before {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 79px;
        z-index: -1;
        display: block;
        width: 2px;
        content: "";
        background-color: #e6ebf1;
    }
    #field-value-short_desc {
        font-size: 24px;
        font-weight: normal;
    }
    #field-status_summary {
        border-top: none;
    }
    #summary-container {
        display: flex;
        flex-direction: column-reverse;
    }
    .module-content {
        display: flex;
        justify-content: space-between;
    }
    main#bugzilla-body button,
    main#bugzilla-body button.minor,
    main#bugzilla-body .bz_query_buttons input,
    main#bugzilla-body .bz_query_remember input {
        position: relative;
        display: inline-block;
        white-space: nowrap;
        vertical-align: middle;
        cursor: pointer;
        background-size: 110% 110%;
        border-radius: 0.25em;
        -moz-appearance: none;
        border: 1px solid rgba(27, 31, 35, 0.2);
        color: #24292e;
        text-shadow: none;
        box-shadow: none;
        background-color: var(--light-gray);
        background-image: none;
    }
    main#bugzilla-body button:hover,
    main#bugzilla-body button.minor:hover {
        box-shadow: none;
        transition: none;
        text-decoration: none;
        background-repeat: repeat-x;
        background-color: #e6ebf1;
        background-image: linear-gradient(-180deg, #f0f3f6 0%, #e6ebf1 90%);
        background-position: 0 -0.5em;
        border-color: rgba(27, 31, 35, 0.35);
    }
    .vcard a,
    .comment a,
    .change a {
        color: rgb(88, 96, 105);
    }
    .comment a:hover,
    .change a:hover {
        text-decoration: underline;
    }
    .activity {
        background: #fbfbfb;
    }
    /* remove useless links */
    #user-guide,
    #bugzilla-etiquette,
    div#message {
        display: none;
    }
    .module-header {
        background-color: var(--light-gray);
        border-radius: 2px;
    }

    /* start - save buttons */
    #new-comment-actions #bottom-save-btn,
    button#commit-btn.major,
    #top-save-btn {
        color: #fff;
        background-color: var(--green);
        background-image: none;
    }

    #new-comment-actions #bottom-save-btn:hover,
    button#commit-btn.major:hover,
    #top-save-btn:hover {
        color: #fff;
        background-color: #34d058;
        background-image: none;
    }
    /* end */
    #new-comment-actions {
        display: flex;
        flex-direction: row-reverse;
        margin-bottom: 3em;
        margin-top: -1em;
        justify-content: space-between;
        padding: 0 16px;
    }


    #new-comment-actions #bottom-save-btn {
        margin-top: -2em;
    }

    button.minor {
        margin-bottom: 0;
    }
    a#this-bug {
        font-size: 24px;
        font-weight: normal;
        color: #a3aab1;
    }
    a#this-bug:visited {
        font-size: 24px;
        color: #a3aab1;
    }
    a#this-bug:hover {
        text-decoration: underline;
        color: #a3aab1;
    }
    .module .fields-lhs,
    .module .fields-rhs {
        width: auto;
        min-width: auto;
        display: inline-table;
    }
    .comment-actions {
        margin-top: 1em;
        text-align: right;
    }

    .comment-actions > [role="group"] {
        display:initial !important;
    }

    table td.comment-actions button {
        color: #586069 !important;
        background-image: none !important;
        background-color: transparent !important;
        padding: 2px 5px !important;
        margin: 8px 0 0 10px !important;
        border: 1px solid rgba(27, 31, 35, 0.1) !important;
        border-radius: 3px !important;
    }
    #commit-btn,
    #cancel-btn {
        margin-bottom: 1em;
    }
    td.flag-value select {
        min-width: 3em;
    }
    #field-status_whiteboard {
        float: left;
    }
    #field-status_whiteboard div.name {
        display: table-cell !important;
    }
    #module-user-story-content textarea {
        width: 100%;
    }
    /* no flex for extensions inserting data in Details section */
    #module-details-content.module-content {
        display: block;
    }
    #field-cf_crash_signature iframe {
        float: none !important;
    }
    .change-author .user-role {
        left: 0;
        top: 46px;
        position: absolute;
        width: 64px;
        margin-left: -58px;
        font-size: smaller;
    }
    .change-author {
        height: 2em;
        padding: 10px 0 !important;
        display:block;
    }
    /* attachments */
    #attachments tr {
        color: #6a737d;
    }
    #attachments .attach-desc-td {
        background-color: transparent !important;
        background-image: none !important;
        padding-left: 10px;
    }
    #attachments .attach-desc a {
        color: #24292e;
        font-size: var(--defaut-font-size);
        font-weight: bold;
    }
    #attachments .attach-desc a:hover {
        color: #24292e;
        text-decoration: none;
    }
    #attachments .attach-patch .attach-desc-td {
        box-shadow: 2px 0 0 #E36209 inset;
    }
    #attachments .attach-desc-td {
        box-shadow: 2px 0 0 var(--blue) inset;
    }
    #attachments .attach-desc-td .attach-desc {
        margin-bottom: 1em;
    }
    #attachments td {
        border-top: 1px solid #e1e4e8;
        padding-top: 0.5em;
        padding-bottom: 0.5em;
    }
    #attachments a {
        font-weight: normal;
        color: rgb(88, 96, 105);
    }
    #attachments a:hover {
        color: rgb(88, 96, 105);
        text-decoration: underline;
    }
    #attachments-actions {
        font-size: var(--defaut-font-size);
    }
    #attachments .flag-name-status {
        font-weight: normal;
        color: green;
    }
    #module-attachments-content {
        display: block;
    }
    #attachments .attach-info {
        margin-top: 0.5em;
    }
    #attachments-add-link::before {
        content: '\E2BC';
        font-family: 'Material Icons';
        vertical-align: middle;
        color: #404040;
        margin-right: 5px;
        font-size: 26px;
    }
    /* emphasize reporter name */
    #field-value-reporter .vcard {
        text-align: center;
    }
    #field-value-reporter .vcard a {
        color: var(--blue);
    }
    /* less intrusive drop-down arrow */
    .vcard a .fn::after,
    .vcard a .ln::after {
        content: ' \25BF';
        opacity: 0.5 !important;
    }
    /* make email requested in NeedInfo stand out */
    #module-people-content .field .name,
    #module-people-content .field .name + .container .fn{
        /* top of page*/
        color: orange;
    }

    #needinfo_container em {
        /* bottom of page*/
        color: orange;
        font-style: normal;
    }
    /* bug links in tracking module */
    #module-tracking-content .fields-rhs div.value {
        font-size: 0;
    }
    .bz_bug_link {
        display: inline-block;
        padding: 3px 4px;
        font-size: 13px;
        line-height: 1;
        border-radius: 2px;
        margin: 1px;
        min-width: 4.3em;
        text-align: center;
    }
    .bz_bug_link.bz_status_NEW,
    .bz_bug_link.bz_status_REOPENED,
    .bz_bug_link.bz_status_ASSIGNED {
        color: #ffffff;
        background-color: var(--blue);
    }
    .bz_bug_link.bz_status_RESOLVED,
    .bz_bug_link.bz_status_VERIFIED {
        color: white;
        background-color: var(--red);
        text-decoration: none !important;
    }
    .bz_bug_link.bz_status_UNCONFIRMED {
        color: white;
        background-color: #5319e7;
    }

    .module .field,
    .module .field .name {
        vertical-align: baseline;
    }
    table.tracking-flags {
        margin-left: -8em;
        border-collapse: collapse;
        width: 400px;
    }
    #module-firefox-tracking-flags-content .name {
        vertical-align: top;
    }
    table.tracking-flags td,
    .tracking-flags th {
        text-align: center;
        border: 1px solid lightgray;
    }
    table.tracking-flags tr:nth-child(2n) {
        background-color: var(--light-gray);
        color: #666;
    }

    /* comment textarea */
    #add-comment {
        background-color: var(--light-gray);
        border: 1px solid #d1d5da;
        border-radius: 3px;
    }

    #add-comment::before {
        content: '\E254';
        font-family: 'Material Icons';
        margin-left: -52px;
        font-size: 44px;
        position: absolute;
        transform: scaleX(-1);
    }

    #comment-tabs {
        margin-top: 5px;
        padding: 0 8px;
        margin-bottom: -1px;
    }

    #comment-tabs li {
        border: 1px solid transparent;
        padding: 10px
    }

    #comment-tabs li[aria-selected="true"] {
        border: 1px solid #d1d5da;
        border-bottom: 1px solid white;
    }

    #comment-edit-tabpanel {
        padding: 8px;
        background-color: white;
        border-top: 1px solid #d1d5da;
    }

    pre#comment-preview {
        border-top: 1px solid #d1d5da;
    }

    textarea#comment {
        display: block;
        width: 100%;
        padding: 8px;
        resize: vertical;
        background-color: #fafbfc;
        border-bottom-right-radius: 0;
        border-bottom-left-radius: 0;
        box-shadow: inset 0 1px 2px rgba(27, 31, 35, 0.075);
    }

    textarea#comment:focus {
        border-color: #2188ff;
        box-shadow: inset 0 1px 2px rgba(27, 31, 35, 0.075), 0 0 0 0.2em rgba(3, 102, 214, 0.3);
    }

    #bottom-right-actions {
        text-align: right;
        float: none;
    }

    #after-comment-commit-button {

        background-color: white;
        padding: 0 16px;
        margin: 0;
        padding-bottom: 4em;
    }

    #resolve-as,
    #bottom-status {
        margin-top: -2em;
    }

    #resolve-as button {
        margin-left: 8px;
    }

    .change-set,
    #new-comment-actions,
    #add-comment {
        margin-left: 64px
    }


    /* fix a bug in which Home and End keys don't work in Firefox  */
    #bugzilla-body {
        overflow-y: auto;
    }

    /* anchor link */
    .change-set:target .change-head,
    .change-set:target .activity {
        background-color: #f1f8ff;
    }

    .change-set:target {
        border: 2px solid #2188ff;
        outline: none;
    }

    .change-set:target::before {
        border-right-color: #2188ff;
    }

    #header-requests-menu-button .icon {
        background: inherit;
    }

    #header-requests-menu-button .icon::before {
        font-size: 24px;
    }

    #header-requests-menu-button .badge {
        font-size: 12px;
        background: linear-gradient(#54a3ff, #006eed);
    }

    /* Mobile  adjustments */
    @media screen and (max-width: 640px) {

        #header {
            width: 600px;
        }

        #header button[title="Mozilla"] img {
            display: none;
        }


        #header #quicksearch_top {
            width: initial;
        }

        #header .searchbox-outer {
            width: 220px;
        }

        .change-gravatar {
            margin-left: 0;
            position: static;
        }
        .gravatar {
            width: 32px;
            height: 32px;
        }

        .change-gravatar .vcard {
            width: 50px;
        }
        .change-author {
            height: auto;
        }
        .change-author .user-role {
            left: auto;
            top: auto;
            position: static;
            width: auto;
            margin-left: 0;
        }

        .change-set::before,
        #add-comment::before {
            content: none;
        }

        .change-set,
        #new-comment-actions,
        #add-comment {
            margin-left: 0;
        }
    }

    /* #start - Style emails recipient list for changes in a readible way */
    #main-inner dl dl dd {
        color: transparent;
        display: flex;
        flex-wrap: wrap;
    }

    #main-inner dl dl dd code {
        color: #666;
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, "Twemoji Mozilla", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
        min-width: 230px;
        font-size: inherit;
        line-height: 1.5em;
    }
    /* #end */
    /* #start - displayed only in edit mode and breaks flex because of https://bugzilla.mozilla.org/show_bug.cgi?id=1454330 */
    #field-status-edit {
        display: block;
    }
    /* #end */
    /* #start - File bug in header green */
    #header .link-file span {
        color: var(--green);
    }
    #header .link-file span:hover {
        color: #34d058;
    }
    /* #end */
    /* start - style new comments since last visit delimiter */
    .new-changes-separator {
        border-top: 2px solid var(--green);
        -moz-user-select: none;
        -webkit-user-select: none;
        user-select: none;
    }

    .new-changes-separator span {
        background-color: var(--green);
        color: white;
        border: none;
    }
    /* #end */
    /* start - style unassigned bugs differently */
    span#field-value-assigned_to i {
        color: var(--red);
        font-weight: bold;
        font-style: normal;
    }

    /* start - Bug list */
    table.bz_buglist {
        background-color: var(--light-gray);
        border: 1px solid #e1e4e8;
        border-radius: 3px 3px 0 0;
        border-collapse: collapse;
        margin: 1em 0;
    }

    table.bz_buglist tr {
        line-height: 2.4em;
        border: 1px solid #e1e4e8;
    }

    table.bz_buglist tr:hover {
        background-color: var(--light-gray) !important;
    }

    table.bz_buglist tr.bz_row_odd,
    table.bz_buglist tr.bz_row_even {
        background-color: white;
        color: var(--black);
    }

    table.bz_buglist .bz_critical {
        color: var(--red) !important;
    }

    table.bz_buglist thead th.sorted_-1,
    table.bz_buglist thead th.sorted_0,
    table.bz_buglist thead th.sorted_1,
    table.bz_buglist thead th.sorted_2,
    table.bz_buglist thead th.sorted_3,
    table.bz_buglist thead th.sorted_4,
    table.bz_buglist thead th.sorted_5,
    table.bz_buglist thead th.sorted_6,
    table.bz_buglist thead th.sorted_7,
    table.bz_buglist thead th.sorted_8,
    table.bz_buglist thead th.sorted_9,
    table.bz_buglist thead th.sorted_10,
    table.bz_buglist thead th.sorted_11,
    table.bz_buglist thead th.sorted_12 {
        background-color: var(--light-gray);
    }

    table.bz_buglist thead th a {
        color: var(--black);
        font-weight: normal;
        font-size: var(--defaut-font-size);
    }

    table.bz_buglist thead th {
        border-bottom: 1px solid #e1e4e8;
    }

    table.bz_buglist tr.bz_secure td.first-child {
        background-image: none;
        white-space: nowrap;
    }
    table.bz_buglist tr.bz_secure td.first-child a {
        background-image: none;
        white-space: nowrap;
    }

    td.bz_id_column a {
        display: inline-block;
        line-height: 1em;
        padding: 4px 4px;
        border-radius: 2px;
        margin: 1px;
        min-width: 4.3em;
        text-align: center;
        color: white;
        background-color: var(--blue);
        letter-spacing: 1.5px;
    }

    table.bz_buglist tr.bz_secure td.first-child a::before {
        content: '\01F512';
        margin-left: -1.6em;
        display:block;
        float: left;
    }

    .bz_short_desc_column a {
        display: block;
    }

    .bz_result_count {
        display: block;
        float: right;
        font-size: 1.3em;
        color: var(--blue);
    }

    ul.search_description {
        display: flex;
        background-color: var(--light-gray);
        border: 1px solid #e1e4e8;
        border-radius: 3px 3px 0 0;
        padding: 1em;
    }

    ul.search_description li  {
        flex-grow: 1;
    }

    ul.search_description li strong {
        display: block;
    }

    hr {
        visibility: hidden;
    }

    .bz_query_head {
        letter-spacing: 1px;
    }

    .bz_query_head h1 {
        margin-bottom: 0;
    }

    .bz_query_timestamp {
        font-size: large;
        font-weight: normal;
    }

    /* #end */
    /* start - Align Private comment checkbox correctly */
    #add-comment-private {
        line-height: 3em;
        padding-right: 1em;
    }
    /* #end */
    /* start - User story */
    #user-story {
        font-family: "Fira Sans", -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, "Twemoji Mozilla", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;
        font-size: var(--defaut-font-size);
        line-height: 1.5em;
        padding: 1em;
        color: var(--black);
    }
    /* #end */

    #mode-container .button-row {
        border-left-color: transparent;
    }


    .bug-type-label.icon::before {
        line-height: initial;
    }

    .bug-type-label.iconic-text {
        text-transform: capitalize;
        font-weight: normal;
    }

    #regressed_by-help-link {
        font-weight: bold;
    }

}
