css:
@-moz-document regexp("http://www.neopets.com/(?!(p|u)l_preview.phtml*|~.*).*"), domain("ncmall.neopets.com")
{
/**************************************************************
***************************************************************
BACKGROUND
***************************************************************
***************************************************************/
	body {
    background: url("https://i.imgur.com/ksTApL1.jpg");
    background-repeat: no-repeat !important;
    background-size: auto !important;
    background-color: #AAE8CF !important;
}
/**************************************************************
***************************************************************
TOP BANNER
***************************************************************
***************************************************************/
  #ban
  {
    background: url("http://i.imgur.com/H3C6ajK.jpg") no-repeat;
    background-size: 100.1%;
    background-position: 0px;
  }
                                          
/*Hides top banner ad*/
  #ad-table
  {
    padding: 43px !important;
    border-collapse: separate !important;
  }

  #ad-table .slug img
  {
    display: none !important;
  }

  #pushdown_banner .ad_wrapper_fixed, #pushdown_banner_btf
  {
    display: none;
  }
/***************************************************************
****************************************************************
NAVIGATION
****************************************************************
****************************************************************/

  #header
  {
    background: url("https://i.imgur.com/mvxzBk4.png") no-repeat;
    background-size: 100.1%;
    height: 76px;
    border-bottom: 2px solid #305880;
  }

  #navigation
  {
    padding-left: 23px;
    position: relative;
    top: -2px;
  }

/*New navigation images: Animal Crossing Font!*/
  #template_nav > li a img
  {
    display: none;
  }

  li.nav_image > a
  {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }

  li.nav_image:nth-child(1)
  {
    margin-left: 6px;
  }

  li.nav_image
  {
    margin-right: -1px;
  }

  li.nav_image:nth-child(1) > a
  {
    background: url(https://i.imgur.com/cGNbUjt.png) no-repeat;
    width: 115px;
    height: 38px;
  }

  li.nav_image:nth-child(2) > a
  {
    background: url(https://i.imgur.com/nJq6qEm.png) no-repeat;
    width: 90px;
    height: 38px;
  }

  li.nav_image:nth-child(3) > a
  {
    background: url(https://i.imgur.com/qvvCzI2.png) no-repeat;
    width: 65px;
    height: 38px;
  }

  li.nav_image:nth-child(4) > a
  {
    background: url(https://i.imgur.com/KOOoRPG.png) no-repeat;
    width: 74px;
    height: 38px;
  }

  li.nav_image:nth-child(5) > a
  {
    background: url(https://i.imgur.com/7vYQQu0.png) no-repeat;
    width: 57px;
    height: 38px;
  }

  li.nav_image:nth-child(6) > a
  {
    background: url(https://i.imgur.com/Gz8tmsv.png) no-repeat;
    width: 97px;
    height: 38px;
  }

  li.nav_image:nth-child(7) > a
  {
    background: url(https://i.imgur.com/E3OVqIu.png) no-repeat;
    width: 61px;
    height: 38px;
  }

  li.nav_image:nth-child(9) > a
  {
    background: url(https://i.imgur.com/EKVlQuM.png) no-repeat;
    width: 70px;
    height: 38px;
  }

  li.nav_image:nth-child(11) > a
  {
    background: url(https://i.imgur.com/Fgg8Yyk.png) no-repeat;
    width: 70px;
    height: 38px;
  }

  ul.dropdown
  {
    border: 1px solid #FDFDE8;
    background-color: #FDFDE8;
  }

  ul.dropdown a:hover
  {
    color: #61BA46 !important;
  }

  #header .eventIcon
  {
    color: #FFF !important;
  }
/*Time*/
  #nst
  {
    color: #2a4237; 
    position: relative;
    left: -10px;
    top: -1px;
    width: 96px;
  }

  .user
  {
    color: #2a4237;

    position: relative;
    top: 8px;
  }

  .user a
  {
    color: #fff !important;
    text-shadow: 2px 2px #75CBA8;
  }

/***************************************************
****************************************************
FOOTER
****************************************************
****************************************************/
  #footer
  {
    background-image: url("https://i.imgur.com/ACWCQd9.png");
    background-size: 112%;
    top: -2px;
    border-top: 2px solid #305880 !important;
  }

/*Camping Villagers footer image!*/
  #footer:before
  {
    width: 308px;
    content: "";
    background: url("https://i.imgur.com/2hpQ8XJ.png") no-repeat;
    background-size: 100%;
    position: absolute;
    left: 700px;
    top: -70px;
    height: 228px;
  }

  .footerNifty
  {
    display: none;
  }

/****************************************************
*****************************************************
SIDEBAR
*****************************************************
*****************************************************/
  .sidebarHeader
  {
    background-color: #79B439;
    color: #FFFFFF !important;
  }

/*Rounded sidebar bottom corners*/
  .sidebarTable > tbody > tr:last-child > td:last-child
  {
    border-radius: 0px 0px 6px 6px;
  }
/*Only add border to sides of box, not header*/
  .sidebarTable > tbody > tr > td
  {
    border: 2px solid #c9c9c9;
    border-top: 0px;
  }

/*****************************************************
******************************************************
CONTENT
******************************************************
******************************************************/
  .content a
  {
    color: #EA676A;
  }

  .content a:hover
  {
    color: #BC101C !important;
  }

  .blistSmall a
  {
    color: #F8B22D !important;
  }

  .blistSmall a:hover
  {
    color: #FDD10A !important;
  }

/*Rounded top and bottom corners*/
  #content table, .contentModuleTable
  {
    border-radius: 8px;
    overflow: hidden;
  }

  .contentModuleHeaderAlt
  {
    background-color: #47980F;
    color: #c4e567 !important;
  }

  .contentModuleContent, .contentModuleBody
  {
    border: 2px solid #c9c9c9;
    border-top: 0px;
    border-radius: 0px 0px 6px 6px;
  }

/*Getting rid of unnecessary borders*/
  .sidebarModule, .sidebarTable, .contentModuleTable, .contentModule
  {
    border: none;
    overflow: hidden;
  }

  #content .contentModuleHeader  #content .contentModuleHeaderAlt, .sidebarHeader
  {
    border-color: transparent !important;
  }

/*News Flash titles*/
  .marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited
  {
    background-color: #79B43B;
    color: #e8f6bd;
  }

  .marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited
  {
    background-color: #47980F;
    color: #c4e567;
  }

  .moreLink, .moreLinkAlt
  {
    border-radius: 4.5px;
  }

  .moreLinkBottom
  {
    border-top: 0px !important;
    padding-top: 0px !important;
    padding-bottom: 8px !important;
  }

  .newItems
  {
    border: 2px solid #FCF0F1 !important;
    background-color: #FCF0F1;
  }

/***Exceptions***/

/*Bottom left new item box*/
  .contentModule > tbody > tr > td > table > tbody > tr:nth-child(4) td.newItems:nth-child(1)
  {
    border-radius: 0px 0px 0px 6px !important;
  }

/*Bottom right new item box*/
  .contentModule > tbody > tr > td > table > tbody > tr:nth-child(4) td.newItems:nth-child(2)
  {
    border-radius: 0px 0px 6px 0px !important;
  }

/*News Flash titles*/
  .content > table:nth-child(1) > tbody > tr > td:nth-child(1) > div > table > tbody > tr:nth-child(2) > td > div:nth-child(2) > table > tbody > tr > td:nth-child(2) > table
  {
    border-radius: 0px !important;
  }
}
