css:
@-moz-document domain("paper.dropbox.com") {


/***********************************************
 * Background - Color
 ***********************************************/

/* body & header & toolbar & table & bottom-right button : background color */
body,
#main-header-container,
.main-header-right,
.insert-rich-object-toolbar-wrapper .insert-toolbar-overlay,
.ace-table,
.hp-versions-button,
.dmc-button-secondary,
.insert-toolbar-preview-image,
.insert-button {
	background-color: #1a1a1a;
}

/* input preview & code block :background color */
.insert-toolbar-preview-image .insert-toolbar-preview-image-inner,
.insert-toolbar-preview-image:hover .insert-toolbar-preview-image-inner ,
.ace-line .insert-toolbar-preview,
.ace-editor .line-list-type-code,
code.listtype-code .insert-toolbar-preview-input {
	background-color: #131313;	
}

/* Task List (Hover) : background olor & font color*/
@media (min-width: 600px){
.ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.focused-line, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.focused-line, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.ace-task-line-highlighted, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.ace-task-line-highlighted {
		background-color:  #404040!important;
    	        color: #ffffff!important;
	}
}


/* main content & left sidebar : background color */
.ace-editor ,
.hp-toc .hp-toc-entries {
     background-color: rgba(0,0,0,0);   
}

/* main content > checkbox (onfocus & onhover): font color*/
@media (min-width: 600px){
.ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.focused-line, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.focused-line, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.ace-task-line-highlighted, .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.ace-task-line-highlighted {
    background-color: #eff3f5;
    color: rgba(0,0,0,0.8);
	}
}


/* header : baseline */
#main-header-container {
	box-shadow: 0 1px rgba(255,255,255,0.5);
}


/***********************************************
 * Font - Family & Color
 ***********************************************/

/* main content : font family */
.ace-editor, 
.ace-feature-bigtitle>.ace-editor>div:first-child {
    font-family:  arial, sans-serif;
}

/* main content & code font: font color */
body,
.ace-editor,
.hp-toc .hp-toc-entry-link,
.hp-versions-button,
.hp-header-title,
.dmc-button-secondary,
.ace-editor code.listtype-code,
.insert-button {
	color: #eff3f5;
}

/* header > folder name of this paper : font color */
.hp-pad-folder a, 
.hp-pad-folder a:active, 
.hp-pad-folder a:link, 
.hp-pad-folder a:visited {
    color: rgb(255, 255, 255);
}

/* left sidebar > active link : font color */
.hp-toc .hp-toc-entry-current .hp-toc-entry-link,
.hp-toc .hp-toc-entry:hover .hp-toc-entry-link {
    color: rgb(255, 255, 255);
}

/* Comment font hightlight : font color */
body.show-comments span.attrcomment, 
body.underline-comments span.attrcomment{
	border-bottom-color: rgb(100, 100, 100);
    background-color: rgb(55, 55, 55)!important;
}

/* icon color */
.dmc-button .svg-icon {
    fill: #4da6ff;
}

/***********************************************
 * Others
 ***********************************************/

/* hide the scroll-bar of left sidebar */
.hp-toc::-webkit-scrollbar { 
    display: none; 
}
}
