css:
@-moz-document regexp("((?=.*?\\/0\\/Nui\\/).*)"), regexp("((?=.*?\\/0\\/Designers\\/).*)") {
.ace-crimson-editor .ace_gutter {
  background: #2F3129 !important;
  color: #8F908A !important;
}

.ace-crimson-editor .ace_print-margin {
  width: 1px !important;
  background: #555651 !important;
}

.ace-crimson-editor {
  background-color: #272822 !important;
  color: #F8F8F2 !important;
}

.ace-crimson-editor .ace_cursor {
  color: #F8F8F0 !important;
}

.ace-crimson-editor .ace_marker-layer .ace_selection {
  background: #49483E !important;
}

.ace-crimson-editor.ace_multiselect .ace_selection.ace_start {
  box-shadow: 0 0 3px 0px #272822 !important;
}

.ace-crimson-editor .ace_marker-layer .ace_step {
  background: rgb(102, 82, 0) !important;
}

.ace-crimson-editor .ace_marker-layer .ace_bracket {
  margin: -1px 0 0 -1px !important;
  border: 1px solid #49483E !important;
}

.ace-crimson-editor .ace_marker-layer .ace_active-line {
  background: #202020 !important;
}

.ace-crimson-editor .ace_gutter-active-line {
  background-color: #272727 !important;
}

.ace-crimson-editor .ace_marker-layer .ace_selected-word {
 /* border: 1px solid #49483E !important;*/
	background-color: transparent !important;
	border: 1px solid #c4c4c4 !important;
    color: #F92672 !important;
}

.ace-crimson-editor .ace_invisible {
  color: #52524d !important;
}

.ace-crimson-editor .ace_entity.ace_name.ace_tag,
.ace-crimson-editor .ace_keyword,
.ace-crimson-editor .ace_meta.ace_tag,
.ace-crimson-editor .ace_storage {
  color: #F92672 !important;
}

.ace-crimson-editor .ace_punctuation,
.ace-crimson-editor .ace_punctuation.ace_tag {
  color: #fff !important;
}

.ace-crimson-editor .ace_constant.ace_character,
.ace-crimson-editor .ace_constant.ace_language,
.ace-crimson-editor .ace_constant.ace_numeric,
.ace-crimson-editor .ace_constant.ace_other {
  color: #AE81FF !important;
}

.ace-crimson-editor .ace_invalid {
  color: #F8F8F0 !important;
  background-color: #F92672 !important;
}

.ace-crimson-editor .ace_invalid.ace_deprecated {
  color: #F8F8F0 !important;
  background-color: #AE81FF !important;
}

.ace-crimson-editor .ace_support.ace_constant,
.ace-crimson-editor .ace_support.ace_function {
  color: #66D9EF !important;
}

.ace-crimson-editor .ace_fold {
  background-color: #A6E22E !important;
  border-color: #F8F8F2 !important;
}

.ace-crimson-editor .ace_storage.ace_type,
.ace-crimson-editor .ace_support.ace_class,
.ace-crimson-editor .ace_support.ace_type {
  font-style: italic !important;
  color: #66D9EF !important;
}

.ace-crimson-editor .ace_entity.ace_name.ace_function,
.ace-crimson-editor .ace_entity.ace_other,
.ace-crimson-editor .ace_entity.ace_other.ace_attribute-name,
.ace-crimson-editor .ace_variable {
  color: #A6E22E !important;
}

.ace-crimson-editor .ace_variable.ace_parameter {
  font-style: italic !important;
  color: #FD971F !important;
}

.ace-crimson-editor .ace_string {
  color: #E6DB74 !important;
}

.ace-crimson-editor .ace_comment {
  color: #75715E !important;
}

.ace-crimson-editor .ace_indent-guide {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWPQ0FD0ZXBzd/wPAAjVAoxeSgNeAAAAAElFTkSuQmCC) right repeat-y !important;
}

.ace_content{
  color: #ffffff !important;
}

.ace-crimson-editor .ace_identifier {
   color: #ffffff !important;
}

