css:
@-moz-document domain("www.amazing-discoveries.org") {
  /* HOMEPAGE */
  body {
    color: #b8b8b8;
  }
  body.custom-background {
    background-color: #282828;
  }
  h1, h2, h3, h4, h5, h6 {
    color: #e8e8e8 !important;
  }
  strong {
    color: #e8e8e8;
  }
  .clr>a>img {
    mix-blend-mode: multiply;
  }
  #header-wrap {
    background: #282828;
    border-bottom: 0px;
  }
  #homepage-features a {
    color: #a1b56c;
  }
  #site-navigation .dropdown-menu a {
    color: #a1b56c;
  }
  /* ARTIKEL */
  .feature-entry-title {
    color: #e8e8e8;
  }
  .page-header-title {
    color: #e8e8e8;
  }
  .loop-entry-title>a {
    color: #e8e8e8;
  }
  .heading {
    color: #e8e8e8;
  }
  .library-entry-title>a {
    color: #e8e8e8;
  }
  .entry-title>a {
    color: #b8b8b8;
  }
  .meta-category {
    color: #b8b8b8;
  }
  .comment-meta .fn {
    color: #e8e8e8;
  }
  /* WIDGETS */
  .widget-title {
    color: #e8e8e8;
  }
  .bx-wrapper .bx-pager .bx-default-pager a {
    background: #585858;
  }
  .bx-wrapper .bx-pager .bx-default-pager a:hover, .bx-wrapper .bx-pager .bx-default-pager .active {
    background: #080808;
  }
  .smooth_slider .smooth_nav .smooth_slider_nnav {
    border: 1px solid #585858;
  }
  .bx-prev, .bx-next {
    filter: invert(100%);
  }
  .smooth_slideri>h2>a {
    color: #e8e8e8 !important;
  }
  .bx-wrapper .bx-pager.bx-default-pager a:hover, .bx-wrapper .bx-pager.bx-default-pager a.active {
    background: #fff;
  }
  .smooth_slideri>span {
    color: #b8b8b8 !important;
  }
  /* AKTUELLE PRODUKTE */
  #wrap[style] {
    background-color: #282828 !important;
  }
  .submenu {
    background-color: #a1b56c !important;
    color: #282828;
    height: 0px !important;
  }
  td.submenu {
    color: black !important;
  }
  .submenu2 {
    background-color: #383838 !important;
    color: #b8b8b8 !important;
  }
  #content>table>tbody>tr>td:not(.main) {
    background-color: #282828 !important;
  }
  td.shopcontrols {
    background-color: #282828;
  }
  #warenkorb_text {
    color: #b8b8b8;
  }
  .shopcontrols>label {
    color: #b8b8b8;
  }
  .infoframe>tbody>tr>td {
    color: #b8b8b8 !important;
  }
  /* PRODUCT VIEW */
  #beschreibungstext_innen {
    color: #b8b8b8;
  }
  .main {
    color: #b8b8b8;
  }
  input[type="text"] {
    color: #b8b8b8 !important;
  }
  .listitem, .listitem2 {
    background-color: #282828 !important;
    color: #b8b8b8 !important;
  }
  .listitem>td, .listitem2>td {
    background-color: #282828 !important;
  }
  .item_show_h1 {
    color: #b8b8b8;
    background-color: #383838;
  }
  .item_show_h2 {
    color: #b8b8b8;
    background-color: #383838;
  }
  table.infoframe tr:first-child {
    background-color: #383838 !important;
  }
  .listheader {
    background-color: #383838;
  }
  .wp-caption-text {
    color: #999999 !important;
  }
}
