css:
@-moz-document url-prefix(http://teknos.esemtia.net/moodle) {
body { background-color: #282828; color: #FFF; }
a { color: /*[[color]]*/ !important; }
.section{ border-radius: 10px; background-color: #111 !important; }
.breadcrumb { border-radius: 10px; background-color: #111; }
.breadcrumb>li { text-shadow: 0 0 0 #000; }

.block .header h2 { background-color: #111; border-bottom: 1px solid #282828; border-top: 0px solid #282828; border-radius: 10px; color: #FFF; }
.block { border-radius-top-left: 12px; border-radius-top-right: 12px; border-radius-bottom-left: 10px; border-radius-bottom-right: 10px; background-color: #1c1c1c;  }

.coursebox { border: 4px dashed #464646; }

img.userpicture {
    border-radius: 50px;
}

div#yui_3_17_2_1_1515602491694_305 {
    background: #111;
    border-radius: 0 0 30px 30px;
}

.navbar-inner {
    background-image: linear-gradient(to bottom, #111111, #111111);
    border: 1px solid #111111;
    box-shadow: 0 1px 20px 9px rgb(0, 0, 0);
}

.navbar-fixed-top .navbar-inner, .navbar-static-top .navbar-inner {
    -webkit-box-shadow: 0 1px 10px rgba(0,0,0,.1);
    -moz-box-shadow: 0 1px 10px rgba(0,0,0,.1);
    box-shadow: 0 1px 20px 9px rgb(0, 0, 0);
	border-radius: 0 0 50px 50px;
}

.navbar .brand {
    float: left;
    display: block;
    padding: 10px 20px 10px;
    margin-left: -20px;
    font-size: 20px;
    font-weight: 200;
    color: #FFF;
    text-shadow: 0 0px 0 #000;

} 

.navbar .nav>li>a {
    float: none;
    padding: 10px 15px 10px;
    color: #FFF;
    text-decoration: none;
    text-shadow: 0 0px 0 #000;
}

img.small-logo {
    display: none;
}

.courses .coursebox.even {
    background-color: #282828;
}




}
