css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("amoursucre.com"), domain("amordoce.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com")
{

/* Fundo da página */   
index > .connected {
    background-color: white !important;
    background-image: url(http://i67.tinypic.com/2rvy7mx.jpg) !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;
    background-attachment: fixed !important;
}

.season-s2[_nghost-c1] .connected[_ngcontent-c1], .season-s2 [_nghost-c1] .connected[_ngcontent-c1] {
    background: url(http://i63.tinypic.com/97lb8k.png),50% no-repeat;
    background-attachment: fixed;
    background-size: cover;
}
.season-s1[_nghost-c1] .connected[_ngcontent-c1], .season-s1 [_nghost-c1] .connected[_ngcontent-c1] {
    background: url(http://i63.tinypic.com/97lb8k.png),50% no-repeat;
    background-attachment: fixed;
    background-size: cover;
}

/* fundo do nick da docete */

.blue[_nghost-c15] {
    background-color: #cdc9c9 !important;
 
}
[_nghost-c5] .panel-connected[_ngcontent-c5] .line-container[_ngcontent-c5] paper-clip-title[_ngcontent-c5] {
    position: relative !important;
    display: inline-block!important;
    z-index: 10!important;
    box-shadow: 1px 1px 2px 0 rgba(125,125,125,0.75)!important;
    vertical-align: top!important;
    margin-top: 4px!important;
} 

/* botões: perfil, fórum, goodies, ajuda */
 
  #panel-menu a {
            background : #fed5df !important ;
            color : white  !important ;
        }    

/* fundo: pa e cash */
   [_nghost-c5] .panel-connected[_ngcontent-c5] .line-container[_ngcontent-c5] .currency[_ngcontent-c5] .values[_ngcontent-c5] {
    border-radius: 8px!important;
    background-color: #f880a5 !important;
    color: white !important ;
    text-shadow: 0 1px 1px black !important;
}
                                                
/* cor da palavra: Episódio (acima de pa e cash AD e UN) */

[_nghost-c17] .actual-season[_ngcontent-c17] {
    color: #817161;
}

/* cor da palavra: "Hight School Life" e "University Life" ao lado de episódio (AD e UN) */
/* AD */
.season-s1 a.color-primary, .season-s1 div.color-primary, .season-s1 h1.color-primary, .season-s1 h2.color-primary, .season-s1 h3.color-primary, .season-s1 h4.color-primary, .season-s1 p.color-primary, .season-s1 span.color-primary {
    color:  #ca96c7 !important ; }

/* UN */
.season-s2 a.color-primary, .season-s2 div.color-primary, .season-s2 h1.color-primary, .season-s2 h2.color-primary, .season-s2 h3.color-primary, .season-s2 h4.color-primary, .season-s2 p.color-primary, .season-s2 span.color-primary {
    color: #d2c8be !important; }

/* icone: livro e maça e chapéu de formatura */

.season-s1[_nghost-c17] .icon[_ngcontent-c17], .season-s1 [_nghost-c17] .icon[_ngcontent-c17] {
    color: #cdc9c9;}
    
.season-s2[_nghost-c17] .icon[_ngcontent-c17], .season-s2 [_nghost-c17] .icon[_ngcontent-c17] {
    color: #f880a5; }


/* cor da barra movel: "Ir para University Life" e "Ir para High school life" */  

.season-s1[_nghost-c17] .switch[_ngcontent-c17], .season-s1 [_nghost-c17] .switch[_ngcontent-c17] {
    background: #a79482;}

.season-s2[_nghost-c17] .switch[_ngcontent-c17], .season-s2 [_nghost-c17] .switch[_ngcontent-c17] {
    background: #fed5df;}

/* botões: meu ape, cidade, meus episódios, personagens, banco */

.season-s2[_nghost-c6] a[_ngcontent-c6], .season-s2 [_nghost-c6] a[_ngcontent-c6] {
    background: #84786b  !important;
}
[_nghost-c6] a[_ngcontent-c6] {
 color: #f5cbcb !important;
}

/* BOTÕES: CONFIRGURAÇÃO DE SOM, FACEBOOK, TWITTER, INSTAGRAM */

/* confirguração de som */

[_nghost-c7] .link-entry.parameters-panel-button[_ngcontent-c7] {
    background: #fed5df
    url("https://www.amordoce.com/image/pictos/parameters.png") no-repeat 12px 11px !important;
}

/* facebook, twitter, instagram */

[_nghost-c7] .link-entry[_ngcontent-c7] {
    background-color: #fed5df !important;
}


/* COR DE FUNDO E COR DA PALAVRA: PAINEL */

[_nghost-c24] .header[_ngcontent-c24] {
    background:#d2c9c0 !important;
    color: #847566 !important ;}

/* COR DE FUNDO E COR DA PALAVRA: ATRÁS DO NICK DA DOCETE NO PAINEL */

[_nghost-c24] .box-info[_ngcontent-c24] .player-name[_ngcontent-c24] {
 background: #958a7e !important;
    color:#fff!important}

/* COR DA PALAVRA: "Meu perfil" E "Minha parencia" NO PAINEL */

[_nghost-c24] .box-info[_ngcontent-c24] .buttons[_ngcontent-c24] a[_ngcontent-c24] {
    color: #ca96c7 !important; }
}
