css:
@-moz-document domain("katcr.co") {
/********** Site Up / Dwn / Login Section **********/

img#upshrink {
    margin: 13px !important;
    position: absolute;
    left: 905px;
    top: 43px;
}
#siteslogan
/* img#smflogo,*/

{
    background-image: url("https://extraimage.net/images/2018/01/13/f5367ead2b13fb87af7d04c24bf2e275.png");
    display: block;
    height: 30px;
    text-indent: -1000%;
    width: 400px;
    position: absolute;
    left: 945px;
    top: 50px;
}
/*********** Site Background - Image / Color ***********/

body {
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC") repeat scroll 0 0 !important;
}
#main_menu {
    float: right;
    position: absolute;
    right: 5px;
    top: 0;
    width: auto;
}
/*********** Site Background - Logo ***********/

#top_section h1.forumtitle a {
    background-image: url("https://extraimage.net/images/2018/01/12/0231c711ce950856b7663bee23cbca58.png");
    display: block;
    height: 40px;
    text-indent: -1000%;
    width: 440px;
    position: absolute;
    left: 10px;
    top: 5px;
}
#top_section .forumtitle a:hover {
    background-image: url("https://extraimage.net/images/2018/01/12/cc92b6b6ed29165ac249953c126d1381.png");
    height: 40px;
    text-indent: -1000%;
    width: 440px;
    position: absolute;
    left: 10px;
    top: 5px;
}
#top_section {
    background: rgba(0, 0, 0, 0) linear-gradient(#000000, #303030) repeat scroll 0 0;
    box-shadow: 0px 0px rgba(255, 255, 255, 0.5) inset, 0 1px 3px rgba(0, 0, 0, 0.5);
    height: 48px;
    min-height: 48px;
}
/*********** Site Header ***********/

#header {
    background: transparent none repeat scroll 0 0;
    box-shadow: none;
    margin-bottom: 0;
    padding: 0;
}
#header,
#header a,
#header h2 {
    color: #c9c9c9;
}
#header div.frame {
    padding: 0;
}
h1.forumtitle {
    margin: 5px;
}
::-webkit-input-placeholder {
    color: #c9c9c9;
}
/*********** Site Header - Search ***********/

#header #search_form [name="search"] {
    background: #000000 none repeat scroll 0 0;
    border: medium none;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 1px 1px rgba(83, 83, 83, 1);
    box-sizing: border-box;
    color: #ffffff;
    font-size: 15px;
    line-height: 20px;
    margin: 70px 0 0 -10px;
    padding: 4px 20px 8px 10px;
    position: absolute;
    left: 350px;
    top: -15px;
    text-shadow: none;
    width: 350px;
    z-index: 3;
}
#search_form .button_submit {
    margin: 13px !important;
    position: absolute !important;
    left: 682px;
    top: 48px;
    display: inline-block !important;
}
#upper_section {
    margin-bottom: 0;
}
#upper_section div.news {
    text-align: left;
}
div#upper_section div.news form {
    left: 235px;
    padding-bottom: 0;
    position: absolute;
    top: 9px;
}
form#search_form {
    color: transparent;
    font-size: 0;
}
form#search_form font,
form#search_form > a {
    display: none;
}
/*********** Top Section -  Drop Down Menu ***********/

.dropmenu li a.firstlevel {
    background: transparent none repeat scroll 0 0;
}
.dropmenu li a.firstlevel:hover,
.dropmenu li:hover a.firstlevel {
    background: #555555 none repeat scroll 0 0;
    box-sizing: border-box;
    border-radius: 3px 3px 3px 3px;
    box-shadow: 0 0.5px rgba(0, 0, 0, 1.0) inset, 0 1px 1px rgba(0, 0, 0, 0);
}
.dropmenu li ul {
    background: #000000 none repeat scroll 0 0;
    border-radius: 3px 3px 3px 3px;
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.5);
}
.dropmenu li li a:hover,
.dropmenu li li:hover > a {
    background: rgba(0, 0, 0, 0) linear-gradient(#323232, #d8d5d2) repeat scroll 0 0 !important;
    border-radius: 3px 3px 3px 3px;
    ;
    box-shadow: 0 1px rgba(255, 255, 255, 0.1) inset, 0 1px 3px rgba(0, 0, 0, 0.5) !important;
}
/*********** Top Section - Avatar ***********/

#header #upper_section .avatar img {
    border: 3px solid #c9c9c9;
    border-radius: 33px;
    margin: 0;
    max-height: 50px;
    max-width: 50px;
    position: absolute;
    left: 220px;
    top: 55px;
}
/*********** Normal, Standard Links. ***********/

a:link {
    color: #c9c9c9;
}
a:link:hover {
    color: #1cc105;
}
body.rich_editor {
    background: #9a9797 none repeat scroll 0 0 !important;
    color: #ffffff !important;
}
body,
td,
th,
tr {
    color: #c9c9c9!important;
}
.content {
    background: #404040 none repeat scroll 0 0;
    border-radius: 2px;
    color: #ffffff;
}
h1,
h2,
h3,
h4,
h5 {
    color: #9a9797;
}
h1 a,
h2 a,
h3 a,
h4 a,
h5 a {
    color: #bebebe;
}
h1 a:hover,
h2 a:hover,
h3 a:hover,
h4 a:hover,
h5 a:hover {
    color: #ffffff;
}
/*********** First Table - Recent Posts ***********/

#ic_recentposts strong a {
    color: #e6e6e6;
}
#ic_recentposts strong a:hover {
    color: #ffffff;
}
#recent_posts_content.hslice {
    background: transparent;
}
#ic_recentposts.middletext dt:nth-of-type(2n),
#ic_recentposts.middletext dd:nth-of-type(2n) {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #707070 0%, #000000 100%) repeat scroll 0 0;
}
#ic_recentposts.middletext dt:nth-of-type(2n+1),
#ic_recentposts.middletext dd:nth-of-type(2n+1) {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #707070 0%, #000000 100%) repeat scroll 0 0;
}
dl#ic_recentposts dt {
    border-bottom: 1px #000000;
    font-size: 12px;
    padding: 4px 6px;
}
dl#ic_recentposts dd {
    border-bottom: 1px solid #000000;
    padding: 4px 6px;
}
/*********** Second Table - Forum ***********/

tr:nth-of-type(2n+1),
.table_list tbody.content tr:nth-child(2n+1),
.table_grid tbody tr:nth-child(2n+1) {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #12171a 0%, #000000 100%) repeat scroll 0 0;
}
tr:nth-of-type(2n),
.table_list tbody.content tr:hover,
.table_grid tbody tr:hover {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #12171a 0%, #000000 100%) repeat scroll 0 0;
}
.table_list tbody.content tr:hover,
.table_grid tbody tr:hover {
    background: #656565;
    border-radius: 7px 7px 7px 7px;
}
table {
    border-spacing: 0;
}
.table_list tbody.content td.info a.subject {
    color: #d6d6d6;
}
.table_list tbody.content tr td,
.table_grid tbody tr td {
    font-size: 12px;
}
.table_grid tbody tr td div span a,
.table_grid tbody tr td div span a:hover {
    color: #d1d1d1;
}
.table_grid tr.catbg,
.table_grid tr.titlebg,
table.table_grid td {
    border-bottom: 1px #000000 !important;
}
.table_grid .catbg th,
table.table_grid td {
    border-right: 1px solid #3f3f3f !important;
}
.table_grid .catbg th:last-child,
table.table_grid td:last-child {
    border-right: none!important;
}
tr.windowbg td,
tr.windowbg2 td,
tr.approvebg td,
tr.highlight2 td,
.table_list tbody.content tr td,
.table_grid tbody tr td {
    border-bottom: 1px solid #3d3d3d;
    border-right: 1px solid #3d3d3d;
}
tr.windowbg td:last-child,
tr.windowbg2 td:last-child,
tr.approvebg td:last-child,
tr.highlight2 td:last-child {
    border-right: none;
}
.divider tr {
    background: transparent none repeat scroll 0 0 !important;
}
#preview_body,
h3.catbg a:hover,
h4.catbg a:hover,
.table_list tbody.header td a:hover {
    color: #ffffff
}
.quoteheader {
    color: #9a9797;
}
#recent #unread.tborder.topic_table table tr td span a {
    color: #d1d1d1;
}
/*********** KAT Community - Info Center  ***********/

div.title_barIC,
div.title_barIC h4.titlebg {
    background: #000000 none repeat scroll 0 0;
}
/*********** Buttons & Search ***********/

.buttonlist ul li a {
    background: #6e0404 none repeat scroll 0 0;
}
.buttonlist ul li a:hover,
.buttonlist ul li a.active {
    background: #12171a none repeat scroll 0 0;
    color: #ffffff;
}
.button_submit,
.button_reset {
    background: #605d5d none repeat scroll 0 0;
    border: medium none;
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.08) inset, 0 -1px 0 0 rgba(0, 0, 0, 0.03) inset, 0 1px 0 0 rgba(255, 255, 255, 0.2) inset;
    text-shadow: 0 1px 0 #191919;
}
.button_submit:hover,
.button_reset:hover {
    background: #2f2e2e none repeat scroll 0 0;
}
.pagelinks a.navPages
/*.pagelinks strong*/

{
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #9a9797 0%, #9a9797 100%) repeat scroll 0 0;
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.08) inset, 0 -1px 0 0 rgba(0, 0, 0, 0.03) inset, 0 1px 0 0 rgba(255, 255, 255, 0.2) inset;
    text-shadow: -1px 1px 0 #191919;
}
.pagelinks a.navPages:hover,
.pagelinks strong {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #343333 0%, #343333 100%) repeat scroll 0 0;
    color: #fff;
    text-shadow: -1px 1px #3c3a37;
}
#display_jump_to > input,
#message_index_jump_to > input {
    cursor: pointer;
    text-transform: uppercase;
}
/*********** Post Reply Area ***********/

input,
button,
select,
textarea {
    background: #343333 none repeat scroll 0 0;
    border: 1px solid #ffffff;
    border-radius: 3px;
    color: #ffffff;
    font: 13px/115% verdana, Helvetica, sans-serif;
    padding: 2px 5px 3px;
}
input:hover,
textarea:hover,
button:hover,
select:hover,
input:focus,
textarea:focus,
button:focus,
select:focus {
    border: 1px solid #53d107;
}
.roundframe {
    background: #303030 none repeat scroll 0 0;
    border: 1px solid #404040;
    border-radius: 4px;
    padding: 0;
}
.roundframe .padding .button_submit.floatright {
    margin-bottom: 8px;
}
.postarea,
.moderatorbar {
    background: #404040 !important;
    border-radius: 3px;
    margin: 0;
    padding-left: 270px;
}
/*********** Alternating Backgrounds For Posts ***********/

.windowbg,
.windowbg2,
.windowbg3 {
    color: #ffffff;
}
.windowbg.description {
    background: #3d3938 none repeat scroll 0 0;
    border: 2px solid #393534;
    color: #ffffff;
}
/*********** Styles For Rounded Headers ***********/

h3.titlebg,
h4.titlebg {
    background: transparent;
    color: #9a9797;
    padding-left: 9px;
}
div.cat_bar {
    background: transparent none repeat scroll 0 0;
    padding-left: 0;
}
h4.catbg,
h4.catbg2,
h3.catbg,
h3.catbg2,
.table_list tbody.header td.catbg,
div.title_bar {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #3f3d3d 0%, #3f3d3d 100%) repeat scroll 0 0 !important;
    border-color: #3f3d3d #3f3d3d #3f3d3d #3f3d3d-moz-use-text-color;
    border-style: solid solid solid none;
    border-width: 0px 0px 0px medium;
    padding-left: 9px;
}
/*********** Post Wrapper ***********/

.post,
.personalmessage {
    color: #ffffff;
}
.poster {
    border-right: medium none;
    font-size: 12px;
    width: 233px;
}
.poster h4 {
    margin-right: -40px;
    padding: 0 0 3px;
}
.poster h4,
.poster h4 a {
    color: #9a9797;
    padding-right: 2px;
}
.keyinfo {
    padding-top: 2px;
}
.post_wrapper {
    background: #000000;
    border-radius: 3px;
    padding: 0 5px;
}
.modifybutton {
    float: none;
    left: 97%;
    position: relative;
}
/*********** Calendar Colors For Birthdays, Events And Holidays ***********/

.birthday {
    color: #980606;
    text-shadow: -1px 1px #191919;
}
.event {
    color: #30b230;
    text-shadow: -1px 1px #191919;
}
.calendar_today {
    background-color: #444444;
    border: 1px solid #666666;
}
/*********** A Descriptive Style ***********/

.description,
.description_board,
.plainbox {
    padding: 0.5em 1em;
    font-size: 12px;
    line-height: 1.4em;
    background: #000000 none repeat scroll 0 0;
    border: 1px #000000;
    margin: 0.2em 1px 1em 1px;
}
.description_board {
    margin: 20px 0;
    padding: 30px 40px;
    background: #282828;
    clear: both;
    overflow: auto;
}
/*********** Common Styles Used To Add Corners To Divisions. ***********/

.windowbg span.topslice,
.windowbg2 span.topslice,
.windowbg3 span.topslice {
    background: #353231 none repeat scroll 0 0;
    border-radius: 3px;
    border-top: 1px inset #b5a3a2;
}
/*********** /* Sticky Topics bg Color ***********/

.stickybg {
    background-color: #e8d8cf;
}
.stickybg2 {
    background-color: #f2e3d9;
}
/*********** /* Sticky Locked Topics bg Color ***********/

.lockedbg,
.lockedbg2 {
    background-color: #d4dce2;
}
.locked_sticky,
.locked_sticky2 {
    background-color: #D8CEC9;
}
/*********** Quote Posts ***********/

blockquote.bbc_standard_quote,
blockquote.bbc_alternate_quote {
    background-color: #4f4c4c;
    border: 1px solid #000000;
    border-top: 1px solid #8e8978!important;
    color: #c9c9c9;
}
.quoteheader {
    background-color: #252525;
    border: 1px double #b5abab !important;
}
/*********** Profile ***********/

#main_admsection #basicinfo h4 {
    color: #ffffff;
}
/*********** Styles For (fatal) Errors ***********/

.error {
    color: orange;
    text-shadow: -1px 1px #000000;
}
.errorbox {
    background-color: #3d3938;
    border: 2px solid #393534;
    margin-bottom: 1em;
    margin-top: 1em;
    padding: 1em;
}
/*********** Simple Feedback Messages ***********/

div#profile_error,
div#profile_success {
    margin: 0 0 1em 0;
    padding: 1em 2em;
    border: 1px solid;
}
div#profile_error {
    border-color: red;
    color: red;
    background: #fee;
}
div#profile_error span {
    text-decoration: underline;
}
div#profile_success {
    background: #000000 none repeat scroll 0 0;
    border-color: #000000;
    color: #000000;
    text-shadow: -1px 1px 0 #000000;
}
/*********** Generic, Color-Related, Classes ***********/

.catbg,
.catbg2,
tr.catbg td,
tr.catbg2 td,
tr.catbg th,
tr.catbg2 th {
    background: rgba(255, 255, 165, 1) none repeat scroll 0 0 !important;
}
/**************************************************************************************************/

.content {
    background: #303030 none repeat scroll 0 0;
    border-radius: 2px;
    color: #ffffff !important;
}
.repValue {
    /* vertical-align: super; */
    
    display: inline-block !important;
    top: -0.5em;
    position: relative;
    font-size: 75%;
    font-weight: bold;
    color: #000000;
    line-height: 100%;
}
quote,
blockquote,
.accentbox {
    background-color: #5c5e5f;
    border: 1px solid #ffffff;
    margin: 10px 0;
    padding: 8px 20px 8px 12px;
    display: block;
    color: #767261;
}
}
