css:
@-moz-document url-prefix("https://rethinkdb.com/"), url-prefix("https://www.rethinkdb.com/") {
body,
.landing .intro {
    background-color: #181818;
}
.landing .intro .realtime-queries {
    background: #181A19;
}
.landing .intro .realtime-queries p.query {
    background: #2a2a2a;
}
.landing .examples,
.landing .community,
.community-wrapper .events {
    background: #202121;
}
section.used-by {
    background: #163030;
}
.landing .get-started.callout {
    background-image: url(https://rethinkdb.com/assets/images/landing/code-pattern.png);
    background-color: #000;
}
.landing .get-started.callout h1 {
    color: #fff;
}
.site-footer,
.site-footer a,
.site-footer h1 {
    color: #B0B0B0;
}
.docs-article .byline,
.api-docs .byline,
code {
    background-color: #000;
    color: #fff;
}
.infobox,
.documentation .infobox,
.documentation .docs-article .infobox,
.blog .post .post-body .infobox,
.documentation .docs-article .toc,
.documentation .docs-landing .docs-intro {
    background: #222526;
}
.infobox.infobox-with-arrow::after,
.documentation .infobox.infobox-with-arrow::after,
.documentation .docs-article .infobox.infobox-with-arrow::after,
.blog .post .post-body .infobox.infobox-with-arrow::after {
    border-color: #222526 transparent;
}
.documentation .docs-article em {
    color: #3e8399;
}
div.highlighter-rouge,
.highlight,
.highlight pre,
pre,
.highlight .hll {
    background: #000;
}
.highlight .n {
    color: #999;
}
.highlight .ss {
    color: #e600ad;
}
.docs-nav li.active a {
    color: #2f6d81;
}
.docs-article .byline,
.api-docs .byline,
code {
    color: #cbcbcb;
}
.infobox.infobox-alert,
.documentation .infobox.infobox-alert,
.documentation .docs-article .infobox.infobox-alert,
.blog .post .post-body .infobox.infobox-alert {
    background: #2a2821;
}
.simple-button,
.cla .cla-choices .button,
.cla .cla-choices .landing .hero .hero-install a,
.landing .hero .hero-install .cla .cla-choices a,
.cla .cla-choices .landing .get-started.callout a,
.landing .get-started.callout .cla .cla-choices a,
.docs .platform-category .platform-buttons li,
.docs .documentation .docs-article .platform-category .platform-buttons li,
.documentation .docs-landing .primary-docs li {
    background: #25292a;
    border-color: #434a4d;
    box-shadow: 0 2px 1px rgba(0, 0, 0, 0.02), inset 0px 1px 0px 0px #454545;
}
.simple-button:hover,
.cla .cla-choices .button:hover,
.cla .cla-choices .landing .hero .hero-install a:hover,
.landing .hero .hero-install .cla .cla-choices a:hover,
.cla .cla-choices .landing .get-started.callout a:hover,
.landing .get-started.callout .cla .cla-choices a:hover,
.docs .platform-category .platform-buttons li:hover,
.docs .documentation .docs-article .platform-category .platform-buttons li:hover,
.documentation .docs-landing .primary-docs li:hover {
    border-color: #5e7981;
}
.documentation .docs-landing .primary-docs li p {
    color: #2f6577;
}
}
