css:
@-moz-document url-prefix("https://www.w3schools.com/") {
.w3schools-logo {
	color: #e5e3ea;
}

#main > div {
	background-color: #2c293c;
}

#main > div > span {
	color: #FFD41D !important;
}

#main > p > span {
	color: #FFD41D !important;
}

#topTD h1, #topTD div, #codeTD div {
	color: #000;
}

.w3-main, .w3-main h1 {
	color: #e5e3ea;
	background-color: #2c293c;
}

.resultcontainerinner div {
    background-color: #ffffff !important;
}

.w3-code span {
	color: #e5e3ea !important;
}

.w3-code > span span {
	color: #e5e3ea !important;
}

.htmlHigh span {
	color: #e5e3ea !important;
}

.jsHigh span {
	color: #e5e3ea !important;
}

hr {
	border-color: #adb3d7;	
}

.top {
	background-color: #2c293c;	
}

.nextprev a:link, .nextprev a:visited {
    background-color: #2c293c;
    color: #e5e3ea;
}

.nextprev a.w3-right {
    background-color: #4CAF50;
}

.w3-right {
    color: #e5e3ea;
}

.w3-example {
    background-color: #2c293c;
    padding: 0.01em 16px;
    margin: 20px 0;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12) !important;
}

.w3-code {
    width: auto;
    background-color: #2c293c;
    padding: 8px 12px;
    border-left: 4px solid #4CAF50;
    word-wrap: break-word;
}

.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    color: #e5e3ea  !important;
    background-color: #2c293c !important;
}

.w3-white, .w3-hover-white:hover {
    color: #e5e3ea  !important;
    background-color: #2c293c !important;
}

.w3-text-dark-grey, .w3-hover-text-dark-grey:hover, .w3-text-dark-gray, .w3-hover-text-dark-gray:hover {
    color: #e5e3ea !important;
}

.w3-black, .w3-hover-black:hover {
    background-color: #2c293c !important;
}

.w3-note {
    background-color: #35375f !important;
    border-left: 6px solid #354299;
}

.w3-warning {
    background-color: #5c1b1b !important;
    border-left: 6px solid #c2443b;
}

.w3-table-all {
    border: 1px solid #656981;
	background-color: #2c293c;
}

.w3-table-all tr:nth-child(2n+1) {
	color: #e5e3ea !important;
    background-color: #2c293c !important;
}
.w3-table-all tr:nth-child(2n) {
	color: #e5e3ea !important;
    background-color: #2e2a42 !important;
}

.w3-bordered tr, .w3-table-all tr {
    border-bottom: 1px solid #656981;
}

.w3-info {
    background-color: #2c293c !important;
    border-left: 6px solid #4CAF50;
}

.certoverlay {
	color: #000;
	background-color: rgba(0,0,0,0);
}
}
