hr,nav .brand-logo,.headertop table tbody tr td a img,.logo a img,#logo a img
{visibility: hidden !important;}

.alert,.panel,.comment_form_container,.prettyprint.linenums
{box-shadow: none !important;}

ol.linenums li
{text-shadow: none !important;}

.headertop table tbody tr td p,.headertopright p
{position: absolute !important;top: 30px !important;right: 320px !important;}

.headertop,#logo
{
     height: 65px !important;
	width: 250px !important;
	/* these numbers match the new image's dimensions */
	padding-left: 125px !important;
	padding-top: 25px !important;
	background: url(https://images.wikia.nocookie.net/__cb15/kekkai-sensen/images/8/89/Wiki-wordmark.png) no-repeat !important;
}







