css:
@-moz-document domain(v3g.ladypopular.ru), domain(v3gfacebook.ladypopular.com), domain(v3.g.ladypopular.com), domain(v3.g.ladypopular.bg) {

/* ==UserStyle==
@name           Lady Popular CSS Fixes
@namespace      USO Archive
@author         MariaLovesQueen
@description    `Some good fixes for game (settings for int & ru versions):- hide arena energy refill by diamonds popup- show energy fill timer- new booster popup view- new mirror in beauty salon viewand other small fixes`
@version        20210410.10.51
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain(v3g.ladypopular.ru), domain(v3gfacebook.ladypopular.com), domain(v3.g.ladypopular.com), domain("v3.g.ladypopular.bg") {

#onlineTime, #footer {
	display: block !important;
}

.cross-promo-btn, .pageant-energy-refill-popup, .arena-energy-refill-popup {
	display: none !important;
}

body.overflow-hidden {
    overflow: auto !important;
}


.pagination-wraper {
	display: table !important;
	width: 100% !important;
}

span.booster-time {
	visibility: visible !important;
	opacity: 1 !important;
}

.boosters-popup.show {
	top: -90px !important;
}

.vip-shop-boosters {
	height: 173px !important;
}

.boosters-popup ul.vip-shop-card.card-top-decor li:not(.card-top-decor_decor) {
    height: 173px !important;
    overflow: visible !important;
}

.duel-html, .duel-search, .boosters-popup, #duel-html, #search_result {
	min-height: 1100px !important;
}

.pagination-wraper a {
	display: table-cell !important;
}

#duel-html .boosters-popup {
	position: absolute !important;
}

#profileBox {
	z-index: 0 !important;
}

.album-cover:after, .partyCenter .thumb>span:first-child, .partyCenter .thumbnail>span:first-child, .fianceappearance .thumbnail-bg::before, .fianceoutfit .thumbnail-bg::before, .hiredecorations .thumbnail-bg::before, .ladyappearance .thumbnail-bg::before, .ladyoutfit .thumbnail-bg::before, .bfMirror-frame, .ladyMirror-frame {
	background: rgba(0,0,0,0) url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=) !important;
	border-color: rgba(0,0,0,0) !important;
	box-shadow: none !important;
	border-radius: 0px !important;
}
.ladyMirrorWrapper, .bfMirrorWrapper {
	background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=) #d1cfdc 50% top repeat !important;
}

.bfMirror-frame, .ladyMirror-frame {
    height: 287px !important;
}

.ladyMirrorWrapper, .bfMirrorWrapper {
    width: 208px !important;
    height: 290px !important;
    left: 0px !important;
    top: -3px !important;
		box-shadow: none !important;
}

.ladyMirror-holder #bodyWrapper {
    left: -164px !important;
    top: -105px !important;
}

.bfMirror-holder #bodyWrapper {
    left: -186px !important;
    top: -85px !important;
}

}

}
