css:
@-moz-document url-prefix("https://avenoel.org/")
{
html {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-image: url(https://image.noelshack.com/fichiers/2018/02/4/1515690011-na.jpg);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

body {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;

    background:rgba(255, 255, 255, 0);
}

main.container .container-content section
{
    background-color: rgba(128, 0, 0, 0.6);
}

tbody
{
    background-color: rgb(128, 0, 0);
}

td
{
    background-color: rgba(128, 0, 0, 0.9);
}

tr
{
    background-color: #1a1a1a;
}

.breadcrumb
{
    background-color: rgb(98, 0, 0);
}

tr.active, tr.active td
{
    background-color: #1a1a1a !important;

}

.btn.btn-primary
{
    background-color: #1a1a1a !important;
}

.navbar li.active a
{
    background-color: rgb(128, 0, 0) !important;
}

.bloc-title
{
    color: white;
}

label
{
    color: white;
}

a, th, .topics-author, .topics-amount, .topics-date, .topic-title
{
    color: white;
}

.breadcrumb li.active
{
    color: white !important;
}

.row.topic-message.odd 
{
    background-color: rgba(128, 0, 0, 0.9);
}

.row.topic-message  
{
    background-color: rgba(98, 0, 0, 0.9)
}

.message-content
{
    color: white;
}

.message-username a
{
    color: white !important;
}

.message-infos
{
    color: white;
}

.message-content-quote
{
    background-color: rgb(150, 0, 0) !important;
}

.message-content-quote a, .message-content-quote i
{
    color: white;
}

.profile-wrapper, .profile-infos tr, .profile-title, profile-stats
{
    background-color: rgb(128, 0, 0) !important;
    color: white;
}

.profile-title
{
    color: white !important;
}

section
{
    color: white;
}

.pagination.pagination-topic a
{
    color: white !important;
}

.chat-message, .chat-message-username a
    {
    color: white !important;
    }

.aside a
{
    color: white !important;
}

.spoiler-content
{
    background-color : rgb(128, 0, 0) !important;
}

.grey-btn.btn
{
    background-color: #1a1a1a;
}

.glyphicon .glyphicon-refresh
{

}

.form-control.post-content, .form-control
{
    opacity: 0.85;
    color: black;
}

a:hover
{
    color: white;
}

.form-group.preview
{
  background-color: rgba(128, 0, 0, 0.9);
  color:white;
}

.profile-biography
{
  background-color: rgb(128, 0, 0) !important;
  color: white;
}

a:focus
{
  color: white;
}

.admin a
{
  color: red !important;
}

.mod a
{
  color: orange !important;
}

.active .topics-author a
{
 color: white;
}
  
   .glyphicon-envelope
 {
     color: white;
 }

.chat-form
{
  background-color: #1a1a1a;
}

.message-quote img
{
  background-image: url(https://image.noelshack.com/fichiers/2018/02/4/1515695100-scdflh12.png);
  max-width: 20px;
  max-height: 20px;
  padding-right: 20px!important;
}

.btn-danger
{
  background-color: rgb(128, 0, 0);
  border-color: rgb(128, 0, 0);
}

.special a
{
  color: #446CB3 !important;
}

.message-edit img
{
  background-image: url(https://image.noelshack.com/fichiers/2018/02/4/1515703776-mhghaaw2.png);
  max-width: 20px;
  max-height: 20px;
  padding-right: 20px!important;
}

.message-delete img
{
  background-image: url(https://image.noelshack.com/fichiers/2018/02/4/1515704242-wpesi4r2.png);
  max-width: 20px;
  max-height: 20px;
  padding-right: 22px!important;
}

.topics-author .special

{
  color: #256cdd;
}

.post-content
{
  background-image: url(https://image.noelshack.com/fichiers/2018/02/6/1515870574-ico2.png);
  background-position: center;
  background-repeat: no-repeat;
  background-size: 120px 80px;
}
}

@-moz-document url-prefix("https://avenoel.org/profil")
{

td{
 background-color: rgba(128, 0, 0, 1);
}
}

