css:
@-moz-document domain("www.gdax.com") {
/* -------------------------------------
header
------------------------------------- */

/* background */
.Navbar_row_2VPRO {
	background-color: #2f3d45 !important;
  	border-bottom: 2px solid #15232c;
}

/* current price and volume */
.Navbar_info-number-wrapper_3zQth, .AccountPanel_sliding-button_3400I:before{
  	background-color: transparent !important;
}

/* currency indicator, select product text, price and volume text, user name */
.ProductSelection_menu-entry_1EmIC, .ProductSelection_description_1xVN9, .MarketInfo_market-stat_6_t5q,  .AccountPanel_account-panel-user-name_S2Srh, .AccountPanel_icon-bar_iAZyZ {
  	color: rgba(255, 255, 255, 0.8) !important;
    opacity: 0.8;
}

/* GDAX Logo and notifications icon */
.Navbar_brand_3CorI, .NotificationsPanel_notifications-button_2K8gC {
  	filter: invert(100%) grayscale(100%);
}

/* dropdown arrow, right side flyout menu icon */
.ProductSelection_arrow_1qa11:before, .ProductSelection_arrow_1qa11:after, .AccountPanel_icon-bar_iAZyZ {
  	background-color: rgba(255, 255, 255, 0.8) !important;
}


/* ----------------------------------
select product dropdown list
---------------------------------- */

/* background */
.ProductSelection_choose-product-list_3dW6t {
    background-color: #2f3d45 !important;
}

/* currency pair and price text */
.ProductSelectionItem_name_5HxEc, .ProductSelectionItem_price_1LPd5 {
    color: #A0A5A9 !important;	
}

/* currency pair and price text hover highlight*/
.ProductSelectionItem_name_5HxEc:hover, .ProductSelectionItem_price_1LPd5:hover {
    color: #FFFFFF !important;	
}
}
