css:
@-moz-document domain("crystalmathlabs.com") {
body {
  font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
  line-height: 1.5;
  font-size: 14px;
  background: #252525;
  color: #f7f7f7;
  margin: 0;
  padding: 0;
}

* {
  transition: 300ms;
}

*:hover {
  transition: 300ms;
}

*:focus {
  outline: none;
}

a {
  color: #ffd6af;
  text-decoration: none;
}

a:hover {
  text-decoration: underline;
  cursor: pointer;
}

h1 {
  margin: 0;
  padding-left: 20px;
}

h1 a {
  text-decoration: none;
  color: #f9f7ed;
}

h1 a:hover {
  text-decoration: none;
  border-bottom: 1px solid #f9f7ed;
  color: #f9f7ed;
}

.news h3 {
  margin-top: 0px;
}

.news {
  padding: 10px;
  background-color: rgba(255, 255, 255, 0.1);
}

.readnews {
  padding: 10px;
  background-color: rgba(255, 255, 255, 0.3);
}

#wrap {
  width: 960px;
  margin: 20px auto;
  margin-top: 0px;
}

#header {
  height: 51px;
  margin-top: 5px;
  text-align: center;
}

#menu {
  background-color: transparent;
  height: 50px;
  line-height: 50px;
  margin-top: 30px;
}

#menu ul {
  list-style-type: none;
  margin: 0;
  text-align: center;
}

#menu ul li {
  padding: 20px;
  float: none;
  text-align: center;
  display: inline;
}

#menu ul li a {
  text-transform: uppercase;
  display: inline;
  padding: 0 6px 0 6px;
  color: #fff;
  text-decoration: none;
  font-size: 10px;
  font-weight: 500;
  letter-spacing: 0.7px;
  opacity: 0.6;
}

#menu ul li a:hover {
  background-color: transparent;
  opacity: 1;
}

#menu ul li ul.dropdown {
  display: none;
  position: absolute;
  background-color: #252525;
  z-index: 999;
  left: 0;
  top: 50px;
  padding-left: 0px;
}

#menu ul li ul.dropdown li {
  width: 100%;
  text-align: left;
}

#menu ul li#selected {
  background-color: transparent;
}

#menu ul li#gamelink {
  float: none;
  margin-right: 10px;
}

#menu ul li form {
  display: inline;
}

#menu ul li form input[type="text"] {
  padding: 5px 25px 6px 15px;
  border: 1px solid #a6a6a6;
  color: #a6a6a6;
  background-color: transparent;
  outline: none;
  border-radius: 2px;
}

#menu ul li form input[type="text"]:focus {
  color: #fff;
}

#menu ul li form input[type="submit"] {
  border-radius: 2px;
  padding: 5px 15px 6px 15px;
  border: 1px solid #a6a6a6;
  background-color: transparent;
  color: #a6a6a6;
  text-transform: uppercase;
  font-weight: 200;
  letter-spacing: 0.7px;
  cursor: pointer;
}

#menu ul li form input[type="submit"]:hover {
  background-color: #a6a6a6;
  color: #000;
}

/*
  1: Home           hide
  2: Current
  3: Records
  4: Virtual Hiscores
  5: Proof Records  hide
  6: More
  7: Search
  8: Change name    hide
  9: Donate         hide
*/

/*
  1 groups
  2 competitions
  3 team competitions
  4 saved lists
  5 signatures        hide
  6 total gains       hide
  7 hall of fame      hide
  8 supplies & ehp
  9 players online    hide
  10 your account     hide
*/

/* Don't hide the secondary menu. */
#menu ul li ul li:nth-child(1),
#menu ul li ul li:nth-child(5),
#menu ul li ul li:nth-child(8),
#menu ul li ul li:nth-child(9) {
  display: inherit;
}

/* Hide secondary menu items. */
#menu ul li ul li:nth-child(5),
#menu ul li ul li:nth-child(6),
#menu ul li ul li:nth-child(7),
#menu ul li ul li:nth-child(9),
#menu ul li ul li:nth-child(10) {
  display: none;
}

/* Hide Home, Proof Records, Change Name, and Donate links. */
#menu ul li:nth-child(1),
#menu ul li:nth-child(5),
#menu ul li:nth-child(8),
#menu ul li:nth-child(9) {
  display: none;
}

/* Change "Current" to "Current Top" */
#menu ul li:nth-child(2) a:after {
  content: ' top';
  visibility: visible;
}

#menu ul li ul li {
  padding: 5px;
  display: block;
}

#menu ul li ul li a {
  float: none;
  text-align: left;
}

#contentwrap {
  background-color: transparent;
  padding: 0 0 0 20px;
  margin-top: 0px;
}

#content {
  float: left;
  width: 710px;
  margin-top: 20px;
}

#content h2 {
  margin: 0;
  color: #fff6cf;
  line-height: 30px;
  font-size: 20px;
  font-weight: 100;
}

#content h2 a {
  color: #fff6cf;
}

#content p {
  padding: 0 0 10px 0;
}

#content ul {
  padding: 0 20px 10px 30px;
}

#sidebar {
  float: right;
  width: 220px;
  margin-top: 20px;
}

#sidebar h3 {
  height: 30px;
  line-height: 30px;
  font-weight: 600;
  font-size: 14px;
  margin: 0;
  padding: 0 0 0 20px;
  color: #fff;
}

#sidebar p {
  padding: 0 10px 10px 10px;
  color: #232323;
}

#sidebar ul.notype {
  list-style-type: none;
  padding: 10px 0 20px 22px;
  margin: 0px;
}

#sidebar ul {
  padding: 10px 0 20px 40px;
  margin: 0;
  color: #fff6cf;
}

#sidebar ul li {
  padding: 0;
  margin: 0;
}

#sidebar ul ul {
  padding: 2px 0 2px 20px;
}

#sidebar ul li.highlightedli {
  color: green;
}

.highlightedli {
  color: green;
}

#ad {
  display: none;
}

#footer {
  background-color: transparent;
  text-align: center;
  color: #fff;
  height: 50px;
  line-height: 50px;
  margin-top: 0px;
}

#footer p {
  padding: 0;
  margin: 0;
}

#footer a {
  color: #fff;
  padding: 20px;
  opacity: 0.6;
  letter-spacing: 0.7px;
}

#footer a:hover {
  opacity: 1;
  text-decoration: none;
}

#diag {
  display: none;
  float: left;
  margin-left: 20px;
}

#footerlinks {
  text-transform: uppercase;
  font-size: 10px;
  font-weight: 500;
  float: none;
}

#footerlinks:not(a) {
  color: transparent;
}

#footerad {
  display: none;
}

/* Dont display the "turn off addblocker" message. */
div[style="text-align:center"],
div[style="text-align:center"] a {
  display: none;
}

#post {
  background-color: #696969;
  width: 775px;
  padding: 8px;
  margin: 8px;
}

#post2 {
  background-color: #696999;
  width: 775px;
  padding: 8px;
  margin: 8px;
}

#profile {
  padding: 8px;
  margin: 8px;
  float: left;
  width: 150px;
}

.clear {
  clear: both;
}

#title {
  width: 300px;
  float: left;
  font-weight: bold;
}

#image {
  float: right;
}

p.in {
  text-indent: 40px;
}

.ui-timepicker-div .ui-widget-header {
  margin-bottom: 8px;
}

.ui-timepicker-div dl {
  text-align: left;
}

.ui-timepicker-div dl dt {
  height: 25px;
  margin-bottom: -25px;
}

.ui-timepicker-div dl dd {
  margin: 0 10px 10px 65px;
}

.ui-timepicker-div td {
  font-size: 90%;
}

.ui-tpicker-grid-label {
  background: none;
  border: none;
  margin: 0;
  padding: 0;
}

.ui-timepicker-rtl {
  direction: rtl;
}

.ui-timepicker-rtl dl {
  text-align: right;
}

.ui-timepicker-rtl dl dd {
  margin: 0 65px 10px 10px;
}

.positive {
  color: #53e053;
  font-weight: bold;
}

.negative {
  color: red;
  font-weight: bold;
}

#competitionTable {
  border: 1px solid white;
  white-space: nowrap;
}

#competitionTable tr td {
  border-bottom: 1px solid white;
  padding: 3px 3px 3px 3px;
  white-space: nowrap;
}

#weekrecords {
  padding-left: 40px;
  padding-right: 40px;
}

.recordsheader {
  text-align: center;
  margin-right: 50px;
}

.recordslist {
  float: left;
  width: 260px;
}

.recordsheader {
  text-align: center;
}

.tooltip {
  color: #000000;
}

.tablecol {
  text-align: right;
  padding-left: 8px;
}

#sidebarad {
  text-align: right;
  margin-right: 22px;
}

#shortsidebarad {
  margin-left: 40px;
}

#headerimg {
  margin: 15px;
  float: none;
}

#headername {
  display: none;
}

#headerad {
  display: none;
}

#track_details {
  float: right;
  width: 170px;
  margin-top: 10px;
}

#user_description {
  word-wrap: break-word;
}

#stats_details {
  float: right;
  width: 285px;
  margin-top: 10px;
}

.data_graph {
  width: 675px;
  height: 450px;
}

table.hiscore_table tr td.hiscore_name {
  text-align: left;
  width: 142px;
}

table.hiscore_table tr td {
  text-align: right;
}

table#stats_table {
  font-size: 1.05em;
  font-weight: 600;
  float: left;
}

table#stats_table tr.evenstatsrow {
  background-color: #252323; /* 201d1d */
}

table#stats_table tr td.column_title {
  text-align: center;
}

table#stats_table tr td.column_skill {
  text-align: left;
  padding-right: 0px;
}

table#stats_table tr td {
  padding-right: 7px;
  text-align: right;
}

table.donators_table tr td {
  width: 150px;
}

table#competition_table tr td.hiscore_name {
  text-align: left;
}

table#competition_table tr td.column_title {
  text-align: center;
}

table#competition_table tr td {
  padding-right: 25px;
  text-align: right;
}

span.explain {
  text-decoration: underline;
}

span.updatebutton {
  font-size: 16px;
  color: red;
}

span.updatebutton a {
  text-decoration: none;
  color: #53e053;
}

span.updatebutton a:hover {
  text-decoration: underline;
}

div#socmediaicons {
  margin-left: 30px;
}

div#socmediaicons a {
  margin-right: 10px;
}

.hangingindent {
  padding-left: 22px;
  text-indent: -22px;
}

.perk_list {
  list-style-type: none;
  padding: 10px 0 20px 22px;
  margin: 0px;
}

#content ul.perk_list ul.perk_list {
  padding-bottom: 0px;
}

.affiliate_nonlink {
  color: #ffd6af;
  text-decoration: none;
}

table.hiscore_table tr td.padded_cell {
  padding-right: 10px;
  padding-left: 10px;
  text-align: center;
}

table.hiscore_table tr td.pr_proof_cell {
  width: 150px;
  padding-right: 10px;
  padding-left: 10px;
  text-align: center;
}

h4 {
  height: 30px;
  line-height: 30px;
  font-weight: 600;
  font-size: 14px;
  color: #fff;
}

}
