css:
@-moz-document regexp(".*bx\\.in\\.th\\/((THB|BTC)/.*)?") {
/* Hide logo strip*/
#logo-strip {
    display: none;
}
/* Prices */
.pillstats.tradestats * {
    vertical-align: bottom;
}
.pillstats.tradestats .last,
.pillstats.tradestats .high,
.pillstats.tradestats .low {
    font-size: 2em;
}
#subnav-strip {
    overflow-x: hidden;
}
#subnav-strip .row {
    width: -moz-max-content;
    width: max-content;
}
#subnav-strip .span8 {
    width: auto;
}
#subnav-strip,
.panel {
    margin-bottom: 2px;
}
.pillstats.tradestats {
    width: -moz-fit-content;
    width: fit-content;
}
/* Common fix */
#content .container.parent>.row {
    margin: auto;
    width: -moz-fit-content;
    width: fit-content;
}
.btn-info {
    background: #49afcd;
}
.btn-small {
    background: none;
}
/* Alerts */
#alerts .alert {
  margin: 0 0 2px 0;
  padding: 2px 5px;
}
#alerts .alert .close {
  right: 0;
}
/* panels */
.panel-header button,
.panel-header .btn {
    padding: 0 6px;
    margin-top: 0!important;
}
.panel-header {
    padding: 3px 5px 5px;
}
.panel-content {
    padding: 3px;
}
.panel-content .nav-tabs li a {
    padding: 2px 10px;
    border-radius: 4px;
}
/* Larger first items on trade tables */
.tradetbls thead th,
.tradetbls tbody td {
    padding: 3px;
    padding-left: 5px;
    overflow-x: hidden;
    white-space: nowrap;
}
.tradetbls tbody tr:first-child td:nth-child(2),
.tradetbls tbody tr:first-child td:nth-child(3) {
    font-size: 1.5em;
}
.tradetbls .panel-content {
    height: -moz-fit-content!important;
    height: fit-content!important;
}
.tradetbls>div:first-child {
    margin: 0;
}
#content .container.parent>.row:first-child {
    width: auto;
    display: flex;
    flex-direction: column-reverse;
}
#content .container.parent>.row:first-child>div {
    float: none;
    width: 100%;
    margin: 0;
}
.panel-content .nav {
    border: none;
    margin-bottom: 2px;
}
.panel-content .nav .active a {
    border: 1px solid #ddd;
    border: none;
}
/* Charts */
.panel-content.charts .nav .active a {
    background: #428bca;
    color: white;
}
.panel-content.charts .nav-tabs li:first-child a:after {
    content: 'Charts';
    line-height: 20px;
    font-size: 14px;
    padding: 0 3px;
}
.panel-content.charts .nav-tabs li:first-child a {
    line-height: 0;
    font-size: 0;
}
.panel-content.charts .nav-tabs li:nth-child(2) {
    display: none;
}
.panel-content.charts .tab-content {
    overflow-x: hidden;
    white-space: nowrap;
    border: 1px solid #ddd !important;
}
#chart1,
#chart2 {
    display: inline-block;
    width: 50%;
    border: none;
}
/* Order boxes */
#orderboxes .control-group {
    margin-bottom: 0.5em;
}
#orderboxes .form-actions {
    padding: 10px;
    text-align: center;
}
#orderboxes .control-label {
    width: 120px;
}
#orderboxes .controls {
    margin-left: 130px;
}
#orderboxes .controls>input[type=text],
#orderboxes .controls>.input-prepend {
    width: 12em;
}
#orderboxes .controls>.input-prepend>input {
    width: 10em;
}
#orderboxes .tab-content {
    display: flex;
}
#orderboxes .nav-tabs li:last-child {
    display: none;
}
#orderboxes .has-switch>div>* {
    padding: 0px;
}
#buy,
#sell,
#open {
    margin: 0;
}
#buyform,
#sellform {
    padding: 3px;
}
#buyform fieldset,
#sellform fieldset {
    margin: 0;
}
#buyform .form-actions,
#sellform .form-actions {
    margin: -3px;
    padding: 3px;
}
#open::before {
    text-align: center;
    display: block;
    font-weight: bold;
}
#open:before {
    content: 'Open Orders';
}
#open {
    flex-grow: 1;
    padding: 3px;
    display: block;
    margin-left: 3px;
}
#open th,
#open td {
    padding: 2px 5px;
}
#open td:last-child {
    text-align: center;
}
#open td .btn {
    padding: 0 0.5em;
}
/* Chatbox and credits */
#chatbox {
    left: auto;
    right: 20px;
}
#chatboxpop {
    left: auto;
    right: 0;
    margin-bottom: 1px;
}
#chatbox:before {
    width: -moz-fit-content;
    width: fit-content;
    position: fixed;
    bottom: 0;
    left: 0;
    border: 1px solid lightgray;
    border-radius: 3px;
    padding: 0 5px;
    background: #f9f9f9;
    color: #888;
    font-size: 0.75em;
    content: 'Stylesheet by @anuith. bitcoin:17Ji7VbQrj3N2oonBvdKWhCiLBac441kp4';
}
}
