css:
@-moz-document domain("whattomine.com"){

.adsbygoogle,iframe,iframe div,iframe span,iframe a,.centered-image-short{display:none!important;pointer-events:none!important}


h4.text-justify.mt-2{font-size:.85rem;color:#515151;transition:color .5s,font-size .5s step-end}
h4.text-justify.mt-2:hover{font-size:1rem;color:#a6a6a6}
.ck-button .btn{line-height:1}

.col-xs-1, .col-xs-2, .col-xs-3, .col-xs-4, .col-xs-5, .col-xs-6, .col-xs-7, .col-xs-8, .col-xs-9, .col-xs-10, .col-xs-11, .col-xs-12{float:left}
.col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {position:relative;min-height:1px;padding-left:15px;padding-right:15px}
.navbar-nav>li{float:left}



body {color: #989898;background-color:#1a2023}
.navbar-default {background-color: #171717;border-color: #272727}
.nav-tabs.secondary>li>a {color: #b5b5b5}
.nav-tabs {border-bottom: 1px solid #525252}
.nav-tabs>li>a {border:none}
.nav-tabs>li>a:hover{background-color: #525252}
.nav-tabs.primary>li.active>a, .nav-tabs.primary>li>a:hover, .nav-tabs.primary>li>a:focus {background-color: #171717;color: #fff}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {border: 1px solid #444;border-bottom-color: transparent;}
.nav-tabs.secondary>li.active>a, .nav-tabs.secondary>li.active>a:hover, .nav-tabs.secondary>li.active>a:focus {background-color: #1a2023}
.nav-tabs.secondary>li.active>a:hover{color:#fff}
.input-group-btn>.btn:hover{background-color:#444;border-color:#777;color:#eee}

.bg-light{background-color:#191919!important}

.nav-tabs.primary>.nav-item>a.nav-link.active{background:#191919;color:#aaa}
.nav-tabs.primary>.nav-item>a.nav-link:hover, .nav-tabs.primary>.nav-item>a.nav-link:focus{background:#444;border-color:#666 #666 #191919;color:#eee}

.nav-tabs.secondary>.nav-item>a.nav-link.active{background:#1a2023;color:#aaa}
.nav-tabs.secondary>.nav-item>a.nav-link:hover,.nav-tabs.secondary>.nav-item>a.nav-link:focus {background:#31414e;color:#eee}
.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link{border-color:#383838 #383838 #1a2023}

.input-group-text {color:#777;background-color:#191919;border:1px solid #353535}

.btn-default {color: #b3b3b3; background-color:#131313;border-color:#272727}
.ck-button-amd input+span {background-color: #1d1616;border-color:#353535}
.ck-button-amd input:hover+span {color:#eee;background-color:#581d1b;border-color:#272727;}
.ck-button-amd input:checked+span {background-color:#a73e3b;border-color:#d62722}
.ck-button-nv input+span {background-color:#141914;border-color:#272727}
.ck-button-nv input:hover+span {color:#eee;background-color: #2f4c00;border-color: #272727;}
.ck-button-nv input:checked+span {background-color:#518000;border-color:#74b700}
.ck-button input+span {background-color: #252525;color: #505050;}
.ck-button input:checked+span {background-color:#31414e;}

.form-control {color: #afafaf;background-color: #171717;border: 1px solid #4a4a4a}
.input-group-addon {color: #7b7b7b;background-color: #1b1b1b;border: 1px solid #2b2b2b}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {background-color: #2d2d2d}

.chosen-container .chosen-drop {border: 1px solid #313131;background: #0e0e0e}
.chosen-container .chosen-results {color: #999}
.active-result.result-selected{color: #ccc;background-color: #1b1b1b}
.chosen-container-active.chosen-with-drop .chosen-single {background-image: linear-gradient(#1a2023 20%, #39444a 80%);border: 1px solid #4a4a4a;box-shadow: 0 1px 0 #0a1419 inset;}
.chosen-container-single .chosen-single,.chosen-container-multi .chosen-choices{background: linear-gradient(#1c2a31 20%, #1a2023 50%, #1a2023 52%, #0f1519 100%);border: 1px solid #4a4a4a;box-shadow: none;color: #cacaca;}
.chosen-container-multi .chosen-choices li.search-choice {background-image: none;border: 1px solid #4a4a4a;background-color: #1a2023;color: #cacaca;box-shadow: none;}
input:-webkit-autofill, textarea:-webkit-autofill, select:-webkit-autofill {background-color:#1c2a31!important; color:#fff!important}

.btn-primary {background-color: #253f56;border-color: #31526f;}
.btn-info {background-color: #212a2d;border-color: #393e40}
.btn-info:hover{background-color: #222;border-color: #333}


.popover.fade{filter:invert(.91);box-shadow:none}


a {color: #6c9eb7}
strong {color: #CCC}

.nav .open>a, .nav .open>a:hover, .nav .open>a:focus {background-color: #3e3e3e}
.dropdown-menu {background-color: #232323}
.dropdown-menu>li>a {background: #333;color:#bbb}
.dropdown-menu>li>a:hover {background: #232323;color:#ccc}
.nav-tabs.primary>li.active>a[href="/calculators"]{background:#1a2023;color:#ccc}

.table{color:#8e8e8e}
.table-hover>tbody>tr:hover {background-color:#191919;color:#c5c5c5}   
.table>thead>tr>th {border-bottom: 2px solid #233138}
.table>thead>tr>th, .table>thead>tr>td, .table>tbody>tr>th, .table>tbody>tr>td, .table>tfoot>tr>th, .table>tfoot>tr>td {border-top: 1px solid #233138}

.table-hover tbody > tr:hover > td:nth-child(1) > div:nth-child(1) > img{filter:drop-shadow(0px 0px 5px #5789a2)drop-shadow(0px 0px 10px #376982)}
.table-hover tbody > tr.warning:hover > td:nth-child(1) > div:nth-child(1) > img{filter:drop-shadow(0px 0px 5px #880a00)drop-shadow(0px 0px 8px #993727)}

.table-success, .table-success>th, .table-success>td {background-color:#263238}
.table-hover .table-success:hover>td{background-color: #171717}

.table-warning, .table-warning>th, .table-warning>td {background-color:#291e1d}
.table-hover .table-warning:hover>td{background-color:#231615}

.badge-warning{background-color:#5f3400;color:#dcdcdc}

.centered-image {height: 0px}
.centered-image img{display:none}


footer{border-top:1px solid #233138}
div.calculator {background: #293d48;border: 2px solid #0e0e0e}
div.calculator.link-index a {color: #c7c7c7}
.page-header {border-bottom: 1px solid #000}
.table>thead>tr>td.active, .table>thead>tr>th.active, .table>thead>tr.active>td, .table>thead>tr.active>th, .table>tbody>tr>td.active, .table>tbody>tr>th.active, .table>tbody>tr.active>td, .table>tbody>tr.active>th, .table>tfoot>tr>td.active, .table>tfoot>tr>th.active, .table>tfoot>tr.active>td, .table>tfoot>tr.active>th {background-color: #1b1b1b}
.table-hover>tbody>tr>td.active:hover, .table-hover>tbody>tr>th.active:hover, .table-hover>tbody>tr.active:hover>td, .table-hover>tbody>tr:hover>.active, .table-hover>tbody>tr.active:hover>th{background-color:#101010}

.card{background-color:#272727}
.card-header+.list-group .list-group-item:first-child{background: #212121}

}
