css:
@-moz-document domain("coinmarketcap.com"){
#leaderboard,#header-banner-wrapper,.divider-under-title{display:none!important}
.night-mode .col-lg-2 {display:none}
.night-mode .col-lg-10 {width:100%}
.night-mode .semifluid.container{padding-right:18px}
.night-mode .banner-alert{background-color:#080808;color:#666}

:not(div)::-webkit-scrollbar{width:10px!important;height:10px!important}
:not(div)::-webkit-scrollbar-thumb{background:#323650!important}
:not(div)::-webkit-scrollbar-thumb:hover{background:#43496d!important}
:not(div)::-webkit-scrollbar-corner,::-webkit-scrollbar-track{background:#090909!important}
:not(div)::-webkit-scrollbar-track-piece{background:#171717!important}

:not(div).resize-grip {background: linear-gradient(-45deg, transparent 2px, rgba(255, 255, 255, 0.75) 2px, transparent 3px, transparent 4.5px, rgba(255, 255, 255, 0.63) 5px, transparent 5.5px);background-color:#222;width:10px;height:10px}

.night-mode .vertical-spacer{margin-top:12px}
.night-mode .col-xs-12.text-center{height:34px}
.night-mode .col-xs-12.text-center h1{color:#757dab;font-weight:500}
.night-mode .header-1x{margin:4px 0 8px}

.night-mode .container {width:100%}
body.night-mode {background-color:#0e0e0e}
.night-mode .table {background-color:#0e0e0e}
.night-mode a {color:#838fd6}
.night-mode a:hover {color:#ccc}

.night-mode .navbar.desktop .navbar-brand {margin:0;height:46px;width:224px;}
.night-mode .cmc-logo_color_black, .night-mode .cmc-logo_color_grey{filter:invert(0.75)sepia(1)saturate(2)hue-rotate(200deg);position:relative;left:-22px;transform:scale(0.75)}
.night-mode .cmc-logo_color_black:hover, .night-mode .cmc-logo_color_grey:hover{filter:brightness(0.95)}
.night-mode .navbar-nav>li>a {padding:5px 12px 5px!important;margin:8px 1px 1px!important}
.night-mode .glyphicon.glyphicon-search.form-control-feedback{line-height:28px}
.night-mode .sitenav-block {padding:0}

.night-mode .navbar.desktop .navbar-form{margin-top:8px}
.night-mode .navbar .form-control{padding:0px 12px;height:32px}
.navbar.desktop .navbar-form .has-feedback .form-control-feedback{top:0px}
.night-mode .navbar {background-color:#0c0c0c;border-bottom:1px solid #454b6f;border-top:1px solid #454b6f;box-shadow:none;min-height:20px;margin-bottom:0}
.night-mode .navbar.desktop .navbar-collapse{padding:0}
.night-mode > .container{margin-top:0px;}
.night-mode .bottom-margin-2x {margin-bottom:14px}
.night-mode .nav.navbar-nav.nav-links {padding-top:2px}

.night-mode .form-control{padding:0px 12px;height:26px}

.night-mode .navbar .navbar-nav>li>a{color:#454b6f;border:1px solid transparent;font-size:16px;font-weight:500;border-radius:5px}
.night-mode .navbar .navbar-nav>li>a:hover, .night-mode .navbar .navbar-nav>li>a:focus{background-color:#0c0c0c;border:1px solid #454b6f}
.night-mode .navbar .navbar-nav>.open>a, .night-mode .navbar .navbar-nav>.open>a:hover, .night-mode .navbar .navbar-nav>.open>a:focus {background-color:#0c0c0c;color:#454b6f;border:1px solid #454b6f}

.night-mode .global-stats.desktop.outer-container{background-color:#17181b;border:none;margin-bottom:0px}
.night-mode .global-stats.desktop.outer-container a{transition:color 0.3s}
.night-mode .global-stats.desktop.outer-container a:hover{color:#fff}
.night-mode .nav>li>a {color:#454b6f;transition:background 0.3s,border 0.3s}
.night-mode .nav>li>a:hover{color:#7f8deb;border-color:#7f8deb;background-color:#0c0c0c;}
.night-mode .nav-tabs>li.active>a{background-color:#0c0c0c;border-color:#454b6f;color: #454b6f}
.night-mode .nav-tabs>li.active>a:hover,.night-mode .nav-tabs>li.active>a:focus{color:#7f8deb;background-color:#0c0c0c;border-color:#7f8deb}
.night-mode .nav .open>a,.night-mode .nav .open>a:hover,.night-mode .nav .open>a:focus {background-color: #0c0c0c;border-color: #212435}

.night-mode .nav-tabs{border-bottom:none!important;margin:2px 1px 0px 1px;}
.night-mode .table>tbody>tr>td{padding:6px 8px 6px 8px;box-shadow:inset 0 -1px 0 0 #1f1f2b}
.night-mode .table>tbody>tr>td.more-options-cell.dropdown{padding:0px 8px 0px 8px}
.night-mode .table>tbody>tr>td .btn{padding:0px 8px}
.night-mode .table>tbody>tr>td .btn:not(:hover){border:1px solid transparent}

.night-mode thead tr {border-top:1px solid #43496d}
.night-mode .floating-header thead tr th {background-color: #0c0c0c}
.night-mode #currencies-all > tbody > tr,.night-mode #currencies > tbody > tr{transition:background .2s}
.night-mode #currencies-all > tbody > tr:hover,.night-mode #currencies > tbody > tr:hover{background:#161721}
.night-mode #currencies-all > tbody > tr:hover .logo-sprite,.night-mode #currencies > tbody > tr:hover .logo-sprite{filter:drop-shadow(0px 0px 5px #7f8deb)drop-shadow(0px 0px 10px #7f8deb)}
.night-mode .table thead tr th:after{border-bottom:1px solid #454b6f}
.night-mode table#currencies>tbody>tr>td, .night-mode table#currencies-all>tbody>tr>td, .night-mode table#assets>tbody>tr>td, .night-mode table#assets-all>tbody>tr>td{border-bottom:none}

.night-mode .btn,.night-mode .btn-primary{transition:background-color .7s}
.night-mode .btn.btn-primary:hover,.night-mode .btn.btn-primary:focus,.night-mode .quick-search-container button:hover,.night-mode .quick-search-container button:focus{background-color:#0c0c0c;color:#7f8deb;border-color:#7f8deb}
.night-mode .form-control,.night-mode .btn,.night-mode .btn-primary,.night-mode .pagination>li>a, .night-mode .pagination>li>span,.night-mode select.form-control+.chosen-container.chosen-container-single .chosen-single,.night-mode select.form-control+.chosen-container .chosen-search input[type=text],.night-mode select.form-control+.chosen-container .chosen-drop{border:1px solid #454b6f;color: #454b6f}
.night-mode .form-control:hover,.night-mode .btn:hover,.night-mode .btn-primary:hover,.night-mode .pagination>li>a:hover, .night-mode .pagination>li>span:hover,.night-mode select.form-control+.chosen-container.chosen-container-single .chosen-single:hover,.night-mode select.form-control+.chosen-container .chosen-search input[type=text]:hover,.night-mode select.form-control+.chosen-container .chosen-drop:hover,.night-mode select.form-control+.chosen-container.chosen-container-single.chosen-container-active .chosen-single,.night-mode .btn-default.active:hover,.night-mode .btn-group>.btn:hover,.night-mode .btn-group.open .btn:not(:hover){border:1px solid #7f8deb;color:#7f8deb;background-color:#0c0c0c}
.night-mode #currency-switch-button{background-color:#1b1c20}
.night-mode #currency-switch-button:hover{background-color:#0c0c0c}


.night-mode hr{border:.5px solid #454b6f}

.night-mode #title a{color:#454b6f;font-size:28px;font-weight:500;transition:color .2s}
.night-mode #title a:hover{color:#7f8deb}
.night-mode .dropdown-menu>li>a, .night-mode .global-stats.desktop.outer-container .dropdown-menu>li>a,.night-mode select.form-control+.chosen-container.chosen-container-single .chosen-single{color:#454b6f;font-size:16px;font-weight:500}
.night-mode .dropdown-menu .divider{background-color:#454b6f}
.night-mode .quick-search-container button{padding:6px 11px}
.night-mode .btn,.night-mode .btn-primary, .night-mode .pagination>li>a, .night-mode .pagination>li>span,.night-mode .nav-tabs>li>a {font-size:14px;font-weight: 500}
.night-mode .dropdown-menu {border-color: #454b6f}
.night-mode .alert-warning {background-color: #181a25;border-color:#282a31;color:#6c75b5}

.night-mode .dataTable td.sorting_1{color:#666;border-left:1px solid #161721;border-right:1px solid #161721}
.night-mode .sorting_asc, .night-mode .sorting_desc, .night-mode #th-name.sorting_asc, .night-mode #th-pair.sorting_asc, .night-mode #th-source.sorting_asc, .night-mode #th-name.sorting_desc, .night-mode #th-pair.sorting_desc, .night-mode #th-source.sorting_desc, .night-mode #th-platform.sorting_asc, .night-mode #th-symbol.sorting_asc, .night-mode #th-platform.sorting_desc, .night-mode #th-symbol.sorting_desc{border-left:1px solid #1b1b1b;border-right:1px solid #1b1b1b}


.night-mode .coinmarketcap-currency-widget>div {background-color: #181a23;color: #a7a7a7;border: 1px solid #3f4671!important}
.night-mode .coinmarketcap-currency-widget>div div{border-color:#3f4671!important}

.night-mode .nav-tabs {border-bottom: 1px solid #454b6f}
.night-mode .coin-summary-item h3 {border-top:1px solid #454b6f;border-bottom:1px solid #454b6f}

.night-mode [class=" highcharts-button-box"]{fill: #191919}
.night-mode [class="highcharts-label-box"]{stroke:#454b6f!important}
.night-mode [class="highcharts-button highcharts-button-normal"] text{fill:#4e5790!important}
.night-mode [class="highcharts-button highcharts-button-hover"] text{fill:#fff!important}
.night-mode [class="highcharts-button highcharts-button-pressed"] text{fill:#7f8deb!important}
.night-mode [class="highcharts-grid highcharts-yaxis-grid "] [class="highcharts-grid-line"]{stroke:#454b6f!important}
.night-mode [class="highcharts-grid highcharts-xaxis-grid "] [class="highcharts-grid-line"]{stroke:#454b6f!important;opacity:.15!important}
.night-mode [class="highcharts-grid highcharts-xaxis-grid highcharts-navigator-xaxis"] [class="highcharts-grid-line"]{stroke:#454b6f!important;opacity:.35!important}
.night-mode [class="highcharts-scrollbar-track"]{fill:#454b6f!important;stroke:#222}
.night-mode [class="highcharts-scrollbar-thumb"],.night-mode [class="highcharts-navigator-handle highcharts-navigator-handle-left"],.night-mode [class="highcharts-navigator-handle highcharts-navigator-handle-right"]{fill:#151515!important;stroke:#333}
.night-mode [class="highcharts-scrollbar-rifles"]{stroke:#7b89e0!important}
.night-mode [class="highcharts-scrollbar-button"]{fill: #191919;stroke:#222}
.night-mode [class="highcharts-navigator-outline"]{stroke:#454b6f!important}
.night-mode [class="highcharts-axis-line"],.night-mode [class="highcharts-tick"]{stroke:#454b6f!important}

.night-mode .table-responsive{border:1px solid #14161f}
.night-mode .border{border:1px solid #454b6f}



}
