	animation: slideforiconout .3s ease;
}
@keyframes snapslide {
	0% {
		left: -300px;
		opacity: 0;
	}
	10% {
		left: 50px;
		opacity: 0;
	}
	100% {
		left: 4rem;
		opacity: 1.0;
	}
}
@keyframes snapslideout {
	0% {
		left: 4rem;
		opacity: 1.0;
	}
	90% {
		left: 50px;
		opacity: 0;
	}
	100% {
		left: -300px;
		opacity: 0;
	}
}
@keyframes slideforiconout {
	0% {
		left: 307px;
		opacity: 1.0;
	}
	90% {
		left: 300px;
		opacity: 0;
	}
	100% {
		left: -300px;
		opacity: 0;
	}
}
@keyframes slideforicon {
	0% {
		left: -300px;
		opacity: 0;
	}
	10% {
		left: 300px;
		opacity: 0;
	}
	100% {
		left: 307px;
		opacity: 1.0;
	}
}
