css:
@-moz-document domain("roblox.com") {
/*  Theme created by kickedbyconsole, if you want, feel free to contact me on Discord: kickedbyconsole#6717  */
	

/*  Logo */


      .icon-logo {
          background-image: url("https://i.imgur.com/m1mp1JM.png")
      																}

      .icon-logo-r {
          -webkit-filter: grayscale(100%)
      															}


/*  Colors */



	.rbx-header {
                         background-color:#a8a8a8              }

	.btn-secondary-xs {
                              background-color:#a8a8a8;
                                        border-color:#a8a8a8 }

	.notification-blue {
                               background-color:#a8a8a8         }

	.btn-secondary-md {
                             background-color:#a8a8a8;
                                        border-color:#a8a8a8 }

	.btn-primary-md {         
                                    background-color:#a8a8a8;
                                        border-color:#a8a8a8 }
    
	.chat-main .chat-header {     
                                            background-color:#a8a8a8 }

	.dialog-container .dialog-header {
                                 background-color:#a8a8a8  }

	.dialog-container 
	.dialog-message-container 
	.dialog-message { 
                              background-color:#a8a8a8;
                                            border-color:#a8a8a8;
                                            	border: 1px solid #a8a8a8 }

	.dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:before {
                                            border-color: rgba(0, 0, 0, 0) }

	#CreateGroupBtn {
                               background-color: #a8a8a8;
                               border-color: #a8a8a8;
                                        }

	.btn-disabled-primary 
							 {
                                      background-color: #444444;
                               border-color: #444444;      
                                        }



	.text-name, .text-name:link, .text-name:hover, .text-name:visited, .text-name:active {
                           color:#a8a8a8                 
                                        }

	.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
                                        box-shadow:0 -4px 0 0 #a8a8a8 inset  
                                        }

	.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
                                        /*box-shadow:0 -4px 0 0 #a8a8a8 inset*/
                                            box-shadow:none
                                        }

	.btn-primary-lg  {
                                          background-color:#a8a8a8;
                                          border-color:#a8a8a8;
                                        }

	.btn-primary-xs {
                               background-color:#a8a8a8;
                               border-color:#a8a8a8;
                                        }

	.btn-secondary-xs:visited {
                                      background-color:#a8a8a8;      
                                        }

	.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited {
                                        border-color: rgba(0, 0, 0, 0) ;
                                        background-color: rgba(0, 0, 0, 0); 
                                        }
	
	.icon-nav-home {
	-webkit-filter: invert(100%)  
	}
	
	.icon-nav-profile {
	   -webkit-filter: invert(100%)   
	}
	
	.icon-nav-message {
	   -webkit-filter: invert(100%)   
	}
	
	.icon-nav-friends {
	   -webkit-filter: invert(100%)   
	}
	
	.icon-nav-charactercustomizer {
	   -webkit-filter: invert(100%)   
	}
	
	.icon-nav-inventory {
	   -webkit-filter: invert(100%)   
	}
	
	.icon-nav-trade {
	   -webkit-filter: invert(100%)   
	}

	.icon-nav-group {
 	  -webkit-filter: invert(100%)   
	}

	.icon-nav-forum {
 	  -webkit-filter: invert(100%)   
	}

	.icon-nav-blog {
   	-webkit-filter: invert(100%)   
	}

	.icon-nav-shop {
	   -webkit-filter: invert(100%)   
	}
    .rplus-icon {
 	-webkit-filter: invert(100%)   
	}

	.profile-avatar-right {
                                background-color:#444444            
                                        }

	.slide-switcher .slide-item-container .slide-item-container-left {
                                        background-color:#444444    
                                        }

	.input-group-btn .input-dropdown-btn:hover, .input-group-btn .input-dropdown-btn:focus {
                                         background-color:#a8a8a8;
                               border-color:#a8a8a8;   
                                        }
	
	.open .input-dropdown-btn {
                                background-color:#a8a8a8;            
                                        }

/*  Fade-in, blur & shadows */

	
	body {
                                            -webkit-animation: fadein 2s;
                                            
                                            -moz-animation: fadein 2s;
                                            
                                            -ms-animation: fadein 2s;
                                            
                                            -o-animation: fadein 2s;
                                            
                                            animation: fadein 2s;
                                            
                                        }

	body {
                                            -webkit-animation: blur 2s;
                                            
                                            -moz-animation: blur 2s;
                                            
                                            -ms-animation: blur 2s;
                                            
                                            -o-animation: blur 2s;
                                            
                                            animation: blur 2s;
                                            
                                        }

	.dropdown-menu {
                            animation: fadein 0.5s; 
                            box-shadow: 0px 0px 100px;
                                        }

	.roblox-popover-content.bottom .notification-stream-wrap.open {
                                      box-shadow: 0px 0px 100px;      
                                        }

	.chat-main {
                             box-shadow: 0px 0px 100px;                
                                        }

	@keyframes blur {
                              from {
                              filter: blur(25px);
                              }
                                            to {
                                            filter: blur(0px)
                                            }
                                        }

	.menu-vertical .menu-option .menu-secondary-option  {
                                           animation: fadein 0.5s;  
                                        }

	.menu-secondary-container {
                                    animation: fadein 0.5s;         
                                        }

	.tooltip-container {
                            -webkit-filter: grayscale(100%)
                                        }

	.upvote {
                         -webkit-filter: grayscale(100%)                   
                                        }

	.downvote {
                       -webkit-filter: grayscale(100%)                          
                                        }

	.vote-numbers {
                       -webkit-filter: grayscale(100%)                     
                                        }

	.navbar-icon-item {
                            -webkit-filter: grayscale(100%)
                                        }

	#header {
    		opacity: 1;
    		box-shadow: 0px 0px 100px;
    		/*display: none;*/
			}

	.rbx-left-col {  
 			box-shadow: 0px 0px 100px;
    		}

	@keyframes fadein {
                              from {
                              opacity: 0;
                              }
                                            to {
                                            opacity: 1
                                            }
                                        }

	@-moz-keyframes fadein {
                              from {
                              opacity: 0;
                              }
                                            to {
                                            opacity: 1
                                            }
                                        }

	@-webkit-keyframes fadein {
                              from {
                              opacity: 0;
                              }
                                            to {
                                            opacity: 1
                                            }
                                        }

	@-o-keyframes fadein {
                              from {
                              opacity: 0;
                              }
                                            to {
                                            opacity: 1
                                            }
                                        }

	@keyframes fadeout {
                              from {
                              opacity: 1;
                              }
                                            to {
                                            opacity: 0
                                            }
                                        }



/*  Adblock */


	.ad,
	.ad *,
	.img_ad,
	.ad-annotations,
	#google_image_div,
	#aw0,
	#AdvertisingLeaderboard {
                                      display: inherit !important;
                                            filter: blur(0px);
                                        }

	.ad,
	.ad *,
	.img_ad,
	.ad-annotations,
	#google_image_div,
	#aw0,
	#AdvertisingLeaderboard {
                                      display: inherit !important;
                                            filter: blur(0px);
                                        }


/*  Animations */


.chat-container.collapsed .chat-main,
.chat-main,
.dialog-container,
.dialog-container.collapsed {
    transition: height 1s
}

.chat-friend-list .chat-friend-container,
.input-group-btn .dropdown-menu li a {
    transition: box-shadow 1s
}

.game-card-container {
     transition: height 0.5s, top 0.5s;
    	height: 200px;
    		overflow: hidden;
                position: relative;
    				top: 25px
}

	.game-card-container:hover {
 		   transition: height 1s, top 1s;
 			   height: 107%;
 				   top: 0px;
 					   box-shadow: 7px 7px 25px
					}

		#navigation {
				 transition: width 1s   
							}

}
