css:
@-moz-document domain(devforum.roblox.com) {    
    .logo-big {
    	content: url("https://i.imgur.com/oz4zCgc.png") !important;
    }
    .logo-small {
    	content: url("https://discourse-cdn-sjc1.com/business/uploads/roblox/original/3X/9/4/947164a4774c8d55ef04dac214aefba7a1e41fc0.png") !important;
    }

    .d-header {
    	background-color: #24292e !important;
    }
    .d-header-icons .unread-notifications {
    	background-color: #4697c5;                                        
    }

    .btn-flat:after {
        width: 0% !important;
        border-top: 0px !important;
    }
    .drop-down-visible .d-header-icons .active .icon:after {
        width: 0% !important;
        border-top: 0px !important;
    }
    .d-header-icons .icon {
    	color: #c8c9ca !important;    
    }
    .icon:hover {
    	background-color: #353C44 !important;    
    }
    .drop-down-visible .d-header-icons .active .icon {
        background-color: #353C44 !important;  
        border-color: rgba(0,0,0,0) !important;
    }

    .menu-panel {
        background-color: #353C44 !important;    
        border-radius: 2px 2px;
        border-color: rgba(0,0,0,0) !important;
    }

    .menu-panel .badge-wrapper.bullet span.badge-category {
    	color: #B9BABC !important;
    }

    #search-term {
        background-color: #3f4448 !important;
        color: #e4e4e4 !important;
    }

    .search-link .blurb {
    	color: #B9BABC !important;
    }
    .search-link .blurb .search-highlight {
    	color: #ececec !important;
    }
    .search-menu li:not(.category):not(.heading) a .user-results {
    	color: #B9BABC !important;
    }
    .menu-panel ul.menu-links li a:hover {
    	background-color: #24292e !important;
    }
    .menu-panel ul li.heading a:hover {
    	background-color: #24292e !important;
    }
    div.menu-links-header a:hover {
    	background-color: #24292e !important;
    }
    .search-menu li:not(.category):not(.heading) a:hover {
    	background-color: #24292e !important;
    }

    .user-menu .notifications .read  {
        background-color: #353C44 !important;
    }

    .user-menu .notifications li span {
        color: #B9BABC !important;
    }
    .user-menu .notifications li:hover {
        background-color: #24292e !important;   
    }
    .user-menu .notifications li  {
        background-color: #515D6B !important;
    }
    .user-menu .notifications .show-all .btn {
        color: #646464;
        background: #4B5054;
    }
    .user-menu .notifications .show-all .btn:hover {
        background: #24292e;
    }
    .menu-panel hr {
        border-top: 1px solid #636363;
    }

    .search-menu .discourse-tag.simple {
        color:  #B9BABC !important;
        background-color: rgba(0,0,0,0) !important;
    }

    .extra-info-wrapper .topic-link {
    	color: #a0a0a0;
    }
    .extra-info-wrapper .topic-statuses i {
    	color: #a0a0a0;                                   
    }
    .extra-info-wrapper .badge-wrapper.bullet span.badge-category {
    	color: #a0a0a0;
    }

    .search-menu .search-context label {
    	color: #B9BABC;   
    }
    .search-menu .no-results {
    	color: #B9BABC;   
    }

    .search-menu .autocomplete a {
    	background-color: #353C44;
    }
    .search-menu .autocomplete a.selected {
    	background-color: #6b6b6b;
    }
    .search-menu .autocomplete li {
    	border-bottom: 1px solid #636363;
    }
    .search-menu .autocomplete  {
    	border: 1px solid #636363
    }
}
