css:
@-moz-document url-prefix("https://2sucres.org") {
/* ==UserStyle==

@name Thème New Retro Wave
@version 0.0
@description Theme New Retro Wave pour 2sucres by Chef aka FoutreAuMiel 
@author FoutreAuMiel

 ==UserStyle== */

div.sb-site-container
{
    background-image: url("https://image.noelshack.com/fichiers/2018/02/4/1515671909-wallhaven-527219.jpg");
    background-repeat: no-repeat;
}

#forum-container
{
    background: none;
}

#forum-container
{
    font-family: Graublau Web! important;
    /*---Partie à mettre en commentaire pour enlever le fond rosé/violet---*/
    /* 
    
    animation: shadowColor 30s linear infinite;
    -webkit-animation: shadowColor 30s linear infinite;
    -moz-animation: shadowColor 30s linear infinite;
    -o-animation: shadowColor 30s linear infinite;
    /*---Partie à mettre en commentaire pour enlevé le fond rosé/violet---*/
}
/*Principal*/

@font-face 
{
    font-family: 'Graublau Web';
    src: url('GraublauWeb.eot');
    src: local('☺︎'), url("GraublauWeb.woff") format("woff"), url("GraublauWeb.otf") format("opentype"), url("GraublauWeb.svg#grablau") format("svg");
}

@keyframes shadowColor
{
    0%
{
        box-shadow: 0px 0px 50px 50px rgba(90, 10, 120, 0.7);
        background: rgba(90, 10, 120, 0.7);
    }
    50%
{
        box-shadow: 0px 0px 50px 50px rgba(160, 10, 95, 0.7);
        background: rgba(160, 10, 95, 0.7);
    }
    100%
{
        box-shadow: 0px 0px 50px 50px rgba(90, 10, 120, 0.7);
        background: rgba(90, 10, 120, 0.7);
    }
}

@-webkit-keyframes shadowColor
{
    0%
{
        box-shadow: 0px 0px 50px 50px rgba(90, 10, 120, 0.7);
        background: rgba(90, 10, 120, 0.7);
    }
    50%
{
        box-shadow: 0px 0px 50px 50px rgba(160, 10, 95, 0.7);
        background: rgba(160, 10, 95, 0.7);
    }
    100%
{
        box-shadow: 0px 0px 50px 50px rgba(90, 10, 120, 0.7);
        background: rgba(90, 10, 120, 0.7);
    }
}
/*header*/

#navbar > ul > li > a
{
    letter-spacing: 1px;
}

.ms-header.ms-header-primary
{
    background: rgb(90, 0, 120);
    min-height: 80px;
    max-height: 780px;
}

.ms-navbar
{
    box-shadow: none;
    background-color: rgba(90, 10, 120, 0.7);
}

.container.container-full div h1
{
    margin-top: 0px;
}

.navbar.yamm.ms-navbar.ms-navbar-primary.shrink.navbar-fixed-top
{
    background-color: rgba(90, 0, 120, 1);
}

md-progress-linear.md-accent .md-container
{
    background: none;
}

md-progress-linear.md-accent .md-bar
{
    background: rgba(160, 10, 95, 0.7);
}

header .container.container-full div
{
    margin-top: 4px;
}

header .container.container-full .header-right
{
    margin-top: 20px !important;
}

header .container.container-full .pull-right.mt-2.ml-2
{
    margin-top: 5px !important;
}

.navbar.yamm.ms-navbar.ms-navbar-primary.navbar-static-top::after
{
    display: none
}

.ms-header .ms-title a img
{
    display: none
}

.ms-header .ms-title h1
{
    margin: 20px 0px 0px 0px
}

.ms-header .ms-title h1 span span::before
{
    color: rgba(220, 150, 255, 0.5);
    text-shadow: 3px 4px 0 rgba(255, 0, 150, 0.9), 3px -1px 0px #707070;
    margin-top: -100px;
    font-size: 70px!important;
    content: "2S" !important;
    font-family: impact;
}

.ms-header .ms-title h1 span span::after
{
    color: rgba(200, 150, 250, 0.5);
    text-shadow: 3px 4px 0 rgba(255, 0, 150, 0.9), 3px -1px 0px #707070;
    font-size: 47px!important;
    content: "UCRES" !important;
    font-family: impact;
}

.list-topics > thead > tr > th.selected,
table.list-topics thead tr,
.toolbar-msg,
.toolbar-msg .md-label,
md-toolbar.toolbar-msg
{
    background: rgb(50, 10, 170) !important;
    font-size: 18px;
    color: rgba(200, 150, 250, 0.5) !important;
    text-shadow: 2px 2px 0 rgba(255, 0, 180, 0.5);
    font-family: impact;
}
/*liste topics*/

.list-topics > tbody > tr.even
{
    background: rgba(150, 0, 75, 0.8);
}

.list-topics > tbody > tr
{
    background: rgba(75, 0, 100, 0.8);
}

.list-topics > tbody > tr td.topic-name
{
    text-indent: -1px;
    word-spacing: 0px !important;
    letter-spacing: 0px;
    font-size: 15px !important;
}

.one-topic .topic-name a
{
    color: rgb(240, 230, 240)
}

.one-topic .topic-name a:visited
{
    color: rgb(150, 140, 150)
}

.one-topic:hover .topic-name a,
.one-topic:hover .topic-date a
{
    color: rgb(240, 230, 240);
}

table.list-topics thead tr th
{
    padding-left: 0px !important
}

table.list-topics thead tr th.topic-nb-message,
table.list-topics thead tr th.topic-connected-count
{
    padding-left: 14px !important
}

.topic-date
{
    max-width: 65px;
}

.list-topics > tbody > tr.even:hover
{
    background-color: rgba(120, 0, 55, 0.8);
}

.list-topics > tbody > tr:hover
{
    background-color: rgba(55, 0, 80, 0.8);
}

.list-topics > tbody > tr.even:active,
.list-topics > tbody > tr.even:focus,
.list-topics > tbody > tr.even:hover
{
    -webkit-animation: pulseEvent 3s linear 1s infinite;
    animation: pulseEvent 3s linear 1s infinite;
    color: white;
}

.list-topics > tbody > tr:hover,
.list-topics > tbody > tr:focus,
.list-topics > tbody > tr:active
{
    -webkit-animation: pulse 5s linear 1.5s infinite;
    animation: pulse 5s linear 1.5s infinite;
    color: white;
}

@-webkit-keyframes pulseEvent
{
    50%
{
        background: rgba(160, 0, 85, 0.9);
    }
}

@keyframes pulseEvent
{
    50%
{
        background: rgba(160, 0, 85, 0.9);
    }
}

@-webkit-keyframes pulse
{
    50%
{
        background: rgba(100, 0, 130, 0.9);
    }
}

@keyframes pulse
{
    50%
{
        background: rgba(100, 0, 130, 0.9);
    }
}
/* Colonne de droite */

.list-topics-container,
#form-send-new-message,
#form-send-new-messag md-toolbar,
#forum-right-col aside
{
    border-radius: 0%;
    border: 4px solid black;
}

div.col-lg-4.col-md-3.col-xs-12.no-pr md-list md-list-item:nth-child(2n+1)
{
    background-color: rgba(150, 0, 75, 0.8) !important;
}

div.col-lg-4.col-md-3.col-xs-12.no-pr md-list md-list-item:nth-child(2n)
{
    background-color: rgba(75, 0, 100, 0.8) !important;
}

div.col-lg-4.col-md-3.col-xs-12.no-pr md-list md-list-item:nth-child(2n+1):hover
{
    background-color: rgba(120, 0, 55, 0.8) !important;
    color: white;
}

div.col-lg-4.col-md-3.col-xs-12.no-pr md-list md-list-item:nth-child(2n):hover
{
    background-color: rgba(55, 0, 80, 0.8)!important;
    color: white;
}

table.list-topics thead tr
{
    border-radius: 0%;
    border-top: 4px solid black;
    border-bottom: 4px solid black;
}

#forum-right-col aside:nth-of-type(1)
{
    margin-top: 0 !important
}

.list-topics.table
{
    margin: 0
}

.list-topics-container h1
{
    display: none;
}

.list-topics-container md-toolbar._md
{
    background-color: rgb(90, 10, 170) !important;
    margin: 0 !important;
}

.col-xs-12.pl-1.pr-1
{
    background-color: rgba(90, 10, 120, 0.7);
    ;
    padding: 5px 0px 5px 0px !important;
    box-sizing: content-box;
}

.col-xs-12.pl-1.pr-1:nth-of-type(2)
{
    border-left: 4px solid black;
    border-right: 4px solid black;
    width: 100%;
    box-sizing: content-box;
    left: -4px;
}

.refresh-topic-list-btn
{
    position: absolute;
    left: 75px;
}
/* Navigation pages */

div.pl-1:nth-child(3) > nav:nth-child(1),
div.pl-1:nth-child(5) > nav:nth-child(1)
{
    text-align: right !important;
    margin-right: 300px;
}

#forum-right-col aside,
.forum-aside-content.smoothy-1.no-mb.no-mt.flex-100,
#forum-right-col aside div md-list-item,
#forum-right-col aside div md-list
{
    background: none;
}

md-list-item .md-no-style.md-button
{
    line-height: 15px;
}

md-list-item div.md-button.md-no-style
{
    padding-right: 2px !important;
}

div.md-secondary-container button
{
    border-radius: 0 !important;
}

div.md-secondary-container button:hover
{
    background-color: red !important;
}

.md-button:not([disabled]):hover
{
    background: none
}
/*liens*/

div.message-content p a
{
    color: rgb(0, 107, 215);
}

div.message-content p a:visited
{
    color: rgb(50, 107, 180);
}

div.message-content p a:hover
{
    color: rgb(255, 64, 0);
}
/*Postes*/

article.row.topic-message.mt-2.no-ml.no-mr.odd
{
    background-color: rgba(90, 10, 120, 0.8);
    border: none;
    box-shadow: 0px 0px 4px 2px rgba(160, 10, 95, 0.8);
}

article.row.topic-message.mt-2.no-ml.no-mr
{
    background-color: rgba(140, 10, 95, 0.8);
    border: none;
    color: rgba(255, 255, 255, 0.7) !important;
    box-shadow: 0px 0px 2px 2px rgba(90, 10, 120, 0.8);
}

div.forum-aside-content
{
    background-color: rgb(90, 10, 170);
}

div.forum-aside-content md-list-item:hover,
div.forum-aside-content md-switch:hover
{
    margin-right: 0;
    background-color: rgb(100, 20, 180)!important;
}

.forum-aside-content
{
    padding: 0 !important;
}

.forum-aside-content md-switch
{
    margin: 0 !important;
    padding-left: 10px;
}

blockquote,
textarea,
.topic-message
{
    color: rgba(255, 255, 255, 0.8) !important;
}

#form-send-new-message
{
    background-color: rgba(90, 10, 120, 0.7);
}

.ms-footer
{
    background-color: rgb(90, 10, 170);
}

.md-toolbar-tools,
div.risibank-menu.col-xs-12.pt-1.pb-1
{
    background-color: rgb(90, 10, 170) !important;
}

.topic-message.message-preview.smoothy-2.mt-2.no-ml.no-mr
{
    background-color: rgb(90, 10, 170) !important;
    border-radius: 0%;
    border: 4px solid black;
}
/*Boutons merci à Victime*/

.md-button.md-default-theme.md-hue-1.md-accent.md-raised,
.md-button.md-hue-1.md-accent.md-raised,
.md-button.md-default-theme.md-hue-1.md-accent.md-fab,
.md-button.md-hue-1.md-accent.md-fab,
.md-button.md-default-theme.md-hue-1.md-fab,
.md-button.md-hue-1.md-fab,
.md-button.md-default-theme.md-accent.md-raised,
.md-button.md-accent.md-raised,
.md-button.md-default-theme.md-accent.md-fab,
.md-button.md-accent.md-fab,
md-switch.md-default-theme.md-hue-1.md-checked .md-thumb,
md-switch.md-hue-1.md-checked .md-thumb,
md-switch.md-default-theme.md-checked .md-thumb,
md-switch.md-checked .md-thumb,
.sucre-pagi > li.active > a
{
    background-color: rgb(100, 60, 210);
    color: #fff;
}

.md-button.md-default-theme.md-hue-1.md-accent.md-raised:not([disabled]):hover,
.md-button.md-hue-1.md-accent.md-raised:not([disabled]):hover,
.md-button.md-default-theme.md-hue-1.md-accent.md-fab:not([disabled]):hover,
.md- button.md-hue-1.md-accent.md-fab:not([disabled]):hover,
.md-button.md-default-theme.md-hue-1.md-fab:not([disabled]):hover,
.md-button.md-hue-1.md-fab:not([disabled]):hover,
.md-button.md-default-theme.md-accent.md-raised:not([disabled]):hover,
.md-button.md-accent.md-raised:not([disabled]):hover,
.md-button.md-default-theme.md-accent.md-fab:not([disabled]):hover,
.md-button.md-accent.md-fab:not([disabled]):hover
{
    background-color: rgb(90, 10, 170);
    color: #fff;
}

md-switch.md-hue-1 .md-thumb
{
    background-color: rgb(150, 100, 250);
}

md-switch[aria-checked="false"] .md-bar
{
    background-color: rgb(110, 70, 220);
}

md-switch.md-default-theme.md-hue-1.md-checked .md-bar,
md-switch.md-hue-1.md-checked .md-bar,
md-switch.md-default-theme.md-checked .md-bar,
md-switch.md-checked .md-bar
{
    background-color: rgb(110, 40, 170);
}

a.md-raised.md-accent.md-hue-1.md-button.md-ink-ripple.flex
{
    background-color: rgb(100, 60, 210);
}

a.md-raised.md-accent.md-hue-1.md-button.md-ink-ripple.flex:hover
{
    background-color: rgb(90, 10, 170);
}
/* FIXES */

textarea
{
    resize: vertical !important;
}

.message-avatar-container,
.message-avatar,
.message-avatar.ng-scope
{
    border: none;
    background: none;
}

div.risibank-stickers-container.smoothy-2.col-xs-12
{
    overflow: auto;
}

.risibank-stickers-container.smoothy-2
{
    height: 110px;
    min-height: 110px;
    overflow: auto;
    resize: vertical;
    max-height: 666px;
}

.risibank-stickers-container.smoothy-2 .risibank-stickers-container
{
    height: 100%;
    max-height: 666px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 img
{
    margin: 7px 2px 2px 2px!important;
    border: 1px solid transparent;
    height: 44px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12
{
    padding: 0;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 img:hover
{
    border: 1px solid black;
    background: rgba(160, 10, 95, 0.7);
    height: 44px;
}

div.form-group.pl-1.mr-1
{
    margin-top: 2px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 a span img
{
    height: 14px;
    width: 14px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 a span img:hover
{
    height: 14px;
    width: 14px;
}

aside.profile-section:nth-child(2) > md-list:nth-child(2)
{
    overflow-y: scroll;
    height: 500px;
}

.dropdown.open
{
    background-color: rgb(130, 10, 95) !important;
}

.dropdown-menu .ms-menu-double ul,
.ms-menu-double-main-menu
{
    background: none;
}

.dropdown-menu .ms-menu-double ul li,
.dropdown-menu .ms-menu-double ul div
{
    color: white!important;
    background-color: rgb(130, 10, 95)
}

li.active a
{
    background-color: rgb(140, 20, 105) !important;
}
}

@-moz-document regexp("https:\\/\\/2sucres\\.org\\/forums\\/[0-9]-[1-9][0-9]+-[1-9]+.+") {
/*Topic*/
/*regex pour savoir si on est sur un topic ou pas : "https:\/\/2sucres\.org\/forums\/[0-9]-[1-9][0-9]+-[1-9]+.+"  */

.col-xs-12.pl-1.pr-1
{
    background: none;
}

.forum-view .topic-title,
.forum-view .topic-title span,
#bot-pagi
{
    background: rgb(50, 10, 170) !important;
    font-size: 20px;
    color: rgba(200, 150, 250, 0.5) !important;
    text-shadow: 1px 1px 0 rgba(255, 0, 180, 0.5);
    font-family: impact;
    height: 60px;
}

.forum-view .topic-title,
#bot-pagi
{
    height: 40px;
    border: 4px black solid;
}

.forum-view .topic-title
{
    text-align: center;
    padding: 5px 0px 5px 0px;
}

#bot-pagi div nav ul li a:hover
{
    background-color: rgba(140, 20, 105, 0.72) !important;
    padding: 12px 10px 8px 10px;
}

.sucre-pagi > li.active > a
{
    padding: 12px 10px 8px 10px;
}
}

@-moz-document url-prefix("https://2sucres.org/messagerie-privee") {
.ms-paper-menu-left{
background : none ! important;
}
.ms-paper-content{
background-color : rgba(0,0,0,0.5)!important;
}
#new-message{color : black !important}
}
