css:
@-moz-document domain("addictedtoprogramming.blogspot.com") {
/*background*/
body {
    background-color: #1C1C1C !important;
}

/*post text*/
.post-body{
color: #C3C3C3 !important;
}

/*post tl;dr (right)*/
.centered-top-container,
.centered-top-placeholder {
    background: #2c2c2c none repeat scroll top left !important;
}
.hero-post-noimage-snippet .snippet-item {
    
    line-height: 20px!important;
    max-height: 162px!important;
    max-width: 838px!important;

}

.heroPost .big-post-text {
    background-color: #2C2C2C !important;
    padding: 25px !important;
    margin: 14px auto !important;
}

/*post tl;dr text*/
.post-body {
    color: #adadadd6 !important;
    font: 400 17px Lora, serif !important;
    margin-bottom: 10px !important;
}

/*title post (left)*/
.heroPost .big-post-title {
    background-color: #2c2c2c!important;
    padding-bottom: 20px!important;
    padding-right: 35px!important;
    padding-left: 35px!important;
    padding-top: 20px!important;
    margin-top: 14px!important;
    margin-right: 12px!important;
}
.heroPost.noimage .big-post-title{
    max-height: 207px!important;
    max-width: 430px!important;
    height: fit-content!important;
}
/*title post text (left)*/
.heroPost .big-post-title h3 a {
    color: #ececec!important;
}


.post-title,
.post-title a {
    color: #f3f3f3!important;
    font: 700 20px Open Sans, sans-serif!important;
}

/*blog name*/
.blog-name h1,
.blog-name h1 a {
    color: #e6e6e6b9!important;
    font: 700 34px Open Sans, sans-serif!important;
    line-height: 24px!important;
}

/*top bar*/
.centered-top {
    max-width: 1600px;
    padding: 9px 456px 0px 136px;
}

/*SEARCH button*/
.centered-top .flat-button {
    color: #dedede !important;
}

/*sidebar on right*/
.sidebar-container {
    background-color: #2c2c2c !important;
    margin-top: 14px !important;
    max-width: 300px !important;
    z-index: 32 !important;
}

/*sidebar text colour*/
.sidebar-container .widget .title {
    color: #b9b9b9!important;
}

/*three dots background*/
.hero-post-noimage-snippet .snippet-fade {
    background: #00000000!important;
    color: #25a186!important;
    height: 30px!important;
}

/*post list*/
.post-outer .snippet-thumbnail{
    position:fixed!important;
    height:0px!important;
    width:0px!important;
}
.post-content {
    background-color: #2c2c2c!important;
    padding-bottom: 25px!important;
    padding-right: 25px!important;
    padding-left: 25px!important;
    padding-top: 25px!important;
    margin-right: 0px!important;
    margin-left: 305px!important;
    max-width: 838px!important;
}
.post-snippet .snippet-fade {
    background: #2c2c2c!important;
}
.post-snippet .snippet-item {
    line-height: 20px!important;
}
/*the newewst posts*/
.feed-message {
    color: #dadada!important;
    font: 600 27px Open Sans, sans-serif!important;
    margin-bottom: 23px!important;
    margin-left: 305px!important;
}
.post-header-container .byline, .post-header-container .byline a {
    color: #dedede!important;
    font: 600 14px Open Sans, sans-serif!important;
}
/*comment space*/
#allHolder{
    background-color: #2c2c2c!important;
}
/*post opened*/
body.item-view .Blog .post-title-container {
    background-color: #25a18673!important;
    margin-bottom: 0px!important;
    padding-bottom: 10px!important;
    padding-right: 10px!important;
    padding-left: 10px!important;
    padding-top: 10px!important;
}
body.item-view .Blog .post-body-container {
    padding-right: 25px!important;
    position: relative!important;
    margin-left: 40px!important;
    margin-top: 35px!important;
}
body.item-view .Blog .post-body {
    margin-bottom: 0!important;
    margin-right: 40px!important;
}
/*share button when post opened*/
body.item-view .Blog .post-body-container .post-sidebar {
    width: 70px;
    height: 20px;
}
/*date when post opened*/
body.item-view .Blog .post-header-container {
    padding-left: 0px!important;
    position: absolute!important;
}
body.item-view .Blog .post-header {
    height: 45px!important;
}
}
