css:
@-moz-document url("https://goodolddownloads.com/enter"), url-prefix("https://god."), domain("goggames.goodolddownloads.com"), domain("god-games.com"), domain("goggamespc7v6z5e.onion") {
#donate-bar:before {
    -webkit-transform: skew(30deg);
    -ms-transform: skew(30deg);
    transform: skew(30deg);
    border-radius: 8px;
    border-left-width: 12px;
    border-right-width: 12px;
    border: 5px #1b1b1b;
    border-left-style: solid;
    border-right-style: solid;
}
body,
.modal-small .modal-inner,
.modal-big .modal-inner {
    background: #3c3c3c;
    color: #969696;
}
.game-blocks .block,
.progresslog,
#searchbar::before,
#navbar {
    background: #212121;
}
table,
.modal .modal__inner,
.game-blocks .block .info .title,
.game-blocks .block .info #game-details .container .items-group .clip,
#game-details .container .items-group .game-blocks .block .info .clip,
.game-blocks .block .info #game-details-bg .container .items-group .clip,
#game-details-bg .container .items-group .game-blocks .block .info .clip,
.game-blocks .block .info #game-details .container .items-group .open-all,
#game-details .container .items-group .game-blocks .block .info .open-all,
.game-blocks .block .info #game-details-bg .container .items-group .open-all,
#game-details-bg .container .items-group .game-blocks .block .info .open-all {
    background: #212121;
    color: #6e6e6e;
}
.tag {
    color: #212121;
    font-size: 8px;
}
#notice,
.game-blocks {
    border-bottom: #212121 1px solid;
}
#notice {
    border-top: #212121 1px solid;
}
.game-blocks .block .date {
    border: 1px solid #3c3c3c;
    color: white;
}
#game-details-bg {
    background: #fff0;
}
#game-details .container::before,
#game-details-bg .container::before {
    background-color: #212121;
}
#game-details > .container::before {
    filter: brightness(20%);
}
.container.game > .bg {
    /*filter: brightness(0.296);*/
    filter: brightness(29.5%);
}
#game-details-bg .bg.fadein {
    opacity: 1;
    -webkit-transition: none;
    transition: none;
}
#game-details-bg .bg.fadein {
    display: none;
}
#game-details .container .items-group .title {
    border-bottom: solid 1px #fff;
}
#game-details .container::before,
#game-details-bg .container::before,
#game-details .container::after,
#game-details-bg .container::after {
    background-color: #9f9f9f1a;
    border-radius: 0.68em;
}
#game-details .container .close::before,
#game-details-bg .container .close::before,
#game-details .container .close::after,
#game-details-bg .container .close::after {
    background: #3c3c3c;
}
#game-details .container .changelog.open,
#game-details-bg .container .changelog.open {
    background-color: #1a1a1ab3;
    border-radius: 0.68em;
}
#game-details,
#game-details-bg {
    min-height: 470px;
}
.btn,
.search-settings > .btn.drop > ul > li > a,
.fa-th::before,
.fa-th-list::before {
    color: #ccc;
}
btn,
.btn,
.view-changer button {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(60, 60, 60, 0.9)), to(rgba(33, 33, 33, 0.9)));
    background: linear-gradient(to bottom, rgb(60, 60, 60), rgb(33, 33, 33));
}
.search-settings > .btn.drop > ul {
    background: -webkit-gradient(linear, left top, left bottom, from(#ccc), to(#d9d9d9));
    background: linear-gradient(to bottom, #333, #303030);
}
#navbar .container .dropdown:hover > ul,
#navbar .container .dropdown.active > ul {
    background: rgb(33, 33, 33);
}
#navbar .container .dropdown:hover > ul li:hover,
#navbar .container .dropdown.active > ul li:hover {
    background: #2589d7;
}
#game-details .container .info a {
    color: rgb(106, 77, 165);
}
#game-details .container .info,
#game-details-bg .container .info,
#game-details .container .items-group .item .filesize {
    color: rgb(240, 240, 240);
}

/*Workaround for some icons being SVGs and some being fonts. #969696 */
.fac-hoster {
    filter: invert(50%);
}
#game-details .container .items-group .item-expand-wrap,
#game-details-bg .container .items-group .item-expand-wrap,
#game-details .container .items-group .item,
#game-details-bg .container .items-group .item {
    border-color: #383838;
    border-left-color: transparent;
    border-right-color: transparent;
}
body > .container > table > tbody > tr > td {
    border-bottom: 2px solid #9999991a;
}
body > .container > table > tbody > tr > td.txt-center {
    border-bottom: 2px dotted #9993;
}
table thead th {
    border-bottom: 1px solid #3c3c3c;
}
}
