css:
@-moz-document domain("www.linfo.re")
{
body,
.info-container,
.page-home .info-container,
.page-home .info-2cols {
    background: rgb(50, 50, 50);
    border-color: transparent;
}
.info-2cols .info-side-col .fil_info ul li .wid-list-texte,
.info-2cols .info-main-col .liste-info .section-article p,
.article-monde .wid_bloc_droit .info-wid-titre,
.info-2cols .info-main-col .section-article,
.info-2cols .info-main-col .article-body p,
.page-article .info-article .info-a1,
.article-section .gauche-title,
.liste-info h3,
.lien_tous_la_plage a,
.meteo-section-temp-texte.left span,
.content_meteo_plage_droite h2,
.content_list_meteo_plage .text_meteo_meteo_plage_droite,
.AR_2 .ob_org_header,
.wid_bloc_droit .info-wid-titre,
body,
a {
    color: #fff !important;
}
.info-breadcrumb .breadcrumb-home {
    color: red;
}
.info-article .article-meta,
.info-profil-time {
    color: grey !important;
}
.info-2cols .info-side-col .fil_info ul li,
.article-section .gauche-title,
.info-2cols .info-main-col,
.AR_2 .ob_org_header,
.list-wrap ul li,
.info-2cols,
.liste-info {
    border: none;
}
.wid-list-hour {
    background: #fff;
    padding: 0 5px;
    border-radius: 15px;
}
.article-section .section-gauche,
.ob_stripDual_container.AR_2,
.list-wrap ul li,
.autres-articles {
    background: rgb(40, 40, 40);
    border-radius: 5px;
}
.article-section .gauche-title {
    margin: 10px;
}
.list-wrap ul li {
    padding: 0;
    padding-bottom: 10px;
    overflow: hidden;
    margin: 10px 0 10px;
}
.list-wrap ul li .wid-list-count {
    width: 18px;
    padding: 5px 4px;
    text-align: center;
    border-radius: 20px;
    position: relative;
    top: -5px;
    left: -7px;
    font-size: 16px;
}
.info-meteo,
.bloc_droite_meteo_de_plage {
    background: rgba(255, 255, 255, .05);
    border: none;
    border-radius: 5px;
}
.info-2cols .info-side-col .info-meteo .info-wid-titre {
    padding-bottom: 10px;
    padding-top: 2px;
}
.je-reagis img {
    border: 2px solid cyan;
    border-radius: 3px;
}
.page-article .container_article .captionfull {
    border-radius: 10px;
}
.info-has-live a {
    color: inherit !important;
}
.container-header .info-actu ul {
    background: rgb(25, 25, 25);
}
.info-actu ul li a img {
    border-color: #000;
}
/*.liste-info {
    transition: all .2s;
}
.liste-info:hover {
    transform: scale(1.05);
}*/
.liste-info .info-profil-media img {
    border-radius: 0px;
}
.bloc_base.info-shopping .content-shop {
    background: rgba(255, 255, 255, .1);
    border: none;
}
#slider1next,
#slider1prev {
    width: 15px;
    height: 35px;
}
#slider1next {
    -webkit-transform: scale(-1, 1);
        -ms-transform: scale(-1, 1);
            transform: scale(-1, 1);
}
#slider1next a,
#slider1prev a {
    border: 2px solid #fff;
    width: 20px;
    height: 20px;
    -webkit-transform: rotate(45deg);
        -ms-transform: rotate(45deg);
            transform: rotate(45deg);
    background: transparent;
    top: 5px;
    left: 5px;
}
.categoriesN1 {
    /*position: inherit;
    width: inherit;*/
    
    height: inherit;
}
.categoriesN1 ul.nav_haut {
    position: absolute;
    background: #222 !important;
    -webkit-box-shadow: 0 10px 15px rgba(0, 0, 0, .1);
            box-shadow: 0 10px 15px rgba(0, 0, 0, .1);
    margin: 0;
    padding: 20px;
    width: calc(1000px - 40px);
    height: inherit;
    min-height: inherit;
    font-size: 0;
}
.categoriesN1 ul.nav_haut li.tabs_menu {
    background: transparent !important;
    min-width: 100px;
    border-radius: 4px;
    display: inline-block;
    vertical-align: top;
    text-align: center;
    margin: 2px;
    overflow: hidden;
}
.categoriesN1 ul.nav_haut li a.sous-links {
    padding: 12px 20px;
    background: rgba(255, 255, 255, .1);
}
/*[[theme]]*/ {
    background: /*[[theme-color]]*/;
}
}
