css:
@-moz-document domain("mediapart.fr") {
body  {
  background-color: #333333;
  color: #333333;
}

.content-article {
  text-align: justify;   
}

.global-wrapper {
  color: #eeeeee;
  background-color: transparent;
}

p {
  color: #eeeeee;
  background-color: transparent; 
}

.main-menu .link-journal {
  background-color: #000;
  color: white;
}

.main-menu a:hover {
  color: #FFF;
}

.menu-sticky a:visited {
  color: #eeeeee;
}

.footer p {
  text-align: justify;
  color: #eeeeee;
  background-color: black; 
}

.mkt-banner .title {
  color: #202222 !important;
  background-color: #EDEDED;;
}

.blackbox p {
  text-align: justify;
  color: #333333;
  background-color: white; 
}

.blackbox {
  text-align: justify;
  color: #333333;
  background-color: white; 
}

.journal-bundle h1, .journal-bundle h2, .journal-bundle h3, .journal-bundle h4, .journal-bundle h5, .journal-bundle h6 {
  color: #eeeeee;
}

.sub-menu.sub-menu-journal {
  background-color: transparent;
}

.about-redactor .l-50 li, .account .post-list.favorite .delete, .author, .back-to, .bookmark span, .calendar-legend, .comment-number, .contact-list, .data-entry select, .follower, .info, .keywords li, .links-list .info, .links-list .source, .manage, .message-list ul, .redactor-activity, .redactor-identity .avatar a, .redactor-identity .social, .redactor-list ul, .redactor-menu, .redactor-overview.contribution, .simple-list .from, .simple-list .type, .slide-show-feeds .source, .small-cap, .sub-menu, .subhead, .taxonomy, .thread .participant, .tools-share, .view-activity, .view-article, .view-more, label {
  color: #eeeeee;
}

a {
  color: #ffffff;
}

a:visited {
  color:inherit;
}

a:active,a:focus,a:hover {
           color:#B2B2B2
         }

.header a, .blackbox a {
  color: #333333;   
}

.taxonomy > li::before {
  background-color: #eeeeee;
}

.read-also {
  color: #eeeeee;
  background-color: #444444;  
}

.copyright, .media .legend, .tweet.legend {
  border-left: 1px solid #B2B2B2;
  color: #eeeeee;
}

.simple-list > .title, .keywords .title {
  background-color: transparent;
}

.simple-list .type .ico {
  color: #eeeeee;
  font-size: .8125rem;
}

.about-redactor > .title, .additionnal-s .content-tracker thead th, .additionnal-contents .title-specific, .block-event > .title, .block-portfolio > .title, .block-post > .title, .block-reco .head .title, .bullet-list > .title, .calendar > .title, .comment-list.is-aside > .title, .contact-list.is-aside > .title, .content-tracker thead .additionnal-contents th, .content-tracker thead .page-biographie .col-left th, .content-tracker thead .portfolio-slideshow th, .content-tracker thead .une-block th, .guest > .title, .help > .title, .is-aside.about-blog > .title, .is-aside.about-edition > .title, .keywords .title, .last-comment .title, .new-blog > .title, .new-comment .title, .new-edition > .title, .next-event h2.title, .page-biographie .col-left .content-tracker thead th, .page-biographie .col-left .title-specific, .portfolio-slideshow .content-tracker thead th, .portfolio-slideshow .title-specific, .post-list-wrapper > .title, .simple-bullet-list .title, .simple-list > .title, .une-block .content-tracker thead th, .une-block .title-specific {
  border-top: 2px solid #eeeeee;
}

.decrease-fs, .increase-fs {
  color: #eeeeee;
  background-color: transparent;
}

.info.edition a, .mini-pager a, .redactor-activity a {
  color: #eeeeee;
}

[class*="sub-menu"] a {
  color: #eeeeee;   
}
[class*="sub-menu"] a:hover {
  color: #B2B2B2;   
}

.sub-menu .pager a {
  color: #eeeeee;
}

.club-bundle .inter-titre-gris, .club-bundle h2.h4, .journal-bundle .inter-titre-gris, .journal-bundle h2.h4 {
  color: #eeeeee;
}

.slide-show-feeds [class^="slide-show-body"] a {
  background-color: transparent;
}

.post-list .keywords .une-block ul, .une-block .bg-highlight, .une-block .post-list .keywords ul {
  background-color: transparent;
}

.slide-show-journal-feeds .slide-show-header {
  border-top: 2px solid #eeeeee;
  background-color: transparent;
}

.slide-show-journal-feeds .taxonomy a {
  color: #eeeeee;
}

.slide-show-journal-feeds .taxonomy a:active, .slide-show-journal-feeds .taxonomy a:focus, .slide-show-journal-feeds .taxonomy a:hover {
  color: #B2B2B2;
}

.slide-show-menu-pn button::after {
  color: #eeeeee;
}

.slide-show-menu-pn button.disabled::after, .slide-show-menu-pn button[disabled]::after {
  color: #b2b2b2;
}

.slide-show .slide-show-menu-els button.active, .slide-show .slide-show-menu-els button:focus, .slide-show .slide-show-menu-els button:hover, .slide-show .slide-show-menu-els button[disabled] {
  background-color: #666666;
}

.slide-show .slide-show-menu-els button {
  background-color: #eeeeee;
}

.block-reco .head {
  background-color: transparent;
}

#colClub > .taxonomy li {
  color: #eeeeee;
}

.extra-header {
  background-color: transparent;
}

.mkt-column p {
  background-color:transparent;   
}

.slide-show-feeds .source {
  color: #eeeeee !important;
}

.multimedia-region {
  background-color: transparent;
  border-top: solid 2px #eeeeee;
  border-bottom: solid 1px #eeeeee;
  box-shadow: transparent 0 6px 6px;
}

.multimedia-container .ico {
  background-color: transparent;
  box-shadow: transparent 1px 1px 5px;
}

.sub-nav a {
  color: #eeeeee;
}

.pager a {
  border: 1px solid #666666;
  color: #eeeeee;
}

.pager a:active, .pager a:focus, .pager a:hover {
  border-color: #eeeeee;
  color: inherit;
}

.journal-bundle .sub-nav {
  border-bottom: solid 1px #eeeeee;
  padding: 9px 0;
}

.sub-nav li::before {
  color: inherit;
}

.guest .fractal-desktop {
  background-color: transparent;
}

.see-more {
  color: inherit;
}
.see-more:active, .see-more:focus, .see-more:hover {
  color: inherit !important;
}

.archive .content-tracker thead th, .archive .title-specific, .content-tracker thead .archive th {
  border-top: 2px solid #eeeeee;
}

.slide-show-feeds .slide-show {
  border-top: 1px solid #eeeeee;
  border-bottom: 1px solid #eeeeee;
}

.notice {
  background-color: transparent;
}

.comment.is-recommended {
  background-color: transparent;
}

.nav-comment {
  border-top: 2px solid #eeeeee;
}

.content-comments .manage a {
  color: inherit;
}

.pager a, .see, .subheader .itunes, .subheader .rss {
  color: inherit;
}

.pager a:active,.pager a:focus,.pager a:hover,.see:active,.see:focus,.see:hover,.subheader .itunes:active,.subheader .itunes:focus,.subheader .itunes:hover,.subheader .rss:active,.subheader .rss:focus,.subheader .rss:hover {
  border-color: #ffffffff;
  color:inherit
}

.une .feeds {
  background-color: transparent;
}

.has-bb {
  border-bottom-color: transparent;
}

.subheader, .teaser {
  border-bottom-width: 0px;
}

.subheader .feeds {
  border-left-color: #FFF;
  border-left-width: 0px;
}
.subheader [class^="icon-"] {
  background-color: transparent;
}
.subheader .logo, .subheader [class^="icon-"] {
  border-right-color: #FFF;
  border-right-width: 0px;
}

.has-bt {
  border-top-color: black;
}
.carousel {
  border-bottom: 1.875rem solid black;
  background-color: black;
}

.large-view > .l-66 {
  border-right-color: #333333;
}

.block-reco .reco-type-nav button {
  color: #eeeeee;
}

.internal-sub-menu .is-active a {
  color: white;
}

.mini-pager li.next {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/pager-next.svg);
}

.comment.is-answer:before {
  background-image:url(https://bakou.ze.cx/mediapart_dusk/is-answer.svg)
}

.view-more span {
  background:url(https://bakou.ze.cx/mediapart_dusk/view-more.svg) 100% 50% no-repeat
}

.sm-link+.see-more {
  background:url(https://bakou.ze.cx/mediapart_dusk/pager-next.svg) 100% 50% no-repeat
}

.back-to a {
  background:url(https://bakou.ze.cx/mediapart_dusk/pager-prev.svg) 4px 50% no-repeat
}

.block-event > .title, .block-portfolio > .title, .block-post > .title, .post-list-wrapper > .title {
  background-color: transparent;
}

.subhead.event {
  background-image:url(https://bakou.ze.cx/mediapart_dusk/event.svg)
}

.subheader .itunes::before, .subheader .rss::before {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/feeds.svg);
              }

.redactor-list {
  background-color: transparent;
}

.sub-header-blog .title, .sub-header-edition .title {
  color: inherit;
}

.alphabet-wrapper {
  background-color: transparent;
}

.mkt-banner p {
  background-color: transparent;
}

.tools-share .favorite a, .tools-social .favorite a {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/favorite.svg);
}

.tools-share .pdf a, .tools-social .pdf a {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/pdf.svg);
}

.tools-share .print a, .tools-social .print a {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/print.svg);
}

.tools-share .recommend a, .tools-social .recommend a {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/recommend.svg);
}

.tools-share .alert a, .tools-social .alert a {
  background-image: url(https://bakou.ze.cx/mediapart_dusk/alert.svg);
}

.tools-share a, .tools-social a {
  color: #eeeeee;
}

.post-list .keywords .title {
  background-color: transparent;
}

.bg-highlight, .highlight-block, .highlight-block-multimedia, .post-list .keywords ul {
  background-color: transparent;
}

.sub-header-article .title,  .post-event .taxonomy a {
  color: inherit;
}

.sub-menu.sub-menu-club {
  background-color: transparent;
}

.about-redactor > .title {
  background-color: transparent;
}

.about-redactor .content {
  background-color: transparent;
}

.is-aside.about-blog > .title, .is-aside.about-edition > .title {
  background-color: transparent;
}

.next-event h2.title {
  background-color: transparent;
}

.post-event, .post-event .event-thumb-item {
  background-color: transparent;
}

.cc-cookie-consent-popup-content {
    background-color: #333333;
}

.cc-cookie-consent-popup-form legend {
    color:#eeeeee;
}

.cc-cookie-consent-popup-choice input[type="radio"] + label {
    border: 1px solid #eeeeee;
}

.cc-cookie-consent-popup-close {
    color: #eeeeee
}

.megaban.offre-basique {
    background-color: #ededed;
}
.mkt-banner.offre-basique {
    background-color: #ededed;
}
h1.title {
    background-color: transparent;   
}
.simple-bullet-list .title {
    background-color: transparent;
}
}
