css:
@-moz-document domain(web.whatsapp.com) {

/* My Custom CSS changes */

.pane-chat-header {
	background-color: rgba(0,0,0,0.0)
}
/* contacts section / chat heads */

.two .k1feT:not(:hover) { width: 76px; -webkit-transition: width 0.5s; transition: width 0.5s }
.two .k1feT .pane-list-controls { -webkit-transition: all 0.4s; transition: all 0.4s; position: relative; right: 0 }
.two .k1feT:not(:hover) .pane-list-controls { position: absolute; right: -200% }
.two .k1feT:hover { -webkit-transition: width 0.5s; transition: width 0.5s }
.two .k1feT .unread .chat-meta span:nth-child(1) > div { transition: left .5s linear; position: absolute; left: 272px }
.two .k1feT .unread:hover .chat-meta span:nth-child(1) > div { left: 250px }
.two .k1feT:not(:hover) .unread .chat-meta span:nth-child(1) > div {
	/* 	position: absolute; */
	
	top: 45px; left: 45px; z-index: 100 }
/* chat section */

.two ._1Iexl { width: 100% }


.pane-chat-tile {
    background-image: url("https://i.imgur.com/xTgwtdu.jpg");
} 
#startup{
    background-image: url("https://i.imgur.com/xTgwtdu.jpg");
    background-size: cover;
    background-position: center;
}

.pane.pane-intro.pane-two {
    width: 100%;
    width: calc(100%-80px);
    background-image: url("https://i.imgur.com/xTgwtdu.jpg");
    background-size: cover;
    background-position: center;
}
.unread {
    background-color: #1CD61C;
    animation: blinker 1s cubic-bezier(.5, 0, 1, 1) infinite alternate;
}

.typing:active .chat,
.typing:active .chat.active {
    background-color: #1CD61C !important;
    animation: blinker 0.1s cubic-bezier(.5, 0, 1, 1) infinite alternate;
    ;
}
@keyframes blinker {
    from {
        background-color: #1CD61C;
        opacity:1.0
    }
    to {
        background-color: #1CD61C;
        opacity:0.5;
    }
}
