css:
@-moz-document domain("fjnbnpbmkenffdnngjfgmeleoegfcffe") {
html{
    background-color:black!important;
    color:white!important;
}
input, textarea, select, button , .CodeMirror-scroll, .CodeMirror-sizer, .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber{
    background-color:#555!important;
    color:white!important;
}
.CodeMirror-scroll, .CodeMirror-sizer, .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber{
    background-color:#222!important;
    color:#999!important;
}
#help-popup{
        background-color:#222!important;
    	color:#999!important;
}
#help-popup .close-icon{
    background:linear-gradient(-45deg, transparent 5px, white 5px, white 6px, transparent 6.5px) no-repeat, linear-gradient(45deg, transparent 5px, white 5px, white 6px, transparent 6.5px) no-repeat;
}
button, select, input{

    background-color: transparent;
    border-radius: 15px;
    border: 2px solid #888;
    display: inline-block;
    cursor: pointer;
    color: rgb(255, 255, 255);
    font-family: Arial;
    font-size: 12px;
    font-weight: bold;
    padding: 5px 8px;
    text-decoration: none;
    text-shadow: rgb(1, 1, 1) 0px 1px
}

.cm-s-default .cm-qualifier{
    color:#666!important;
}
.cm-s-default .cm-atom{
    color:#3366ff!important;
}
.cm-s-default .cm-builtin{
    color:#8533ff!important;
}
.CodeMirror-linebackground{
    background-color:black;
}
.CodeMirror-selected{
    background-color:black!important;
}
div.CodeMirror-cursor{
    background-color:white!important;
}

::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #000;
    width: 14px;
    height: 14px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c;
}
}

@-moz-document regexp("(.*?fjnbnpbmkenffdnngjfgmeleoegfcffe.*?\\/popup.html)") {
.tab-item{
    background-color:black!important;
    color:white!important;
}
#menu_inner{
    background-color:#333!important;
    border-radius:5px;
}
.bottom_controls .manage_styles, .bottom_controls .add_style{
    color:white!important;
}
.tabs-container{
    background:black!important;
}
#nostyles, #unavailable-message, #noServerConnection, #zerostyles .message{
    color:white!important;
}
.find-styles{
    background-color:#333!important;
    box-shadow:none;
}
.entry{
    background-color:#333!important;
    color:white!important;
}
.style-name{
    color:#0099ff!important;
}
}
