css:
/* i really want this to be global */
body {
    font-family: Gothic, sans-serif;
    font-size: 13px;
    line-height: 1.42857;
    color: #000;
    background-color: #131315;
}
.widget {
    background: #131315;
}
#header .head {
    height: 68px;
    background: linear-gradient(45deg, rgba(75, 54, 124, 1) 1%, rgba(107, 54, 124, 1) 19%, rgba(124, 54, 107, 1) 40%, rgba(124, 54, 72, 1) 50%, rgba(124, 54, 54, 1) 60%, rgba(124, 89, 54, 1) 80%, rgba(124, 124, 54, 1) 100%);
    position: relative;
}
.widget.schedule .widget-body .timeline .item:nth-child(2n) {
    background: #171717;
}
.widget.servers .range>span {
    display: inline-block;
    background: #171717;
    color: #fff;
    padding: 1px 8px;
    font-size: .9rem;
    margin: 0 2px 6px;
    -webkit-border-radius: 2px;
    -khtml-border-radius: 2px;
    -moz-border-radius: 2px;
    -ms-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
    font-weight: 300;
    cursor: pointer;
}
.widget.servers ul.episodes li a {
    display: block;
    height: 28px;
    line-height: 28px;
    min-width: 38px;
    padding: 0 8px;
    text-align: center;
    font-weight: 300;
    margin: 4px;
    -webkit-border-radius: 3px;
    -khtml-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    background: #111;
    color: #fdfdfd;
    border-bottom: 1px solid #fdfdfd;
    font-size: 1.05rem;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    list-style: none;
    font-size: 13px;
    text-align: left;
    background-color: #131315;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 3px;
    -webkit-box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
    box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
    background-clip: padding-box;
}
.widget .widget-title {
    border-bottom: 1px solid #694ba1;
    min-height: 35px;
    padding: 0 10px;
}
.widget.genres ul li {
    width: 33.3%;
    float: left;
    border-right: 1px solid #694ba1;
}
.btn-default {
    color: #0a0a0a;
    background-color: #000000;
    border-color: #000000;
}
.link-list .item:nth-child(4n-2),
.link-list .item:nth-child(4n-3) {
    background: #171717;
}
.widget.ranking .item {
    overflow: hidden;
    padding: 8px;
    border-bottom: 1px solid #694ba1;
}
#header .nav #menu>li .sub {
    display: none;
    width: 500px;
    background: #080707;
    overflow: hidden;
    position: absolute;
    top: 34px;
    left: 0;
    padding: 10px;
    -webkit-box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    -khtml-box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    -ms-box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    -o-box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    box-shadow: 1px 1px 3px rgba(0, 0, 0, .4);
    z-index: 10;
}
.widget.schedule .widget-body .timeline .item {
    overflow: hidden;
    padding: 10px 0;
    border-bottom: 1px solid #FFF;
    font-size: 1.1rem;
    font-family: "Nunito Sans",sans-serif;
}
.simple-film-list .item {
    overflow: hidden;
    padding: 10px;
    border-bottom: 1px solid #694ba1;
}
.widget.schedule .widget-body .slider .items .item {
    float: left;
    text-align: center;
    height: 70px;
    font-family: "Nunito Sans",sans-serif;
    cursor: pointer;
    width: 101.2px;
    padding: 12px 0;
    -webkit-border-radius: 5px;
    -khtml-border-radius: 5px;
    -moz-border-radius: 5px;
    -ms-border-radius: 5px;
    -o-border-radius: 5px;
    border-radius: 5px;
    background-color: #111;
    margin: 5px;
}
#header .head #logo a {
    display: block;
    padding-top: 28px;
    color: #000000;
    text-align: center;
    text-transform: uppercase;
    font-weight: 400;
    letter-spacing: .03rem;
    font-size: .78rem;
}
.widget.info .info .desc .morelink {
    display: block;
    margin-bottom: 10px;
    background: linear-gradient(45deg, rgba(75, 54, 124, 1) 0%, rgba(107, 54, 124, 1) 20%, rgba(124, 54, 107, 1) 40%, rgba(124, 54, 72, 1) 50%, rgba(124, 54, 54, 1) 60%, rgba(124, 89, 54, 1) 80%, rgba(124, 124, 54, 1) 100%);
    color: #111;
    text-align: center;
    cursor: pointer;
    padding: 1px;
    font-size: .9rem;
}
#header .nav {
    font-family: "Nunito Sans",sans-serif;
    height: 34px;
    background: #131315;
}
.btn-primary {
    color: #000;
    background: linear-gradient(45deg, rgba(75, 54, 124, 1) 0%, rgba(107, 54, 124, 1) 20%, rgba(124, 54, 107, 1) 40%, rgba(124, 54, 72, 1) 50%, rgba(124, 54, 54, 1) 60%, rgba(124, 89, 54, 1) 80%, rgba(124, 124, 54, 1) 100%);
    border-color: #000;
}
.widget.servers ul.episodes li a:visited {
    background: #2c253c;
    border-color: #fdfcff;
}
.abmsg {
    background: #131315
}
