css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br"), domain("eldarya.com.br")
{

/*Fenrirsulfr Familiar*/  
 
  /*Images - pictures*/
  #pet-headerPortrait-base
  {
    background: url("https://78.media.tumblr.com/82b9b706dacdd680af5ab5ca8e26c4d1/tumblr_p2iq4pC7Zx1trxrtqo3_500.png") -15px top no-repeat !important;
    background-size: auto 100px !important;
  }

  #pet-portrait
  {
    background: url("https://78.media.tumblr.com/584ab85655090a63531ab75f32897e2e/tumblr_p2iq4pC7Zx1trxrtqo2_500.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"]
  {
    display: none !important;
  }

    /*Infos*/

  #pet-info-name:before
  {
    content: "Moloch" !important;
    font-size: 20px !important;
  }

  #pet-info-name
  {
    font-size: 0 !important;
  }

  #pet-info-evolve > .on
  {
    font-size: 0 !important;
  }

  .pet-info-evolve-icon
  {
    font-size: 13px !important;
  }

  .pet-info-evolve-icon:after
  {
    content: "Este animalito sera primo de los lobos Fenrir?";
  }

  #pet-info-racegender:before
  {
    content: "Fenrirsulfr" !important;
    font-size: 16px !important;
    line-height: 20px !important;
  }

  #pet-info-racegender
  {
    font-size: 0 !important;
    margin-top: 5px;
  }

  img[src*="/static/img/f.png"]
  {
    background: url("http://www.eldarya.fr/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
  }
 
 /*Nourriture - food*/
  
  #pet-info-food > img
  {
    background: url("https://78.media.tumblr.com/b7da1ed822353c14a42bd0d2915c2b7f/tumblr_p2iq4pC7Zx1trxrtqo1_100.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
  }

  #pet-info-food
  {
    font-size: 0 !important;
  }

  #pet-info-food:before
  {
    content: "???" !important;
    font-size: 16px !important;
  }

  #pet-info-food > span
  {
    font-size: 16px !important;
  }

  #player-display-pet
  {
    background: url("http://www.eldarya.es/static/img/scenario/6c674137a21826761dc60deac366a036.png") no-repeat;
    background-size: auto 100%;
    height: 600px !important;
    width: 630px !important;
  }

  #player-display-pet > img
  {
    display: none !important;
  }
}
