css:
@-moz-document domain("techcrunch.com") {
body {
    background: #6e6a6a;
    color: #9d9d9d;
}
.header {
    background: #128629;
    color: #bfbfbf;
    border-bottom: 1px solid #0a0a0a;
}
.announcement {
    background: #574e4e;
    border-bottom: 1px solid #e5e5e5;
}
@media (min-width: 60em) {
    [role=main].split {
        background: #484343;
    }
}
@media (min-width: 60em) {
    [role=main].split .l-main {
        background: #2c2929;
        color: #a3a3a1;
    }
}
input {
    background-color: #757779;
}
.l-sidebar .newsletters-signup .newsletter-body {
    background: #56565b;
}
.aside-alt {
    background: #525553;
}
@media (min-width: 46.8em) {
    .block-featured .block-content {
        background: #2c2929;
        color: #bbb8b8;
    }
}
.block h2 a,
.block h3 a,
.block h4 a {
    color: #818181;
}
.event-related-articles,
.video-promo-carousel {
    background-color: #111a4e;
}
#carousel-manhattan-homepage {
    background: #2c2f2e;
}
.carousel.carousel-manhattan .sidebar .video-container .video {
    background: #333933;
    border-right: 3px solid #060606;
}
.feature-island-container {
    background: #3d3d3d;
}
.crunchbase-island .data-card {
    background: #575656;
}
.card-title {
    background: #575454;
    color: #777d77;
    border-bottom: 4px solid #e8e8e8;
}
.tabs-large {
    background: #797979;
}
.tabs-large a.active {
    background: #666464;
}
.tabs a {
    border: 1px solid #d5d5d5;
    color: #7b7979;
    background: #919291;
}
.aside {
    border-top: 7px solid #595858;
    border-bottom: 7px solid #3b3a3a;
}
.crunch-network .block:after,
.crunch-network .block:before,
.event-related-articles:after,
.event-related-articles:before,
.video-promo-carousel:after,
.video-promo-carousel:before {
    background: #595858;
}
}
