css:
@-moz-document domain('cc98.org') {
    /* ad at home page */
    .rightPart > div:nth-child(3) {
        display: none;
    }
    .mainPageCount {
    	margin-top: 0;
    }

    /* ad at list page */
    #listRoot > div.row:nth-child(3) img {
        display: none;
    }

    /* ad at post page */
    .topicInfo-ads {
        display: none;
    }
    .topicInfo-title {
        width: 100%;
    }
    #topicTitleProp {
        width: 100%;
    }
    .topicInfo-boardMessage {
        width: 11rem;
    }

    /* fix header bar */
    .topBar-mainPage,
    .topBar {
        position: fixed;
    }
    .topBar,
    .topBar-mainPage {
        min-width: 1140px;
    }
    .headerWithoutImage {
        z-index: 201;
    }

    /* void scroll mutation */
    :root {
        overflow-y: scroll;
    }
}
